TopRowKPIs_1.query:
q = load "${App.Datasets.Demo_Projects_and_Credits_tp.FullyQualifiedName}";
result = group q by all;
result = foreach result generate unique(q.'ProjectName') as 'TotalProjects', avg(q.'AvgPrice') as 'AvgPricePerTCO2', avg(q.'TotalAllocatedCredits') as 'TotalCreditsAllocated', avg(q.'TotalAvailableCredits') as 'AvailableCredits', avg(q.'TotalPurchasedCredits') as 'TotalCreditsPurchased', avg(q.'SustainabilityCredits') as 'NrOfSustainabilityCredits';
result = limit result 2000;
