Emission_Activity_Ch_1.query:
q = load "${App.Datasets.Demo_Emission_Activity_Tab.FullyQualifiedName}";
q = group q by 'Date_Year';
qs = foreach q generate 'Date_Year' as 'Date_Year', sum('Actual_Emissions_tCO2e') as 'A';
qs = filter qs by (A is not null);
qs = foreach qs generate A , 'Date_Year';
qs = fill qs by (dateCols=('Date_Year',"Y"));
qs = timeseries qs generate 'A' as StatisticalForecast with (length=10, dateCols=('Date_Year', "Y"));
qs = foreach qs generate  'Date_Year', 'StatisticalForecast','A';
final = group q by  'Date_Year' full, qs by  'Date_Year';
final = foreach final generate coalesce(q.'Date_Year',qs.'Date_Year') as 'Date_Year',{{row(Static_Emission_Acti_1.selection, [], ["Expression", "Alias"]).asProjection()}},{{row(static_emission_acti_1.selection, [], ["Expression", "Alias"]).asProjection()}};

Emissions_by_Country_1.query:
q = load "${App.Datasets.Demo_Net_Zero_Cloud.FullyQualifiedName}";
q = filter q by {{cell(AcctNameFilter.selection, 0, "QueryFilterPart").asString()}};result = group q by 'Country';
result = foreach result generate q.'Country' as 'Country', sum(q.'Emission_Value') as 'A';
result = order result by ('Country' asc);
result = limit result 2000;

Travel_and_Vehicles__2.query:
q = load "${App.Datasets.Demo_Net_Zero_Cloud.FullyQualifiedName}";
q = filter q by {{cell(AcctNameFilter.selection, 0, "QueryFilterPart").asString()}};q = filter q by (!('Emission_Type' in ["Comercial Building", "Company Shuttle ", "Data Center Facility", "Equipment"]) || 'Emission_Type' is null);
q = group q by ('Emission_Type', 'Emission_Category');
q = foreach q generate 'Emission_Type' as 'Emission_Type', 'Emission_Category' as 'Emission_Category', sum('Emission_Value') as 'sum_Emission_Value';
q = order q by 'sum_Emission_Value' desc;
q = limit q 2000;

Scope_Wise_KPIs_1.query:
q = load "${App.Datasets.Demo_Scope_Emissions.FullyQualifiedName}";

-- Applying Account filter
q = filter q by {{cell(AcctNameFilter.selection, 0, "Display").asEquality("Account")}};

-- Applying date filters
q_A = filter q by {{row(Date_1.selection, [0], ["min", "max"]).asDateRange("date('Date_Year', 'Date_Month', 'Date_Day')")}};
q_B = filter q by {{row(Date_2.selection, [0], ["min", "max"]).asDateRange("date('Date_Year', 'Date_Month', 'Date_Day')")}};

-- Applying Scope filters to present metrics
q_A_sc_1 = filter q_A by 'Scope' == "Scope 1";
q_A_sc_2 = filter q_A by 'Scope' == "Scope 2";
q_A_sc_3 = filter q_A by 'Scope' == "Scope 3";

-- Applying Scope filters to past metrics
q_B_sc_1 = filter q_B by 'Scope' == "Scope 1";
q_B_sc_2 = filter q_B by 'Scope' == "Scope 2";
q_B_sc_3 = filter q_B by 'Scope' == "Scope 3";

result = group q_A_sc_1 by all full, q_A_sc_2 by all full, q_A_sc_3 by all full, q_B_sc_1 by all full, q_B_sc_2 by all full, q_B_sc_3 by all;

result = foreach result generate avg(q_A_sc_1.'Emissions') as 'Scope_1_Present', avg(q_B_sc_1.'Emissions') as 'Scope_1_Past', avg(q_A_sc_2.'Emissions') as 'Scope_2_Present', avg(q_B_sc_2.'Emissions') as 'Scope_2_Past', avg(q_A_sc_3.'Emissions') as 'Scope_3_Present', avg(q_B_sc_3.'Emissions') as 'Scope_3_Past';

result = foreach result generate 'Scope_1_Present', 'Scope_1_Past', 'Scope_2_Present', 'Scope_2_Past', 'Scope_3_Present', 'Scope_3_Past', (Scope_1_Present - Scope_1_Past) / Scope_1_Past as 'Scope_1_Percentage', (Scope_2_Present - Scope_2_Past) / Scope_2_Past as 'Scope_2_Percentage', (Scope_3_Present  - Scope_3_Past) / Scope_3_Past as 'Scope_3_Percentage';

Date_Year_3.query:
q = load "${App.Datasets.Demo_Net_Zero_Cloud.FullyQualifiedName}";
q = filter q by {{cell(AcctNameFilter.selection, 0, "QueryFilterPart").asString()}};
q_B = filter q by {{row(Date_2.selection, [0], ["min", "max"]).asDateRange("date('Date_Year', 'Date_Month', 'Date_Day')")}};
q_A = filter q by {{row(Date_1.selection, [0], ["min", "max"]).asDateRange("date('Date_Year', 'Date_Month', 'Date_Day')")}};
q = cogroup q_B by all full, q_A by all;
q = foreach q generate sum(q_B.'Emission_Value') as 'B', sum(q_A.'Emission_Value') as 'TotalCarbonEmissions';
q = foreach q generate 'B', 'TotalCarbonEmissions', -0.045 as 'TotalCarbonEmissionsVariation';

