FuelEmission_table_1.query:
q = load "${App.Datasets.Demo_FuelType_Predictions_tp.FullyQualifiedName}";
q = foreach q generate 'FuelTypeContributions_Last1yrScope2MktBasedEmssnInTco2e' as 'FuelTypeContributions_Last1yrScope2MktBasedEmssnInTco2e', 'InnerJoin_FuelType' as 'InnerJoin_FuelType', 'PredictedScope2MktBasedEmssnInTco2e' as 'PredictedScope2MktBasedEmssnInTco2e', 'PredictionData_ExternalDa_Year' as 'PredictionData_ExternalDa_Year', 'PredictionData_StnryAsset_BusinessRegion' as 'PredictionDat_StnryAsset_BusinessRegion', 'PredictionData_StnryAsset_StationaryAssetType' as 'PredictionData_StnryAsset_StationaryAssetType';
