q = foreach q  generate ((sum('PredictedScope2MktBasedEmssnInTco2e')-sum('FuelTypeContributions_Last1yrScope2MktBasedEmssnInTco2e'))/sum('FuelTypeContributions_Last1yrScope2MktBasedEmssnInTco2e'))*100 as 'percentChange',sum('PredictedScope2MktBasedEmssnInTco2e') as 'Emissions',sum('FuelTypeContributions_Last1yrScope2MktBasedEmssnInTco2e') as 'Actual', case when ((sum('PredictedScope2MktBasedEmssnInTco2e')-sum('FuelTypeContributions_Last1yrScope2MktBasedEmssnInTco2e'))/sum('FuelTypeContributions_Last1yrScope2MktBasedEmssnInTco2e')) < 0 then "#53E570" else "#FE5C4C"  end as 'perc_Change_Format_Color';
q = foreach q generate number_to_string(percentChange,"#,##0.00")+"%" as percentChange,Emissions,case when percentChange >= 0 then "△" else "▽" end as 'symbolpercentage','perc_Change_Format_Color';

