TopDrivers_1.query:
q = load "${App.Datasets.Demo_FuelType_Predictions_tp.FullyQualifiedName}";
q1=foreach q generate  'StnryAssetEnvrSrcId','InnerJoin_FuelType','PredictionData_ExternalDa_Year','PredictionData_StnryAsset_BusinessRegion','PredictionData_Predictor1' as 'predictor','PredictionData_Predictor1_Impact' as 'predictor_impact';
q2=foreach q generate 'StnryAssetEnvrSrcId','InnerJoin_FuelType','PredictionData_ExternalDa_Year','PredictionData_StnryAsset_BusinessRegion', 'PredictionData_Predictor2' as 'predictor','PredictionData_Predictor2_Impact' as 'predictor_impact';
q3=foreach q generate 'StnryAssetEnvrSrcId','InnerJoin_FuelType','PredictionData_ExternalDa_Year','PredictionData_StnryAsset_BusinessRegion','PredictionData_Predictor3'  as 'predictor','PredictionData_Predictor3_Impact' as 'predictor_impact';
final = union q1,q2,q3 ;
result = foreach final generate case when sum('predictor_impact') < 0 then "🔴" else "🟢" end as 'c','predictor','predictor_impact';
result = order result by 'predictor_impact' desc;
result= foreach result generate c,predictor;
result = limit result 3;
