Emissions_Diff_as_To_1.query:
q = load "${App.Datasets.Demo_Predicted_Supply_Chain_Emissions.FullyQualifiedName}";
q_B = filter q by date('Date_Year', 'Date_Month', 'Date_Day') in ["{{cell(FutureYear_1.selection, 0, "Offset").asString()}} years ahead".."{{cell(FutureYear_1.selection, 0, "Offset").asString()}} years ahead"];
q_A = filter q by date('Date_Year', 'Date_Month', 'Date_Day') in ["current year".."current year"];
q = cogroup q_B by 'Supplier_Type' full, q_A by 'Supplier_Type';
q = foreach q generate coalesce(q_B.'Supplier_Type', q_A.'Supplier_Type') as 'Supplier_Type', avg(q_B.'Spend_Based_Predicted_Emission') as 'B', avg(q_A.'Spend_Based_Emission') as 'A';
q = order q by 'A' desc;
q = foreach q generate 'Supplier_Type', (B-A)/{{cell(Category_Analysis_KP_1.result, 0, "TotalBaseline").asString()}} as 'Change as a % of Total Baseline Procurement Emissions';

Category_Analysis_KP_1.query:
q = load "${App.Datasets.Demo_Predicted_Supply_Chain_Emissions.FullyQualifiedName}";
q_B = filter q by date('Date_Year', 'Date_Month', 'Date_Day') in ["2 years ahead".."2 years ahead"];
q_A = filter q by date('Date_Year', 'Date_Month', 'Date_Day') in ["current year".."current year"];
q = cogroup q_B by 'Supplier_Type' full, q_A by 'Supplier_Type';
q = foreach q generate coalesce(q_B.'Supplier_Type', q_A.'Supplier_Type') as 'Supplier_Type', avg(q_B.'Spend_Based_Predicted_Emission') as 'B', avg(q_A.'Spend_Based_Emission') as 'A';
q = group q by all;
q = foreach q generate sum('B') as 'TotalPredicted', sum('A') as 'TotalBaseline';
q = foreach q generate 'TotalPredicted', 'TotalBaseline', ('TotalPredicted'-'TotalBaseline')/'TotalBaseline' as 'ChangeFromBaselineToEstimated';

