Spend_Based_Timeline_2.query:
q = load "${App.Datasets.Demo_Predicted_Supply_Chain_Emissions.FullyQualifiedName}";
q = filter q by date('Date_Year', 'Date_Month', 'Date_Day') in ["{{cell(DateRangeYrs.selection, 0, "Number2").asString()}} years ago"..];
q = filter q by date('Date_Year', 'Date_Month', 'Date_Day') in [dateRange([0,1,1], [{{cell(FutureYear_1.selection, 0, "Year").asString()}},12,31])];
q = group q by ('Date_Year', 'Supplier_Type');
q = foreach q generate q.'Date_Year' as 'Date_Year', q.'Supplier_Type' as 'Supplier_Type', avg(q.'Spend_Based_Emission') as 'A', avg(q.'Spend_Based_Predicted_Emission') as 'B';
q = group q by 'Date_Year';
q = foreach q generate 'Date_Year' as 'Date_Year', sum('A') as 'Spend Based', sum('B') as 'Spend Based Predicted';

Qty_Based_Timeline_1.query:
q = load "${App.Datasets.Demo_Predicted_Supply_Chain_Emissions.FullyQualifiedName}";
q = group q by ('Date_Year', 'Supplier_Type');
q = foreach q generate q.'Date_Year' as 'Date_Year', q.'Supplier_Type' as 'Supplier_Type', avg(q.'Qty_Based_Emission') as 'A', avg(q.'Qty_Based_Predicted_Emission') as 'B';
q = group q by 'Date_Year';
q = foreach q generate 'Date_Year' as 'Date_Year', sum('A') as 'Qty Based', sum('B') as 'Qty Based Predicted';

FutureYear_1.query:
q = load "${App.Datasets.Demo_Predicted_Supply_Chain_Emissions.FullyQualifiedName}";
q = foreach q generate toString(now(), "yyyy") as 'Curr_year','Date_Year' as 'Year';
q = group q by 'Year';
q = foreach q generate 'Year' as 'Year', count() as 'count', first('Curr_year') as 'curr_year';
q = foreach q generate 'Year' as 'Year', 'curr_year', (string_to_number('Year')-string_to_number('curr_year')) as 'Offset';
q = filter q by 'Year' > 'curr_year';
q = foreach q generate 'Year' as 'Year', 'Offset' as 'Offset';
q = order q by 'Year' asc;
q = limit q 2000;
