Supplier_Scope_3_Emi_1.query:
q = load "${App.Datasets.Demo_Scope_3_Procurement_tp.FullyQualifiedName}";
q_A = filter q by {{row(dateSelector_1.selection, [0], ["min", "max"]).asDateRange("date('Date_Year','Date_Month','Date_Day')")}};
q_B = filter q by {{row(dateSelector_2.selection, [0], ["min", "max"]).asDateRange("date('Date_Year','Date_Month','Date_Day')")}};
result = group q_A by 'Supplier' full, q_B by 'Supplier';
result = foreach result generate coalesce(q_A.'Supplier', q_B.'Supplier') as 'Supplier', avg(q_A.'Scope_3_Emissions') as 'Scope 3 Emissions in Report Year', sum(q_B.'Scope_3_Emissions') as 'Scope 3 Emissions in Comparative Year';
result = foreach result generate 'Supplier', 'Scope 3 Emissions in Comparative Year', 'Scope 3 Emissions in Report Year', ('Scope 3 Emissions in Report Year' - 'Scope 3 Emissions in Comparative Year')/'Scope 3 Emissions in Comparative Year' as 'Change in Emissions', case when (('Scope 3 Emissions in Report Year' - 'Scope 3 Emissions in Comparative Year')/'Scope 3 Emissions in Comparative Year') > 0 then "#6CCE56" else "#F2536D"
end as 'Change Format Color';
result = order result by 'Scope 3 Emissions in Report Year' desc nulls last;
result = limit result 2000;
