Response_and_abandon_1.query:
invitations = load "${App.Datasets.SurveyInvitation18_tp.FullyQualifiedName}";
responses = load "${App.Datasets.SurveyResponseWithDetails13_tp.FullyQualifiedName}";
completed = filter responses by 'Status' == "Completed";
abandoned = filter responses by 'Status' == "Started";
result = group invitations by ('SurveyId', 'Survey_Name') left, completed by ('SurveyId', 'Survey_Name') left, abandoned by ('SurveyId', 'Survey_Name');
result = foreach result generate  invitations.'Survey_Name' as 'Survey_Name', count(abandoned) as 'Abandoned Count',
count(completed) as 'Completed Count',
count(invitations) as 'Invitations Count';
result = group result by all;
result = foreach result generate sum('Invitations Count') as 'sum_# Invitations', sum('Completed Count') as 'sum_# Responses', sum('Abandoned Count')/ sum('Invitations Count') as 'Abandonment Rate', sum('Completed Count')/ sum('Invitations Count') as 'Response Rate';

NPS___Customer_Journ_2.query:
config = load "${App.Datasets.Journey_Survey_Question_Config_Metadata18_tp.FullyQualifiedName}";
config = filter config by 'Context' == "Customer NPS"; 
config = filter config by 'QuestionType' == "NPS"; config = filter config by {{cell(NPS___Customer_Journey_1.selection,0,"JourneyConfig_Journey").asEquality("Journey")}};

q = load "${App.Datasets.SurveyQuestionResponsewithDetails18_tp.FullyQualifiedName}"; q = filter q by {{cell(NPS___Customer_Journey_1.selection,0,"JourneyConfig_Journey").asEquality("JourneyConfig_Journey")}};
q = filter q by 'Question_QuestionType' == "NPS"; promoters = filter q by 'NumberValue' > 8;
detractors = filter q by 'NumberValue' < 7;

journey = group config by ('Stage', 'SurveyID', 'SurveyVersionID', 'QuestionID') left, q by ('JourneyConfig_Stage', 'SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId') left,  promoters by ('JourneyConfig_Stage','SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId') left, detractors by ('JourneyConfig_Stage','SurveyVersion_Survey_Id', 'SurveyVersionId', 'QuestionId');

journey = foreach journey generate config.'Stage' as 'Stage', config.'SurveyID' as 'SurveyID', config.'SurveyVersionID' as 'SurveyVersionID', config.'QuestionID' as 'QuestionID', first(config.'SurveyName') as 'SurveyName', count(promoters) - count(detractors) as 'Value', count(q) as 'count';

journey = group journey by all;
journey = foreach journey generate round((sum('Value')*100)/sum('count'),2) as 'Value', sum('count') as 'count';

