q = filter q by 'Question_QuestionType' == "NPS";q = filter q by {{column(NPS___Customer_Journ_1.selection,["SurveyName"]).asEquality("SurveyVersion_Survey_Name")}} && {{column(Responses_by_stage_1.selection,["SurveyName"]).asEquality("SurveyVersion_Survey_Name")}} && {{column(NPS___Customer_Journ_4.selection,["SurveyName"]).asEquality("SurveyVersion_Survey_Name")}};
