retail_store = load "${App.Datasets.CG_RetailStore20_tp.FullyQualifiedName}";
retail_store = group retail_store by ('CreatedDate_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', 'CreatedDate_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}');
retail_store = foreach retail_store generate 'CreatedDate_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}' + "~~~" + 'CreatedDate_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}' as 'CreatedDate_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}~~~CreatedDate_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}';
-- Incomplete High Priority Visits
visit1 = load "${App.Datasets.CG_Visit36_tp.FullyQualifiedName}";
visit1 = filter visit1 by 'Status' in ["Abandoned", "Planned"] and  'VisitPriority' == "High" and  date('PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Month', 'PlannedVisitStartTime_Day') in [.."1 day ago"];
visit1 = group visit1 by ('PlannedVisitStartTime_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', 'PlannedVisitStartTime_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}');
visit1 = foreach visit1 generate 'PlannedVisitStartTime_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', 'PlannedVisitStartTime_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', count(visit1) as 'High_Priority_Incomplete_Visits';
visit1 = fill visit1 by (dateCols=('PlannedVisitStartTime_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}','PlannedVisitStartTime_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', "Y-W"));
visit1 = timeseries visit1 generate 'High_Priority_Incomplete_Visits' as 'Incomplete_Visits' with (length=4, predictionInterval=80, dateCols=('PlannedVisitStartTime_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', 'PlannedVisitStartTime_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', "Y-W"), seasonality=12);
visit1 = foreach visit1 generate 'PlannedVisitStartTime_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}' + "~~~" + 'PlannedVisitStartTime_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}' as 'PlannedVisitStartTime_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}~~~PlannedVisitStartTime_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', coalesce('High_Priority_Incomplete_Visits', 'Incomplete_Visits') as 'Incomplete_Visits', 'Incomplete_Visits_low_80', 'Incomplete_Visits_high_80';
-- Sales Details
sale1 = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
sale1 = group sale1 by ('Order_ActivatedDate_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', 'Order_ActivatedDate_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}');
