sale1 = timeseries sale1 generate 'sum_TotalPrice' as 'Sales' with (length=4, predictionInterval=80, dateCols=('Order_ActivatedDate_Year{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', 'Order_ActivatedDate_Week{{cell(date_parser_1.result, 0, "Is_Fiscal").asString()}}', "Y-W"), seasonality=12);
