sales_B = filter sales by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
total_sale = group sales_A by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom','Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name') full, sales_B by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom','Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name');
total_sale = foreach total_sale generate coalesce(sales_A.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', sales_B.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom') as 'Store_Group',coalesce(sales_A.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', sales_B.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name') as 'Store', sum(sales_A.'TotalPrice') as 'current_sales', sum(sales_B.'TotalPrice') as 'previous_sales', sum(sales_A.'TotalPrice') - sum(sales_B.'TotalPrice') as 'Sales_Decline', (sum(sales_A.'TotalPrice') - sum(sales_B.'TotalPrice'))/sum(sales_B.'TotalPrice') as 'Sales_Decline_percent', sum(sales_A.'Quantity') as 'current_qty', sum(sales_B.'Quantity') as 'previous_qty', sum(sales_A.'Quantity') - sum(sales_B.'Quantity') as 'Quantity_Decline', (sum(sales_A.'Quantity') - sum(sales_B.'Quantity'))/sum(sales_B.'Quantity') as 'Quantity_Decline_percent';

---Decline in SalesVelocity
total_sv = group sales_A by ('{{column(static_3.selection,["Api_name_for_decline"]).asObject()}}', 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week') full,
sales_B by ('{{column(static_3.selection,["Api_name_for_decline"]).asObject()}}', 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week');
total_sv = foreach total_sv generate coalesce(sales_A.'{{column(static_3.selection,["Api_name_for_decline"]).asObject()}}', sales_B.'{{column(static_3.selection,["Api_name_for_decline"]).asObject()}}') as 'Store_StoreGroup', sales_A.'Order_ActivatedDate_Year'+"-"+ sales_A.'Order_ActivatedDate_Week' as 'CurrentPeriod_YearWeek', sales_B.'Order_ActivatedDate_Year'+"-"+ sales_B.'Order_ActivatedDate_Week' as 'PreviousPeriod_YearWeek', sum(sales_A.'TotalPrice') as 'current_sales', sum(sales_B.'TotalPrice') as 'previous_sales';
total_sv = group total_sv by 'Store_StoreGroup';
total_sv = foreach total_sv generate 'Store_StoreGroup' as 'Store_Group', 'Store_StoreGroup' as 'Store', sum('previous_sales') / unique('PreviousPeriod_YearWeek') as 'previous_sv', ((sum('current_sales') / unique('CurrentPeriod_YearWeek')) - (sum('previous_sales') / unique('PreviousPeriod_YearWeek'))) as 'SalesVelocity_Decline', ((sum('current_sales') / unique('CurrentPeriod_YearWeek')) - (sum('previous_sales') / unique('PreviousPeriod_YearWeek'))) / (sum('previous_sales') / unique('PreviousPeriod_YearWeek')) as 'SalesVelocity_Decline_percent';

---Decline in Compliance (CG_RetailVisitKpi)
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp_A = filter comp by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
comp_B = filter comp by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
total_comp = group comp_A by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, comp_B by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_comp = foreach total_comp generate coalesce(comp_A.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', comp_B.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom') as 'Store_Group', coalesce(comp_A.'AssessmentTask_Visit_RetailStore_Name', comp_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store', avg(comp_A.'Compliance_Percentage') as 'current_comp', avg(comp_B.'Compliance_Percentage') as 'previous_comp', avg(comp_A.'Compliance_Percentage') - avg(comp_B.'Compliance_Percentage') as 'CompliancePercent_Decline';

---Decline in FacingCompliance (CG_RetailVisitKpi)
fcs_A = filter comp_A by 'Type_Computed' == "Facing";
fcs_B = filter comp_B by 'Type_Computed' == "Facing";
total_fcs = group fcs_A by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, fcs_B by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_fcs = foreach total_fcs generate coalesce(fcs_A.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', fcs_B.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom') as 'Store_Group', coalesce(fcs_A.'AssessmentTask_Visit_RetailStore_Name', fcs_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store', avg(fcs_A.'Compliance_Percentage') as 'current_fcs', avg(fcs_B.'Compliance_Percentage') as 'previous_fcs', avg(fcs_A.'Compliance_Percentage') - avg(fcs_B.'Compliance_Percentage') as 'FCS_Decline';

---Decline in SoSCompliance (CG_RetailVisitKpi)
sos_A = filter comp_A by 'Type_Computed' == "ShareOfShelf";
sos_B = filter comp_B by 'Type_Computed' == "ShareOfShelf";
total_sos = group sos_A by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, sos_B by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_sos = foreach total_sos generate coalesce(sos_A.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', sos_B.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom') as 'Store_Group', coalesce(sos_A.'AssessmentTask_Visit_RetailStore_Name', sos_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store', avg(sos_A.'Compliance_Percentage') as 'current_sos', avg(sos_B.'Compliance_Percentage') as 'previous_sos', avg(sos_A.'Compliance_Percentage') - avg(sos_B.'Compliance_Percentage') as 'SoS_Decline';

---Decline in InventoryCompliance (CG_RetailVisitKpi)
inventory_A = filter comp_A by 'Type_Computed' == "Inventory";
inventory_B = filter comp_B by 'Type_Computed' == "Inventory";
total_inventory = group inventory_A by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, inventory_B by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_inventory = foreach total_inventory generate coalesce(inventory_A.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', inventory_B.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom') as 'Store_Group', coalesce(inventory_A.'AssessmentTask_Visit_RetailStore_Name', inventory_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store', avg(inventory_A.'Compliance_Percentage') as 'current_inventory', avg(inventory_B.'Compliance_Percentage') as 'previous_inventory', avg(inventory_A.'Compliance_Percentage') - avg(inventory_B.'Compliance_Percentage') as 'Inventory_Decline';

---Decline in OutofStock (CG_RetailVisitKpi)
oos_A = filter comp_A by 'ActualBooleanValue' == "True" and 'Type_Computed' == "OutOfStock";
oos_B = filter comp_B by 'ActualBooleanValue' == "True" and 'Type_Computed' == "OutOfStock";
total_oos = group oos_A by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, oos_B by ('AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_oos = foreach total_oos generate coalesce(oos_A.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom', oos_B.'AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom') as 'Store_Group', coalesce(oos_A.'AssessmentTask_Visit_RetailStore_Name', oos_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store', count(oos_A) as 'current_oos', count(oos_B) as 'previous_oos', count(oos_A) - count(oos_B) as 'OOS_Decline';

---Decline in High Priority Visits not completed (CG_Visit)
visit = load "${App.Datasets.CG_Visit36_tp.FullyQualifiedName}";
visit = filter visit by 'Status' in ["Abandoned", "Planned"] and  'VisitPriority' == "High" and  date('PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Month', 'PlannedVisitStartTime_Day') in [.."1 day ago"];
visit_A = filter visit by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Month', 'PlannedVisitStartTime_Day')")}};
visit_B = filter visit by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('PlannedVisitStartTime_Year', 'PlannedVisitStartTime_Month', 'PlannedVisitStartTime_Day')")}};
total_visit = group visit_A by ('RetailStore_RetailStoreGroupName_Custom', 'RetailStore_Name') full,visit_B by ('RetailStore_RetailStoreGroupName_Custom', 'RetailStore_Name');
total_visit = foreach total_visit generate coalesce(visit_A.'RetailStore_RetailStoreGroupName_Custom', visit_B.'RetailStore_RetailStoreGroupName_Custom') as 'Store_Group', coalesce(visit_A.'RetailStore_Name', visit_B.'RetailStore_Name') as 'Store', count(visit_A) as 'current_HighPriorityIncompleteVisits', count(visit_B) as 'previous_HighPriorityIncompleteVisits', count(visit_A) - count(visit_B) as 'HighPriorityIncompleteVisits_Decline', (count(visit_A) - count(visit_B)) / count(visit_B) as 'HighPriorityIncompleteVisits_Decline_percent';

--Decline in Cases(CG_Case)
case1 = load "${App.Datasets.CG_Case36_tp.FullyQualifiedName}";
case_A = filter case1 by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('CreatedDate_Year', 'CreatedDate_Month', 'CreatedDate_Day')")}};
case_B = filter case1 by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('CreatedDate_Year', 'CreatedDate_Month', 'CreatedDate_Day')")}};
total_case = group case_A by ('RetailStore_RetailStoreGroupName_Custom','RetailStore_Name') full, case_B by ('RetailStore_RetailStoreGroupName_Custom','RetailStore_Name');
total_case = foreach total_case generate coalesce(case_A.'RetailStore_RetailStoreGroupName_Custom', case_B.'RetailStore_RetailStoreGroupName_Custom') as 'Store_Group',coalesce(case_A.'RetailStore_Name', case_B.'RetailStore_Name') as 'Store', count(case_A) - count(case_B) as 'Cases_Decline', count(case_A) as 'current_cases', count(case_B) as 'previous_cases', (count(case_A) - count(case_B)) / count(case_B) as 'Cases_Decline_percent';

--Final cogroup table
final_table = cogroup total_sale by  ('Store','Store_Group') full, total_sv by  ('Store','Store_Group') full, total_comp by  ('Store','Store_Group') full, total_fcs by  ('Store','Store_Group') full, total_sos by ('Store','Store_Group') full, total_inventory by ('Store','Store_Group') full, total_oos by ('Store','Store_Group') full, total_visit by ('Store','Store_Group') full, total_case by ('Store','Store_Group');

final_table = foreach final_table generate coalesce(total_sale.'Store', total_sv.'Store', total_comp.'Store', total_fcs.'Store', total_sos.'Store', total_oos.'Store', total_visit.'Store',total_case.'Store') as 'Store', coalesce(total_sale.'Store_Group', total_sv.'Store_Group', total_comp.'Store_Group', total_fcs.'Store_Group', total_sos.'Store_Group',total_oos.'Store_Group', total_visit.'Store_Group',total_case.'Store_Group') as 'Store_Group', 
sum(total_sale.'previous_sales') as 'previous_sales', sum(total_sale.'Sales_Decline') as 'Sales_Decline', 
sum(total_sale.'previous_qty') as 'previous_qty', sum(total_sale.'Quantity_Decline') as 'Quantity_Decline', 
sum(total_sv.'previous_sv') as 'previous_sv', sum(total_sv.'SalesVelocity_Decline') as 'SalesVelocity_Decline', 
sum(total_fcs.'previous_fcs') as 'previous_fcs', sum(total_fcs.'FCS_Decline') as 'FCS_Decline', 
avg(total_comp.'previous_comp') as 'previous_comp', avg(total_comp.'CompliancePercent_Decline') as 'CompliancePercent_Decline', 
avg(total_sos.'previous_sos') as 'previous_sos', avg(total_sos.'SoS_Decline') as 'SoS_Decline', 
sum(total_oos.'previous_oos') as 'previous_oos', sum(total_oos.'OOS_Decline') as 'OOS_Decline', 
sum(total_visit.'previous_HighPriorityIncompleteVisits') as 'previous_HighPriorityIncompleteVisits', sum(total_visit.'HighPriorityIncompleteVisits_Decline') as 'HighPriorityIncompleteVisits_Decline', 
sum(total_case.'previous_cases') as 'previous_cases', sum(total_case.'Cases_Decline') as 'Cases_Decline', 
sum(total_inventory.'previous_inventory') as 'previous_inventory', sum(total_inventory.'Inventory_Decline') as 'Inventory_Decline';

final_table1 = group final_table by {{column(static_3.selection,["Field_value"]).asObject()}};

final_table1 = foreach final_table1 generate {{column(static_3.selection,["Field_value"]).asObject()}}, 
sum('Sales_Decline') as 'Sales_Decline', sum('Sales_Decline') / sum('previous_sales') as 'Sales_Decline_percent',
sum('Quantity_Decline') as 'Quantity_Decline', sum('Quantity_Decline') / sum('previous_qty') as 'Quantity_Decline_percent',
sum('SalesVelocity_Decline') as 'SalesVelocity_Decline', sum('SalesVelocity_Decline') / sum('previous_sv') as 'SalesVelocity_Decline_percent',
avg('FCS_Decline') as 'FCS_Decline', avg('FCS_Decline') / avg('previous_fcs') as 'FCS_Decline_percent',
avg('CompliancePercent_Decline') as 'CompliancePercent_Decline', avg('CompliancePercent_Decline') / avg('previous_comp') as 'CompliancePercent_Decline_percent',
avg('SoS_Decline') as 'SoS_Decline', avg('SoS_Decline') / avg('previous_sos') as 'SoS_Decline_percent',
avg('Inventory_Decline') as 'Inventory_Decline', avg('Inventory_Decline') / avg('previous_inventory') as 'Inventory_Decline_percent',
sum('OOS_Decline') as 'OOS_Decline', sum('OOS_Decline') / sum('previous_oos') as 'OOS_Decline_percent',
sum('HighPriorityIncompleteVisits_Decline') as 'HighPriorityIncompleteVisits_Decline', sum('HighPriorityIncompleteVisits_Decline') / sum('previous_HighPriorityIncompleteVisits') as 'HighPriorityIncompleteVisits_Decline_percent',
sum('Cases_Decline') as 'Cases_Decline', sum('Cases_Decline') / sum('previous_cases') as 'Cases_Decline_percent';


final_table1 = foreach final_table1 generate {{row(static_3.selection,[0],["Field_value","Api_name_for_decline"]).asProjection()}}, {{row(static_1.selection,[0],["Field_value_decline","Alias_decline"]).asProjection()}}, {{row(static_1.selection,[0],["Field_value_decline_percent","Alias_decline_percent"]).asProjection()}};
final_table1 = filter final_table1 by {{cell(static_1.selection,0,"Alias_decline_percent").asObject()}} is not null;
final_table1 = order final_table1 by {{cell(static_1.selection,0,"Alias_decline").asObject()}} {{cell(static_2.selection,0,"Text_fordecline").asObject()}};

