order1 = foreach order1 generate 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name' as 'Store','Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom' as 'Store_Group','Order_ActivatedDate_Year'+"-"+'Order_ActivatedDate_Week' as 'YearWeek', sum('TotalPrice') as 'Sales',unique('OrderId') as 'Orders',sum('Quantity') as 'Quantity';
