order1 = foreach order1 generate {{column(static_3.selection,["Field_value"]).asObject()}} as 'Store', {{column(static_3.selection,["Field_value"]).asObject()}} as 'Store_Group', sum('Sales') as 'Sales', sum('Orders') as 'Orders', sum('Quantity') as 'Quantity', (sum('Sales')/unique('YearWeek')) as 'sales_velocity';

-- Compliance% Calculation
