inventory = foreach inventory generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store','AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom' as 'Store_Group',avg('Compliance_Percentage') as 'Avg_InventoryCompliance_Percentage';

