oos = foreach oos generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store','AssessmentTask_Visit_RetailStore_RetailStoreGroupName_Custom' as 'Store_Group',count() as 'OutOfStock_ActualCount';

