case1 = foreach case1 generate case1.'RetailStore_Name' as 'Store', case1.'RetailStore_RetailStoreGroupName_Custom' as 'Store_Group',count(case1) as 'Total_Cases';

--final multidataset table
final_table = cogroup order1 by ('Store','Store_Group') full, comp by ('Store','Store_Group') full, sos by ('Store','Store_Group') full, inventory by ('Store','Store_Group') full, oos by ('Store','Store_Group') full, fcs by ('Store','Store_Group') full, visit1 by ('Store','Store_Group') full, case1 by ('Store','Store_Group');
final_table = foreach final_table generate coalesce(order1.'Store', sos.'Store', oos.'Store', comp.'Store',fcs.'Store', visit1.'Store', case1.'Store', inventory.'Store') as 'Store', coalesce(order1.'Store_Group', sos.'Store_Group', oos.'Store_Group', comp.'Store_Group',fcs.'Store_Group', visit1.'Store_Group', case1.'Store_Group', inventory.'Store_Group') as 'Store_Group', avg(order1.'sales_velocity') as 'Sales_Velocity', sum(order1.'Sales') as 'Sales', sum(order1.'Quantity') as 'Quantity', sum(oos.'OutOfStock_ActualCount') as 'OutOfStock_Count', sum(case1.'Total_Cases') as 'Total_Cases', avg(sos.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage', avg(comp.'Avg_Compliance_Percentage') as 'Avg_Compliance_Percentage', avg(fcs.'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage', sum(visit1.'High_Priority_Incomplete_Visits')as 'High_Priority_Incomplete_Visits', avg(inventory.'Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
final_table1 = group final_table by {{column(static_3.selection,["Field_value"]).asObject()}};
final_table1 = filter final_table1 by {{cell(static_1.selection,0,"Alias_name").asObject()}} is not null;
final_table1 = foreach final_table1 generate {{row(static_3.selection,[0],["Field_value","Api_name"]).asProjection()}},{{cell(static_1.selection,0,"Field_value").asObject()}} as '{{cell(static_1.selection,0,"Alias_name").asObject()}}';
final_table1 = order final_table1 by '{{cell(static_1.selection,0,"Alias_name").asObject()}}' {{cell(static_2.selection,0,"Text2").asObject()}};

