range_compliance_metrics.query:
retail = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";

-- Compliance% Calculation
comp = filter retail by 'Include_In_Compliance' == "True";
comp_result = group comp by ('AssessmentTask_Visit_RetailStore_Name');
comp_result = foreach comp_result generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_CompliancePercentage';
comp_result = group comp_result by all;
comp_result = foreach comp_result generate min('Avg_CompliancePercentage') as 'min_Avg_CompliancePercentage', max('Avg_CompliancePercentage') as 'max_Avg_CompliancePercentage';

--OutOfStockCompliance
oos = filter retail by 'Include_In_Compliance'== "True" and 'Type_Computed' == "OutOfStock";
oos_result = group oos by ('AssessmentTask_Visit_RetailStore_Name');
oos_result = foreach oos_result generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_OutOfStock_Compliance';
oos_result = group oos_result by all;
oos_result = foreach oos_result generate min('Avg_OutOfStock_Compliance') as 'min_Avg_OutOfStock_Compliance', max('Avg_OutOfStock_Compliance') as 'max_Avg_OutOfStock_Compliance';

-- Share of Shelf
sos = filter retail by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = group sos by ('AssessmentTask_Visit_RetailStore_Name');
sos = foreach sos generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_SoSCompliance_Percentage';
sos = group sos by all;
sos = foreach sos generate min('Avg_SoSCompliance_Percentage') as 'min_Avg_SoSCompliance_Percentage', max('Avg_SoSCompliance_Percentage') as 'max_Avg_SoSCompliance_Percentage';

--FacingsCompliance% Calculation
fcs = filter retail by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs = group fcs by  ('AssessmentTask_Visit_RetailStore_Name');
fcs = foreach fcs generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
fcs = group fcs by all;
fcs = foreach fcs generate min('Avg_FacingsCompliance_Percentage') as 'min_Avg_FacingsCompliance_Percentage', max('Avg_FacingsCompliance_Percentage') as 'max_Avg_FacingsCompliance_Percentage';

--PromotionCompliance
promo = filter retail by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Promotion";
promo = group promo by ('AssessmentTask_Visit_RetailStore_Name');
promo = foreach promo generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_PromoCompliance_Percentage';
promo = group promo by all;
promo = foreach promo generate min('Avg_PromoCompliance_Percentage') as 'min_Avg_PromoCompliance_Percentage', max('Avg_PromoCompliance_Percentage') as 'max_Avg_PromoCompliance_Percentage';

--InventoryCompliance
inv = filter retail by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Inventory";
inv = group inv by ('AssessmentTask_Visit_RetailStore_Name');
inv = foreach inv generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
inv = group inv by all;
inv = foreach inv generate min('Avg_InventoryCompliance_Percentage') as 'min_Avg_InventoryCompliance_Percentage', max('Avg_InventoryCompliance_Percentage') as 'max_Avg_InventoryCompliance_Percentage';

--final multidataset table 
final_table = cogroup sos by all full, oos_result by all full, comp_result by all full,fcs by all full, promo by all full, inv by all;

final_table = foreach final_table generate

avg(oos_result.'min_Avg_OutOfStock_Compliance') as 'min_Avg_OutOfStock_Compliance', 
avg(oos_result.'max_Avg_OutOfStock_Compliance') as 'max_Avg_OutOfStock_Compliance', 
avg(comp_result.'min_Avg_CompliancePercentage') as 'min_Avg_CompliancePercentage', 
avg(comp_result.'max_Avg_CompliancePercentage') as 'max_Avg_CompliancePercentage', 
avg(promo.'min_Avg_PromoCompliance_Percentage') as 'min_Avg_PromoCompliance_Percentage', 
avg(promo.'max_Avg_PromoCompliance_Percentage') as 'max_Avg_PromoCompliance_Percentage', 
avg(sos.'min_Avg_SoSCompliance_Percentage') as 'min_Avg_SoSCompliance_Percentage', 
avg(sos.'max_Avg_SoSCompliance_Percentage') as 'max_Avg_SoSCompliance_Percentage', 
avg(fcs.'min_Avg_FacingsCompliance_Percentage') as 'min_Avg_FacingsCompliance_Percentage', 
avg(fcs.'max_Avg_FacingsCompliance_Percentage') as 'max_Avg_FacingsCompliance_Percentage', 
avg(inv.'min_Avg_InventoryCompliance_Percentage') as 'min_Avg_InventoryCompliance_Percentage', 
avg(inv.'max_Avg_InventoryCompliance_Percentage') as 'max_Avg_InventoryCompliance_Percentage';

final_table = foreach final_table generate round('{{cell(picklist_compliance_metrics.selection, 0, "min").asString()}}',2) as 'min', round('{{cell(picklist_compliance_metrics.selection, 0, "max").asString()}}',2) as 'max';

