Parent_Account_metri_1.query:
--OutOfStock
oos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
oos = filter oos by 'ActualBooleanValue'== "True" and 'Type_Computed' == "OutOfStock";
oos_result = group oos by 'AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names';
oos_result = foreach oos_result generate oos.'AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names' as 'Parent_Account', count(oos) as 'OutOfStock_Count';
-- Order Details
order1 = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
order1 = group order1 by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names','Order_ActivatedDate_Year','Order_ActivatedDate_Week');
order1 = foreach order1 generate 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names' as 'Parent_Account','Order_ActivatedDate_Year'+"-"+'Order_ActivatedDate_Week' as 'YearWeek', sum('TotalPrice') as 'Sales', sum('Quantity') as 'Quantity';
order1 = group order1 by 'Parent_Account';
order1 = foreach order1 generate 'Parent_Account', (sum('Sales')/unique('YearWeek')) as 'avg_sales_velocity', sum('Sales') as 'total_sales',sum('Quantity') as 'total_quantity';
-- Share of Shelf
sos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
sos = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = group sos by ('AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names', 'AssessmentTask_Visit_RetailStore_Name');
sos = foreach sos generate 'AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names' as 'Parent_Account', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as Avg_SoSCompliance_Percentage;
sos = group sos by 'Parent_Account';
sos = foreach sos generate 'Parent_Account', avg('Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage';
-- Compliance% Calculation
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp_result = group comp by ('AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names','AssessmentTask_Visit_RetailStore_Name');
comp_result = foreach comp_result generate 'AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names' as 'Parent_Account', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_Compliance_Percentage';
comp_result = group comp_result by 'Parent_Account';
comp_result = foreach comp_result generate 'Parent_Account', avg('Avg_Compliance_Percentage') as 'Avg_CompliancePercentage';
-- FacingsCompliance% Calculation
fcs = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
fcs = filter fcs by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs = group fcs by ('AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names', 'AssessmentTask_Visit_RetailStore_Name');
fcs = foreach fcs generate 'AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names' as 'Parent_Account', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
fcs = group fcs by 'Parent_Account';
fcs = foreach fcs generate 'Parent_Account', avg('Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
--PromotionCompliance
promo = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
promo = filter promo by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Promotion";
promo = group promo by ('AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names', 'AssessmentTask_Visit_RetailStore_Name');
promo = foreach promo generate 'AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names' as 'Parent_Account', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_PromoCompliance_Percentage';
promo = group promo by 'Parent_Account';
promo = foreach promo generate 'Parent_Account', avg('Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage';
--InventoryCompliance
inv = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
inv = filter inv by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Inventory";
inv = group inv by ('AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names', 'AssessmentTask_Visit_RetailStore_Name');
inv = foreach inv generate 'AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names' as 'Parent_Account', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
inv = group inv by 'Parent_Account';
inv = foreach inv generate 'Parent_Account', avg('Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
--final multidataset table 
final_table = cogroup order1 by 'Parent_Account' full, sos by 'Parent_Account' full, oos_result by 'Parent_Account' full, comp_result by 'Parent_Account' full,fcs by 'Parent_Account' full, promo by 'Parent_Account' full, inv by 'Parent_Account';
final_table = foreach final_table generate coalesce(order1.'Parent_Account',oos_result.'Parent_Account',comp_result.'Parent_Account',fcs.'Parent_Account',sos.'Parent_Account', promo.'Parent_Account', inv.'Parent_Account') as 'Parent Account',
sum(order1.'avg_sales_velocity') as 'avg_sales_velocity',sum(order1.'total_sales') as 'total_sales',sum(order1.'total_quantity') as 'total_quantity',
sum(oos_result.'OutOfStock_Count') as 'OutOfStock_Count',avg(sos.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage',avg(comp_result.'Avg_CompliancePercentage') as 'Avg_CompliancePercentage',avg(fcs.'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage', avg(promo.'Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage', avg(inv.'Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
final_table1 = group final_table by 'Parent Account';
final_table1 = filter final_table1 by {{cell(picklist_1_1.selection,0,"Api_name").asObject()}} is not null;
final_table1 = foreach final_table1 generate 'Parent Account' as 'AssessmentTask_Visit_RetailStore_Account_Parent_Account_Names', {{cell(picklist_1_1.selection,0,"Field_value").asObject()}}  as '{{cell(picklist_1_1.selection,0,"Api_name").asObject()}}';
final_table1 = order final_table1 by {{cell(picklist_1_1.selection,0,"Api_name").asObject()}} desc;

