date_parser_1.query:
q = load "${App.Datasets.CG_RetailStore20_tp.FullyQualifiedName}";
q = foreach q generate "1970" as 'Date_Year', "01" as 'Date_Month', "01" as 'Date_Day';
q = group q by ('Date_Year', 'Date_Month', 'Date_Day');
q = foreach q generate 'Date_Year', 'Date_Month', 'Date_Day', count() as 'sum';
-- use fill() to generate null rows for any missing dates. Then you can use timeseries().
q = fill q by (dateCols=('Date_Year','Date_Month','Date_Day', "Y-M-D"));
q = timeseries q generate 'sum' as 'cnt' with (length=60000,  dateCols=('Date_Year','Date_Month','Date_Day', "Y-M-D"));
q = foreach q generate 'Date_Year' + "-" + 'Date_Month' + "-" + 'Date_Day'+ " 00:00:00" as 'Date','Date_Year', 'Date_Month', 'Date_Day';
q_A = filter q by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Date_Year', 'Date_Month', 'Date_Day')")}};
q_A = foreach q_A generate toDate('Date') as 'Date', date_to_epoch(toDate('Date')) as 'Date_sec_epoch';
q_A = group q_A by all;
q_A = foreach q_A generate min('Date') as 'min_Date', max('Date') as 'max_Date', min('Date_sec_epoch') as 'min_Date_sec_epoch', max('Date_sec_epoch') as 'max_Date_sec_epoch';
q_B = filter q by date('Date_Year', 'Date_Month', 'Date_Day') in ["current fiscal_year".."current fiscal_year"];
q_B = foreach q_B generate toDate('Date') as 'Date', date_to_epoch(toDate('Date')) as 'Date_sec_epoch';
q_B = group q_B by all;
q_B = foreach q_B generate min('Date') as 'min_current_fiscalYr_Date', max('Date') as 'max_current_fiscalYr_Date', min('Date_sec_epoch') as 'min_current_fiscalYr_Date_sec_epoch', max('Date_sec_epoch') as 'max_current_fiscalYr_Date_sec_epoch';
q_C = filter q by date('Date_Year', 'Date_Month', 'Date_Day') in ["current year".."current year"];
q_C = foreach q_C generate toDate('Date') as 'Date', date_to_epoch(toDate('Date')) as 'Date_sec_epoch';
q_C = group q_C by all;
q_C = foreach q_C generate min('Date') as 'min_current_CalendarYr_Date', max('Date') as 'max_current_CalendarYr_Date', min('Date_sec_epoch') as 'min_current_CalendarYr_Date_sec_epoch', max('Date_sec_epoch') as 'max_current_CalendarYr_Date_sec_epoch';
q = cogroup q_A by all full, q_B by all full, q_C by all;
q = foreach q generate first(q_A.'min_Date') as 'min_Date', toDate(first(q_A.'max_Date_sec_epoch')+86399) as 'max_Date', first(q_A.'min_Date_sec_epoch') as 'min_Date_sec_epoch', first(q_A.'max_Date_sec_epoch')+86399 as 'max_Date_sec_epoch', first(q_B.'min_current_fiscalYr_Date') as 'min_current_fiscalYr_Date', toDate(first(q_B.'max_current_fiscalYr_Date_sec_epoch')+86399) as 'max_current_fiscalYr_Date', first(q_C.'min_current_CalendarYr_Date') as 'min_current_CalendarYr_Date', toDate(first(q_C.'max_current_CalendarYr_Date_sec_epoch')+86399) as 'max_current_CalendarYr_Date', (case when "{{coalesce(cell(Current_Period_1.selection,0,"min"),"All").asObject()}}" matches "fiscal" and first(q_C.'min_current_CalendarYr_Date') != first(q_B.'min_current_fiscalYr_Date') then "_Fiscal" else "" end) as 'Is_Fiscal';
q = foreach q generate 'min_Date', 'max_Date', 'min_Date_sec_epoch', 'max_Date_sec_epoch', 'min_current_fiscalYr_Date', 'min_current_CalendarYr_Date', "["+replace(toString('min_Date',"yyyy-MM-dd"),"-",",")+"]" as 'Date_Min', "["+replace(toString('max_Date',"yyyy-MM-dd"),"-",",")+"]" as 'Date_Max', 'Is_Fiscal';

