visit = foreach visit generate  'Store','Store Group','Account','State','City','Location', daysBetween(toDate(last_visited_time), now() ) as 'days_since_last_visited';

--final multidataset table
final_table = cogroup store1 by ('Store','Store Group','Account','State','City','Location') full,order1 by ('Store','Store Group','Account','State','City','Location') full,sos by ('Store','Store Group','Account','State','City','Location') full,fcs by ('Store','Store Group','Account','State','City','Location') full,oos_result by ('Store','Store Group','Account','State','City','Location') full,comp by ('Store','Store Group','Account','State','City','Location') full,promo by ('Store','Store Group','Account','State','City','Location')full,nps_result by ('Store','Store Group','Account','State','City','Location') full,case1 by ('Store','Store Group','Account','State','City','Location') full,visit by ('Store','Store Group','Account','State','City','Location');

final_table = foreach final_table generate coalesce(store1.'Store', order1.'Store', sos.'Store', fcs.'Store', oos_result.'Store',comp.'Store',promo.'Store',nps_result.'Store',case1.'Store',visit.'Store') as 'RetailStore_Name', coalesce(store1.'Location', order1.'Location', sos.'Location', fcs.'Location', oos_result.'Location',comp.'Location',promo.'Location',nps_result.'Location',case1.'Location',visit.'Location') as 'RetailStore_Location_Name',coalesce(store1.'Account', order1.'Account', sos.'Account', fcs.'Account', oos_result.'Account',comp.'Account',promo.'Account',nps_result.'Account',case1.'Account',visit.'Account') as 'RetailStore_Account_Name',coalesce(store1.'State', order1.'State', sos.'State', fcs.'State',oos_result.'State',comp.'State',promo.'State',nps_result.'State',case1.'State',visit.'State') as 'RetailStore_Location_Address_State',coalesce(store1.'City', order1.'City', sos.'City', fcs.'City',oos_result.'City',comp.'City',promo.'City',nps_result.'City',case1.'City',visit.'City') as 'RetailStore_Location_Address_City'
,sum(order1.'Sales') as 'Sales',sum(order1.'Quantity') as 'Quantity',avg(sos.'SoS_Compliance_Percentage') as 'Share_of_Shelf',sum(oos_result.'OOS_Compliance_Percentage') as 'Out_of_Stock',avg(fcs.'FCS_Compliance_Percentage') as 'Facings',avg(promo.'Promo_Compliance_Percentage') as 'Promotions',
sum(nps_result.'NPS_score_percentage') as 'NPS',sum(case1.'Total_Cases') as 'Total Cases',sum(visit.'days_since_last_visited') as 'days_since_last_visit',avg(comp.'Compliance_Percentage') as 'Compliance_Percentage_1';
final_table = filter final_table by 'RetailStore_Name' is not null; 
final_table = order final_table  by 'Compliance_Percentage_1' desc;

Prod_ProdCat_Metric_1.query:
-- Compliance% Calculation
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp_result = group comp by ('ProductCategory_Name_Custom','Product_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
comp_result = foreach comp_result generate 'ProductCategory_Name_Custom' as 'Product_Category','Product_Name_Custom' as 'Product', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_Compliance_Percentage';
comp_result = group comp_result by  ('Product_Category','Product');
comp_result = foreach comp_result generate 'Product_Category' as 'Product_Category','Product' as 'Product', avg('Avg_Compliance_Percentage') as 'Avg_CompliancePercentage';
-- Order Details
order1 = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
order1 = group order1 by ({{column(product_toggle_1.selection,["Field_Value"]).asObject()}},'Order_ActivatedDate_Year','Order_ActivatedDate_Week');
order1 = foreach order1 generate {{column(product_toggle_1.selection,["Field_Value"]).asObject()}},'Order_ActivatedDate_Year'+"-"+'Order_ActivatedDate_Week' 
as 'YearWeek', sum('TotalPrice') as 'Sales',sum('Quantity') as 'Quantity';
order1 = group order1 by ({{column(product_toggle_1.selection,["Field_Value"]).asObject()}});
order1 = foreach order1 generate {{column(product_toggle_1.selection,["Field_Value"]).asObject()}} as 'Product_Category', {{column(product_toggle_1.selection,["Field_Value"]).asObject()}} as 'Product', (sum('Sales')/unique('YearWeek')) as 'avg_sales_velocity',sum('Sales') as 'Sales',sum('Quantity') as 'Quantity';
-- Share of Shelf
sos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
sos = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = group sos by ('ProductCategory_Name_Custom','Product_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
sos = foreach sos generate 'ProductCategory_Name_Custom' as 'Product_Category','Product_Name_Custom' as 'Product', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_SoSCompliance_Percentage';
sos = group sos by ('Product_Category','Product');
sos = foreach sos generate 'Product_Category', 'Product', avg('Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage';
--OutOfStock
oos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
oos = filter oos by 'ActualBooleanValue'== "True" and 'Type_Computed' == "OutOfStock";
oos_result = group oos by ('ProductCategory_Name_Custom','Product_Name_Custom');
oos_result = foreach oos_result generate oos.'ProductCategory_Name_Custom' as 'Product_Category',oos.'Product_Name_Custom' as 'Product',count(oos) as 'OutOfStock_Count';
-- FacingsCompliance% Calculation
fcs = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
fcs = filter fcs by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs = group fcs by  ('ProductCategory_Name_Custom','Product_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
fcs = foreach fcs generate 'ProductCategory_Name_Custom' as 'Product_Category','Product_Name_Custom' as 'Product', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
fcs = group fcs by ('Product_Category','Product');
fcs = foreach fcs generate 'Product_Category', 'Product', avg('Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
--PromotionCompliance
promo = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
promo = filter promo by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Promotion";
promo = group promo by ('ProductCategory_Name_Custom','Product_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
promo = foreach promo generate 'ProductCategory_Name_Custom' as 'Product_Category','Product_Name_Custom' as 'Product', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_PromoCompliance_Percentage';
promo = group promo by ('Product_Category','Product');
promo = foreach promo generate 'Product_Category', 'Product', avg('Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage';
--InventoryCompliance
inv = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
inv = filter inv by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Inventory";
inv = group inv by ('ProductCategory_Name_Custom','Product_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
inv = foreach inv generate 'ProductCategory_Name_Custom' as 'Product_Category','Product_Name_Custom' as 'Product', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
inv = group inv by ('Product_Category','Product');
inv = foreach inv generate 'Product_Category', 'Product', avg('Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
--final multidataset table 
final_table = cogroup order1 by ('Product_Category','Product') full, sos by ('Product_Category','Product') full, oos_result by ('Product_Category','Product') full, comp_result by ('Product_Category','Product') full,fcs by ('Product_Category','Product') full, promo by ('Product_Category','Product') full, inv by ('Product_Category','Product');
final_table = foreach final_table generate coalesce(comp_result.'Product_Category',order1.'Product_Category',sos.'Product_Category',oos_result.'Product_Category',fcs.'Product_Category',promo.'Product_Category',inv.'Product_Category') as 'ProductCategory_Name_Custom', coalesce(comp_result.'Product',order1.'Product',sos.'Product',oos_result.'Product',fcs.'Product',promo.'Product',inv.'Product') as 'Product_Name_Custom', 
avg(order1.'avg_sales_velocity') as 'avg_sales_velocity',sum(order1.'Sales') as 'total_sales',sum(order1.'Quantity') as 'total_quantity', sum(oos_result.'OutOfStock_Count') as 'OutOfStock_Count',avg(sos.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage',avg(comp_result.'Avg_CompliancePercentage') as 'Avg_CompliancePercentage',avg(fcs.'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage',avg(promo.'Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage', avg(inv.'Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
final_table1 = group final_table by '{{column(product_toggle_1.selection,["API_Name"]).asObject()}}';
final_table1 = filter final_table1 by {{cell(picklist_1_1.selection,0,"Api_name").asObject()}} is not null;
final_table1 = foreach final_table1 generate '{{column(product_toggle_1.selection,["API_Name"]).asObject()}}',{{cell(picklist_1_1.selection,0,"Field_value").asObject()}}  as '{{cell(picklist_1_1.selection,0,"Api_name").asObject()}}';
final_table1 = order final_table1 by {{cell(picklist_1_1.selection,0,"Api_name").asObject()}} desc;

Prod_ProdCat_Change_1.query:
---Decline in Compliance (CG_RetailVisitKpi)
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp_A = filter comp by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
comp_B = filter comp by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
total_comp = group comp_A by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, comp_B by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_comp = foreach total_comp generate coalesce(comp_A.'ProductCategory_Name_Custom', comp_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom',
coalesce(comp_A.'Product_Name_Custom', comp_B.'Product_Name_Custom') as 'Product_Name_Custom', coalesce(comp_A.'AssessmentTask_Visit_RetailStore_Name', comp_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(comp_A.'Compliance_Percentage') as 'current_comp', avg(comp_B.'Compliance_Percentage') as 'previous_comp';
total_comp = group total_comp by ('ProductCategory_Name_Custom', 'Product_Name_Custom');
total_comp = foreach total_comp generate 'ProductCategory_Name_Custom', 'Product_Name_Custom', avg('current_comp') as 'current_comp', avg('previous_comp') as 'previous_comp';
---Decline in OutofStock (CG_RetailVisitKpi)
oos_A = filter comp_A by 'ActualBooleanValue' == "True" and 'Type_Computed' == "OutOfStock";
oos_B = filter comp_B by 'ActualBooleanValue' == "True" and 'Type_Computed' == "OutOfStock";
total_oos = group oos_A by ('ProductCategory_Name_Custom', 'Product_Name_Custom') full, oos_B by ('ProductCategory_Name_Custom', 'Product_Name_Custom');
total_oos = foreach total_oos generate coalesce(oos_A.'ProductCategory_Name_Custom', oos_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(oos_A.'Product_Name_Custom', oos_B.'Product_Name_Custom') as 'Product_Name_Custom', count(oos_A) as 'current_oos', count(oos_B) as 'previous_oos';

-- Decline in Sales(CG_OrderItem)
sales = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
sales_A = filter sales by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
sales_B = filter sales by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
total_sale = group sales_A by ({{column(product_toggle_1.selection,["Field_Value"]).asObject()}}) full, sales_B by ({{column(product_toggle_1.selection,["Field_Value"]).asObject()}});
total_sale = foreach total_sale generate coalesce(sales_A.{{column(product_toggle_1.selection,["Field_Value"]).asObject()}}, sales_B.{{column(product_toggle_1.selection,["Field_Value"]).asObject()}}) as 'Product_ProductCategory_ProductCategory_Name', coalesce(sales_A.{{column(product_toggle_1.selection,["Field_Value"]).asObject()}}, sales_B.{{column(product_toggle_1.selection,["Field_Value"]).asObject()}}) as 'Product_Name', sum(sales_A.'TotalPrice') as 'current_sales', sum(sales_B.'TotalPrice') as 'previous_sales', sum(sales_A.'Quantity') as 'current_qty', sum(sales_B.'Quantity') as 'previous_qty';

---Decline in SalesVelocity
total_sv = group sales_A by ({{column(product_toggle_1.selection,["Field_Value"]).asObject()}}, 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week') full, sales_B by ({{column(product_toggle_1.selection,["Field_Value"]).asObject()}}, 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week');

total_sv = foreach total_sv generate coalesce(sales_A.{{column(product_toggle_1.selection,["Field_Value"]).asObject()}}, sales_B.{{column(product_toggle_1.selection,["Field_Value"]).asObject()}}) as 'Product_ProductCategory_ProductCategory_Name', coalesce(sales_A.{{column(product_toggle_1.selection,["Field_Value"]).asObject()}}, sales_B.{{column(product_toggle_1.selection,["Field_Value"]).asObject()}}) as 'Product_Name', sales_A.'Order_ActivatedDate_Year'+"-"+ sales_A.'Order_ActivatedDate_Week' as 'CurrentPeriod_YearWeek', sales_B.'Order_ActivatedDate_Year'+"-"+ sales_B.'Order_ActivatedDate_Week' as 'PreviousPeriod_YearWeek', sum(sales_A.'TotalPrice') as 'current_sales', sum(sales_B.'TotalPrice') as 'previous_sales';

total_sv = group total_sv by ('Product_ProductCategory_ProductCategory_Name', 'Product_Name');
total_sv = foreach total_sv generate 'Product_ProductCategory_ProductCategory_Name', 'Product_Name', sum('current_sales') / unique('CurrentPeriod_YearWeek') as 'current_sv', sum('previous_sales') / unique('PreviousPeriod_YearWeek') as 'previous_sv';

---Decline in FacingCompliance (CG_RetailVisitKpi)
fcs_A = filter comp_A by 'Type_Computed' == "Facing";
fcs_B = filter comp_B by 'Type_Computed' == "Facing";
total_fcs = group fcs_A by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, fcs_B by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_fcs = foreach total_fcs generate coalesce(fcs_A.'ProductCategory_Name_Custom', fcs_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(fcs_A.'Product_Name_Custom', fcs_B.'Product_Name_Custom') as 'Product_Name_Custom', coalesce(fcs_A.'AssessmentTask_Visit_RetailStore_Name', fcs_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(fcs_A.'Compliance_Percentage') as 'current_fcs', avg(fcs_B.'Compliance_Percentage') as 'previous_fcs';
total_fcs = group total_fcs by ('ProductCategory_Name_Custom', 'Product_Name_Custom');
total_fcs = foreach total_fcs generate 'ProductCategory_Name_Custom', 'Product_Name_Custom', avg('current_fcs') as 'current_fcs', avg('previous_fcs') as 'previous_fcs';
---Decline in SoSCompliance (CG_RetailVisitKpi)
sos_A = filter comp_A by 'Type_Computed' == "ShareOfShelf";
sos_B = filter comp_B by 'Type_Computed' == "ShareOfShelf";
total_sos = group sos_A by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, sos_B by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_sos = foreach total_sos generate coalesce(sos_A.'ProductCategory_Name_Custom', sos_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(sos_A.'Product_Name_Custom', sos_B.'Product_Name_Custom') as 'Product_Name_Custom', coalesce(sos_A.'AssessmentTask_Visit_RetailStore_Name', sos_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(sos_A.'Compliance_Percentage') as 'current_sos', avg(sos_B.'Compliance_Percentage') as 'previous_sos';
total_sos = group total_sos by ('ProductCategory_Name_Custom', 'Product_Name_Custom');
total_sos = foreach total_sos generate 'ProductCategory_Name_Custom', 'Product_Name_Custom', avg('current_sos') as 'current_sos', avg('previous_sos') as 'previous_sos';
---Decline in PromotionCompliance (CG_RetailVisitKpi)
promo_A = filter comp_A by 'Type_Computed' == "OutofStock";
promo_B = filter comp_B by 'Type_Computed' == "OutofStock";
total_promo = group promo_A by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, promo_B by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_promo = foreach total_promo generate coalesce(promo_A.'ProductCategory_Name_Custom', promo_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(promo_A.'Product_Name_Custom', promo_B.'Product_Name_Custom') as 'Product_Name_Custom', coalesce(promo_A.'AssessmentTask_Visit_RetailStore_Name', promo_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(promo_A.'Compliance_Percentage') as 'current_promo', avg(promo_B.'Compliance_Percentage') as 'previous_promo';
total_promo = group total_promo by ('ProductCategory_Name_Custom', 'Product_Name_Custom');
total_promo = foreach total_promo generate 'ProductCategory_Name_Custom', 'Product_Name_Custom', avg('current_promo') as 'current_promo', avg('previous_promo') as 'previous_promo';
---Decline in InventoryCompliance (CG_RetailVisitKpi)
inventory_A = filter comp_A by 'Type_Computed' == "Inventory";
inventory_B = filter comp_B by 'Type_Computed' == "Inventory";
total_inventory = group inventory_A by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name') full, inventory_B by ('ProductCategory_Name_Custom', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
total_inventory = foreach total_inventory generate coalesce(inventory_A.'ProductCategory_Name_Custom', inventory_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(inventory_A.'Product_Name_Custom', inventory_B.'Product_Name_Custom') as 'Product_Name_Custom', coalesce(inventory_A.'AssessmentTask_Visit_RetailStore_Name', inventory_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(inventory_A.'Compliance_Percentage') as 'current_inventory', avg(inventory_B.'Compliance_Percentage') as 'previous_inventory';
total_inventory = group total_inventory by ('ProductCategory_Name_Custom', 'Product_Name_Custom');
total_inventory = foreach total_inventory generate 'ProductCategory_Name_Custom', 'Product_Name_Custom', avg('current_inventory') as 'current_inventory', avg('previous_inventory') as 'previous_inventory';

--final multidataset table
final_table = cogroup total_sale by ('Product_ProductCategory_ProductCategory_Name', 'Product_Name') full, total_sv by ('Product_ProductCategory_ProductCategory_Name', 'Product_Name') full, total_comp by ('ProductCategory_Name_Custom', 'Product_Name_Custom') full, total_fcs by  ('ProductCategory_Name_Custom', 'Product_Name_Custom') full, total_sos by ('ProductCategory_Name_Custom', 'Product_Name_Custom') full, total_oos by ('ProductCategory_Name_Custom', 'Product_Name_Custom') full,total_promo by ('ProductCategory_Name_Custom', 'Product_Name_Custom') full, total_inventory by ('ProductCategory_Name_Custom', 'Product_Name_Custom');
final_table = foreach final_table generate coalesce(total_sale.'Product_ProductCategory_ProductCategory_Name', total_sv.'Product_ProductCategory_ProductCategory_Name', total_fcs.'ProductCategory_Name_Custom',total_comp.'ProductCategory_Name_Custom',total_sos.'ProductCategory_Name_Custom',total_oos.'ProductCategory_Name_Custom',total_promo.'ProductCategory_Name_Custom', total_inventory.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', 

coalesce(total_sale.'Product_Name', total_sv.'Product_Name', total_fcs.'Product_Name_Custom',total_comp.'Product_Name_Custom',total_sos.'Product_Name_Custom',total_oos.'Product_Name_Custom',total_promo.'Product_Name_Custom', total_inventory.'Product_Name_Custom') as 'Product_Name_Custom', 

 avg(total_comp.'current_comp') as 'current_comp', sum(total_oos.'current_oos') as 'current_oos', sum(total_sale.'current_sales') as 'current_sales', sum(total_sale.'current_qty') as 'current_qty', sum(total_sv.'current_sv') as 'current_sv', avg(total_fcs.'current_fcs') as 'current_fcs', avg(total_sos.'current_sos') as 'current_sos', avg(total_promo.'current_promo') as 'current_promo', avg(total_inventory.'current_inventory') as 'current_inventory', 
 
 avg(total_comp.'previous_comp') as 'previous_comp', sum(total_oos.'previous_oos') as 'previous_oos', sum(total_sale.'previous_sales') as 'previous_sales', sum(total_sale.'previous_qty') as 'previous_qty', sum(total_sv.'previous_sv') as 'previous_sv', avg(total_fcs.'previous_fcs') as 'previous_fcs', avg(total_sos.'previous_sos') as 'previous_sos', avg(total_promo.'previous_promo') as 'previous_promo', avg(total_inventory.'previous_inventory') as 'previous_inventory';

final_table1 = group final_table by '{{column(product_toggle_1.selection,["API_Name"]).asObject()}}';
final_table1 = foreach final_table1 generate '{{column(product_toggle_1.selection,["API_Name"]).asObject()}}', avg('current_comp') as 'current_comp', sum('current_oos') as 'current_oos', sum('current_sales') as 'current_sales', sum('current_qty') as 'current_qty', sum('current_sv') as 'current_sv', avg('current_fcs') as 'current_fcs', avg('current_sos') as 'current_sos', avg('current_promo') as 'current_promo', avg('current_inventory') as 'current_inventory', 
 
 avg('previous_comp') as 'previous_comp', sum('previous_oos') as 'previous_oos', sum('previous_sales') as 'previous_sales', sum('previous_qty') as 'previous_qty', sum('previous_sv') as 'previous_sv', avg('previous_fcs') as 'previous_fcs', avg('previous_sos') as 'previous_sos', avg('previous_promo') as 'previous_promo', avg('previous_inventory') as 'previous_inventory';
final_table1 = foreach final_table1 generate '{{column(product_toggle_1.selection,["API_Name"]).asObject()}}', 'current_comp' as 'Avg_CompliancePercentage', (current_comp-previous_comp)/previous_comp as 'CompliancePercent_Decline_percent', 'current_oos' as 'OutOfStock_Count', ('current_oos'-'previous_oos')/'previous_oos' as 'OOS_Decline_percent', 'current_sales' as  'total_sales', ('current_sales'-'previous_sales')/'previous_sales' as 'Sales_Decline_percent', 'current_qty' as 'total_quantity', ('current_qty'-'previous_qty')/'previous_qty' as 'Quantity_Decline_percent', 'current_sv' as 'avg_sales_velocity', ('current_sv'-'previous_sv')/'previous_sv' as 'SalesVelocity_Decline_percent', 'current_fcs' as 'Avg_FacingsCompliance_Percentage', (current_fcs-previous_fcs)/previous_fcs as 'FCS_Decline_percent', 'current_sos' as 'Avg_SoSCompliance_Percentage', (current_sos-previous_sos)/previous_sos as 'SoS_Decline_percent', 'current_promo' as 'Avg_PromoCompliance_Percentage', (current_promo-previous_promo)/(previous_promo) as 'Promo_Decline_percent', 'current_inventory' as 'Avg_InventoryCompliance_Percentage', (current_inventory - previous_inventory)/previous_inventory as 'Inventory_Decline_percent';
final_table1 = foreach final_table1 generate '{{column(product_toggle_1.selection,["API_Name"]).asObject()}}', '{{cell(picklist_1_1.selection,0,"Api_name").asObject()}}', {{row(picklist_1_1.selection,[0],["Field_value_decline_percent","Alias_decline_percent"]).asProjection()}};
