zipcode_metric_1.query:
--OutOfStock
oos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
oos = filter oos by 'ActualBooleanValue'== "True" and 'Type_Computed' == "OutOfStock";
oos_result = group oos by 'AssessmentTask_Visit_RetailStore_Location_Address_City';
oos_result = foreach oos_result generate oos.'AssessmentTask_Visit_RetailStore_Location_Address_City' as 'City',count(oos) as 'OutOfStock_Count';

-- Order Details
order1 = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
order1 = group order1 by( 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Location_Address_City','Order_ActivatedDate_Year','Order_ActivatedDate_Week');
order1 = foreach order1 generate 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Location_Address_City' as 'City','Order_ActivatedDate_Year'+"-"+'Order_ActivatedDate_Week' as 'YearWeek', sum('TotalPrice') as 'total_sales', sum('Quantity') as 'total_quantity';
order1 = group order1 by 'City';
order1 = foreach order1 generate 'City', (sum('total_sales')/unique('YearWeek')) as 'avg_sales_velocity', sum('total_sales') as 'total_sales',sum('total_quantity') as 'total_quantity';
-- Share of Shelf
sos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
sos = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = group sos by ('AssessmentTask_Visit_RetailStore_Location_Address_City', 'AssessmentTask_Visit_RetailStore_Name');
sos = foreach sos generate 'AssessmentTask_Visit_RetailStore_Location_Address_City' as 'City', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as Avg_SoSCompliance_Percentage;
sos = group sos by 'City';
sos = foreach sos generate 'City', avg('Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage';

-- Compliance% Calculation
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp_result = group comp by ('AssessmentTask_Visit_RetailStore_Location_Address_City','AssessmentTask_Visit_RetailStore_Name');
comp_result = foreach comp_result generate 'AssessmentTask_Visit_RetailStore_Location_Address_City' as 'City', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_CompliancePercentage';
comp_result = group comp_result by 'City';
comp_result = foreach comp_result generate 'City' as 'City', avg('Avg_CompliancePercentage') as 'Avg_CompliancePercentage';
-- FacingsCompliance% Calculation
fcs = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
fcs = filter fcs by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs = group fcs by ('AssessmentTask_Visit_RetailStore_Location_Address_City', 'AssessmentTask_Visit_RetailStore_Name');
fcs = foreach fcs generate 'AssessmentTask_Visit_RetailStore_Location_Address_City' as 'City', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
fcs = group fcs by 'City';
fcs = foreach fcs generate 'City', avg('Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
--PromotionCompliance
promo = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
promo = filter promo by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Promotion";
promo = group promo by ('AssessmentTask_Visit_RetailStore_Location_Address_City', 'AssessmentTask_Visit_RetailStore_Name');
promo = foreach promo generate 'AssessmentTask_Visit_RetailStore_Location_Address_City' as 'City', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_PromoCompliance_Percentage';
promo = group promo by 'City';
promo = foreach promo generate 'City', avg('Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage';

--Average Inventory Compliance
inv = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
inv = filter inv by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Inventory";
inv_result = group inv by ('AssessmentTask_Visit_RetailStore_Location_Address_City', 'AssessmentTask_Visit_RetailStore_Name');
inv_result = foreach inv_result generate 'AssessmentTask_Visit_RetailStore_Location_Address_City' as 'City', 'AssessmentTask_Visit_RetailStore_Name', avg('Compliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
inv_result = group inv_result by 'City';
inv_result = foreach inv_result generate 'City', avg('Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';

--final multidataset table 
final_table = cogroup order1 by 'City' full, sos by  'City' full, oos_result by  'City' full, comp_result by 'City' full,fcs by 'City' full, promo by 'City' full, inv_result by 'City';
final_table = foreach final_table generate coalesce(order1.'City',oos_result.'City',comp_result.'City',fcs.'City',sos.'City',promo.'City', inv_result.'City') as 'City', sum(order1.'avg_sales_velocity') as 'avg_sales_velocity',sum(order1.'total_sales') as 'total_sales', sum(order1.'total_quantity') as 'total_quantity', sum(oos_result.'OutOfStock_Count') as 'OutOfStock_Count', avg(sos.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage', avg(comp_result.'Avg_CompliancePercentage') as 'Avg_CompliancePercentage',avg(fcs.'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage', avg(promo.'Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage', avg(inv_result.'Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
final_table1 = group final_table by 'City';
final_table1 = filter final_table1 by {{cell(picklist_1_1.selection,0,"Api_name").asObject()}} is not null;
final_table1 = foreach final_table1 generate 'City' as 'AssessmentTask_Visit_RetailStore_Location_Address_City', {{cell(picklist_1_1.selection,0,"Field_value").asObject()}}  as '{{cell(picklist_1_1.selection,0,"Api_name").asObject()}}';
