Store_PercentChange_1.query:
--Store Details
store = load "${App.Datasets.CG_Visit36_tp.FullyQualifiedName}";
store = group store by 'RetailStore_Name';
store = foreach store generate 'RetailStore_Name' as 'Store_Name';

---Decline in Compliance (CG_RetailVisitKpi)
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp_A = filter comp by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
comp_B = filter comp by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
total_comp = group comp_A by ('AssessmentTask_Visit_RetailStore_Name') full, comp_B by ('AssessmentTask_Visit_RetailStore_Name');
total_comp = foreach total_comp generate coalesce(comp_A.'AssessmentTask_Visit_RetailStore_Name', comp_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(comp_A.'Compliance_Percentage') as 'current_comp', avg(comp_B.'Compliance_Percentage') as 'previous_comp';
total_comp = group total_comp by 'Store_Name';
total_comp = foreach total_comp generate 'Store_Name', avg('current_comp') as 'current_comp', avg('previous_comp') as 'previous_comp';
total_comp = foreach total_comp generate 'Store_Name','current_comp', 'previous_comp', current_comp-previous_comp as 'Decline', (current_comp-previous_comp)/previous_comp as 'Decline_percent';

---Decline in OutofStock (CG_RetailVisitKpi)
oos_A = filter comp_A by 'ActualBooleanValue' == "True" and 'Type_Computed' == "OutOfStock";
oos_B = filter comp_B by 'ActualBooleanValue' == "True" and 'Type_Computed' == "OutOfStock";
total_oos = group oos_A by ('AssessmentTask_Visit_RetailStore_Name') full, oos_B by ('AssessmentTask_Visit_RetailStore_Name');
total_oos = foreach total_oos generate coalesce(oos_A.'AssessmentTask_Visit_RetailStore_Name', oos_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', count(oos_A) as 'current_oos', count(oos_B) as 'previous_oos', count(oos_A) - count(oos_B) as 'Decline', (count(oos_A) - count(oos_B)) / count(oos_B) as 'Decline_percent';

-- Decline in Sales(CG_OrderItem)
sales = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
sales_A = filter sales by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
sales_B = filter sales by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
total_sale = group sales_A by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name') full, sales_B by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name');
total_sale = foreach total_sale generate coalesce(sales_A.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', sales_B.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', sum(sales_A.'TotalPrice') as 'current_sales', sum(sales_B.'TotalPrice') as 'previous_sales', sum(sales_A.'TotalPrice') - sum(sales_B.'TotalPrice') as 'Sales_Decline', (sum(sales_A.'TotalPrice') - sum(sales_B.'TotalPrice'))/sum(sales_B.'TotalPrice') as 'Sales_Decline_percent', sum(sales_A.'Quantity') as 'current_qty', sum(sales_B.'Quantity') as 'previous_qty', sum(sales_A.'Quantity') - sum(sales_B.'Quantity') as 'Quantity_Decline', (sum(sales_A.'Quantity') - sum(sales_B.'Quantity'))/sum(sales_B.'Quantity') as 'Quantity_Decline_percent';

---Decline in SalesVelocity
total_sv = group sales_A by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week') full, sales_B by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week');
total_sv = foreach total_sv generate coalesce(sales_A.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', sales_B.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', sales_A.'Order_ActivatedDate_Year'+"-"+ sales_A.'Order_ActivatedDate_Week' as 'CurrentPeriod_YearWeek', sales_B.'Order_ActivatedDate_Year'+"-"+ sales_B.'Order_ActivatedDate_Week' as 'PreviousPeriod_YearWeek', sum(sales_A.'TotalPrice') as 'current_sales', sum(sales_B.'TotalPrice') as 'previous_sales';
total_sv = group total_sv by 'Store_Name';
total_sv = foreach total_sv generate 'Store_Name', sum('current_sales') / unique('CurrentPeriod_YearWeek') as 'current_sv', sum('previous_sales') / unique('PreviousPeriod_YearWeek') as 'previous_sv', ((sum('current_sales') / unique('CurrentPeriod_YearWeek')) - (sum('previous_sales') / unique('PreviousPeriod_YearWeek'))) as 'SalesVelocity_Decline', ((sum('current_sales') / unique('CurrentPeriod_YearWeek')) - (sum('previous_sales') / unique('PreviousPeriod_YearWeek'))) / (sum('previous_sales') / unique('PreviousPeriod_YearWeek')) as 'SalesVelocity_Decline_percent';

---Decline in FacingCompliance (CG_RetailVisitKpi)
fcs_A = filter comp_A by 'Type_Computed' == "Facing";
fcs_B = filter comp_B by 'Type_Computed' == "Facing";
total_fcs = group fcs_A by ('AssessmentTask_Visit_RetailStore_Name') full, fcs_B by ('AssessmentTask_Visit_RetailStore_Name');
total_fcs = foreach total_fcs generate coalesce(fcs_A.'AssessmentTask_Visit_RetailStore_Name', fcs_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(fcs_A.'Compliance_Percentage') as 'current_fcs', avg(fcs_B.'Compliance_Percentage') as 'previous_fcs';
total_fcs = group total_fcs by 'Store_Name';
total_fcs = foreach total_fcs generate 'Store_Name', avg('current_fcs') as 'current_fcs', avg('previous_fcs') as 'previous_fcs';
total_fcs = foreach total_fcs generate 'Store_Name','current_fcs', 'previous_fcs', current_fcs-previous_fcs as 'Decline', (current_fcs-previous_fcs)/previous_fcs as 'Decline_percent';

---Decline in SoSCompliance (CG_RetailVisitKpi)
sos_A = filter comp_A by 'Type_Computed' == "ShareOfShelf";
sos_B = filter comp_B by 'Type_Computed' == "ShareOfShelf";
total_sos = group sos_A by ('AssessmentTask_Visit_RetailStore_Name') full, sos_B by ('AssessmentTask_Visit_RetailStore_Name');
total_sos = foreach total_sos generate coalesce(sos_A.'AssessmentTask_Visit_RetailStore_Name', sos_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(sos_A.'Compliance_Percentage') as 'current_sos', avg(sos_B.'Compliance_Percentage') as 'previous_sos';
total_sos = group total_sos by 'Store_Name';
total_sos = foreach total_sos generate 'Store_Name', avg('current_sos') as 'current_sos', avg('previous_sos') as 'previous_sos';
total_sos = foreach total_sos generate 'Store_Name','current_sos', 'previous_sos', current_sos-previous_sos as 'Decline', (current_sos-previous_sos)/previous_sos as 'Decline_percent';

---Decline in PromotionCompliance (CG_RetailVisitKpi)
promo_A = filter comp_A by 'Type_Computed' == "OutofStock";
promo_B = filter comp_B by 'Type_Computed' == "OutofStock";
total_promo = group promo_A by ('AssessmentTask_Visit_RetailStore_Name') full, promo_B by ('AssessmentTask_Visit_RetailStore_Name');
total_promo = foreach total_promo generate coalesce(promo_A.'AssessmentTask_Visit_RetailStore_Name', promo_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(promo_A.'Compliance_Percentage') as 'current_promo', avg(promo_B.'Compliance_Percentage') as 'previous_promo';
total_promo = group total_promo by 'Store_Name';
total_promo = foreach total_promo generate 'Store_Name', avg('current_promo') as 'current_promo', avg('previous_promo') as 'previous_promo';
total_promo = foreach total_promo generate 'Store_Name','current_promo', 'previous_promo', current_promo-previous_promo as 'Decline', (current_promo-previous_promo)/(previous_promo) as 'Decline_percent';

---Decline in InventoryCompliance (CG_RetailVisitKpi)
inventory_A = filter comp_A by 'Type_Computed' == "Inventory";
inventory_B = filter comp_B by 'Type_Computed' == "Inventory";
total_inventory = group inventory_A by ('AssessmentTask_Visit_RetailStore_Name') full, inventory_B by ('AssessmentTask_Visit_RetailStore_Name');
total_inventory = foreach total_inventory generate coalesce(inventory_A.'AssessmentTask_Visit_RetailStore_Name', inventory_B.'AssessmentTask_Visit_RetailStore_Name') as 'Store_Name', avg(inventory_A.'Compliance_Percentage') as 'current_inventory', avg(inventory_B.'Compliance_Percentage') as 'previous_inventory';
total_inventory = group total_inventory by 'Store_Name';
total_inventory = foreach total_inventory generate 'Store_Name', avg('current_inventory') as 'current_inventory', avg('previous_inventory') as 'previous_inventory';
total_inventory = foreach total_inventory generate 'Store_Name','current_inventory', 'previous_inventory', current_inventory-previous_inventory as 'Decline', (current_inventory - previous_inventory)/previous_inventory as 'Decline_percent';

--final multidataset table
final_table = cogroup store by 'Store_Name' full, total_sale by 'Store_Name' full, total_sv by 'Store_Name' full, total_comp by 'Store_Name' full, total_fcs by  'Store_Name' full, total_sos by 'Store_Name' full, total_oos by 'Store_Name' full,total_promo by 'Store_Name' full, total_inventory by 'Store_Name';

final_table = foreach final_table generate coalesce(store.'Store_Name', total_sale.'Store_Name', total_sv.'Store_Name', total_fcs.'Store_Name',total_comp.'Store_Name',total_sos.'Store_Name',total_oos.'Store_Name',total_promo.'Store_Name', total_inventory.'Store_Name') as 'Store_Name', 

sum(total_sale.'Sales_Decline') as 'Sales_Decline', sum(total_sale.'Sales_Decline_percent') as 'Sales_Decline_percent', sum(total_sale.'Quantity_Decline') as 'Quantity_Decline', sum(total_sale.'Quantity_Decline_percent') as 'Quantity_Decline_percent', sum(total_fcs.'Decline') as 'FCS_Decline', sum(total_fcs.'Decline_percent') as 'FCS_Decline_percent', avg(total_sv.'SalesVelocity_Decline') as 'SalesVelocity_Decline', avg(total_sv.'SalesVelocity_Decline_percent') as 'SalesVelocity_Decline_percent', avg(total_comp.'Decline') as 'CompliancePercent_Decline', avg(total_comp.'Decline_percent') as 'CompliancePercent_Decline_percent', avg(total_sos.'Decline') as 'SoS_Decline', avg(total_sos.'Decline_percent') as 'SoS_Decline_percent', sum(total_oos.'Decline') as 'OOS_Decline', sum(total_oos.'Decline_percent') as 'OOS_Decline_percent', avg(total_promo.'Decline') as 'Promo_Decline', avg(total_promo.'Decline_percent') as 'Promo_Decline_percent', avg(total_inventory.'Decline') as 'Inventory_Decline', avg(total_inventory.'Decline_percent') as 'Inventory_Decline_percent', avg(total_comp.'current_comp') as 'Avg_CompliancePercentage', sum(total_oos.'current_oos') as 'OutOfStock_Count', sum(total_sale.'current_sales') as 'total_sales', sum(total_sale.'current_qty') as 'total_quantity', sum(total_sv.'current_sv') as 'avg_sales_velocity', avg(total_fcs.'current_fcs') as 'Avg_FacingsCompliance_Percentage', avg(total_sos.'current_sos') as 'Avg_SoSCompliance_Percentage', avg(total_promo.'current_promo') as 'Avg_PromoCompliance_Percentage', avg(total_inventory.'current_inventory') as 'Avg_InventoryCompliance_Percentage';

final_table1 = group final_table by 'Store_Name';

final_table1 = foreach final_table1 generate 'Store_Name' as 'RetailStore_Name', {{cell(picklist_1_1.selection,0,"Field_value").asObject()}}  as '{{cell(picklist_1_1.selection,0,"Api_name").asObject()}}', {{row(picklist_1_1.selection,[0],["Field_value_decline_percent","Alias_decline_percent"]).asProjection()}};

final_table1 = filter final_table1 by '{{cell(picklist_1_1.selection,0,"Api_name").asObject()}}' is not null;
