DeadOutlets_query_1.query:
---Stores_with_orders_inLast3months---
orders_A = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
orders_A = filter orders_A by date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day') in ["3 months ago".."current day"];
orders_A = group orders_A by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Id', 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name');
orders_A = foreach orders_A generate 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Id', 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', unique('OrderId') as 'Number of Orders';
---EachStores_CreatedDt_LastOrderDt---
orders_B = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
orders_B = foreach orders_B generate 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Id', 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', toDate(substr('Order_ActivatedDate', 1, 10),"yyyy-MM-dd") as 'Order_ActivatedDate';
store = load "${App.Datasets.CG_RetailStore20_tp.FullyQualifiedName}";
store = filter store by date('CreatedDate_Year', 'CreatedDate_Month', 'CreatedDate_Day') in [.."3 months ago"];
store = foreach store generate toDate(substr('CreatedDate', 1, 10),"yyyy-MM-dd") as 'CreatedDate', 'Name', 'Id', 0 as 'Number of Orders';
---multidataset---
result = cogroup store by ('Id', 'Name') full, orders_B by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Id', 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name') full, orders_A by ('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Id', 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name');
result = foreach result generate store.'Id' as 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Id', store.'Name' as 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', max(orders_B.'Order_ActivatedDate') as 'Order_ActivatedDate', first(store.'CreatedDate') as 'CreatedDate', coalesce(sum(orders_A.'Number of Orders'),0) as 'Number_of_Orders_A', sum(store.'Number of Orders') as 'Number_of_Orders_B';
result = foreach result generate 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Id', 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', 'Order_ActivatedDate', 'CreatedDate', ('Number_of_Orders_A'+'Number_of_Orders_B') as 'Number of Orders', now() as 'today';
result = filter result by 'Number of Orders' == 0;
result = foreach result generate 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', 'Order_ActivatedDate', daysBetween('CreatedDate', 'today') as 'Days Since Store Creation', daysBetween('Order_ActivatedDate', 'today') as 'Days Since Last Order';
result = foreach result generate 'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name', (case when 'Order_ActivatedDate' is not null then 'Days Since Last Order' else 'Days Since Store Creation' end) as 'Number of Days';
result = order result by 'Number of Days' desc;

