sos = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = group sos by ('AssessmentTask_Visit_RetailStore_Name');
sos = foreach sos generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as Avg_SoSCompliance_Percentage;
-- Compliance% Calculation
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp_result = group comp by ('AssessmentTask_Visit_RetailStore_Name');
comp_result = foreach comp_result generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_Compliance_Percentage';
-- FacingsCompliance% Calculation
fcs = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
fcs = filter fcs by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs = group fcs by ('AssessmentTask_Visit_RetailStore_Name');
fcs = foreach fcs generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
--PromotionCompliance
promo = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
promo = filter promo by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Promotion";
promo = group promo by ('AssessmentTask_Visit_RetailStore_Name');
promo = foreach promo generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_PromoCompliance_Percentage';
--InventoryCompliance
inv = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
inv = filter inv by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Inventory";
inv = group inv by ('AssessmentTask_Visit_RetailStore_Name');
inv = foreach inv generate 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
--final multidataset table 
final_table = cogroup order1 by 'Store_Name' full, sos by 'Store_Name' full, oos_result by 'Store_Name' full, comp_result by 'Store_Name' full,fcs by 'Store_Name' full, promo by 'Store_Name' full, inv by 'Store_Name';
final_table = foreach final_table generate coalesce(order1.'Store_Name',oos_result.'Store_Name',comp_result.'Store_Name',fcs.'Store_Name',sos.'Store_Name', promo.'Store_Name', inv.'Store_Name') as 'Store_Name',
sum(order1.'avg_sales_velocity') as 'avg_sales_velocity',sum(order1.'total_sales') as 'total_sales',sum(order1.'total_quantity') as 'total_quantity',
sum(oos_result.'OutOfStock_Count') as 'OutOfStock_Count',avg(sos.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage',avg(comp_result.'Avg_Compliance_Percentage') as 'Avg_CompliancePercentage',avg(fcs.'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage', avg(promo.'Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage', avg(inv.'Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
final_table1 = group final_table by 'Store_Name';
final_table1 = filter final_table1 by {{cell(picklist_1_1.selection,0,"Api_name").asObject()}} is not null;
final_table1 = foreach final_table1 generate 'Store_Name' as 'AssessmentTask_Visit_RetailStore_Name', {{cell(picklist_1_1.selection,0,"Field_value").asObject()}}  as '{{cell(picklist_1_1.selection,0,"Api_name").asObject()}}';
final_table1 = order final_table1 by {{cell(picklist_1_1.selection,0,"Api_name").asObject()}} desc;
