AvgWeeklyRevenue_Gau_1.query:
total_weekrev = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
total_weekrev_A = filter total_weekrev by 'Product_Name' == "{{cell(lens_3_2.selection, 0, "Product_Name_Custom").asString()}}";
total_weekrev_A = group total_weekrev_A by 'Product_Name';
total_weekrev_A = foreach total_weekrev_A generate 'Product_Name' as 'Product', (sum('TotalPrice')/unique('Order_ActivatedDate_Week')) as 'avg_weekly_revenue', "select" as 'text', "A" as 'order';
weekly_rev = group total_weekrev by 'Product_Name';
weekly_rev = foreach weekly_rev generate 'Product_Name', (sum('TotalPrice')/unique('Order_ActivatedDate_Week')) as 'avg_weekly_revenue';
total_weekrev_B = foreach weekly_rev generate 'Product_Name' as 'Product', 'avg_weekly_revenue' as 'max_avg_weekly_revenue', "max" as 'text', "B" as 'order';
total_weekrev_B = order total_weekrev_B by 'max_avg_weekly_revenue' desc;
total_weekrev_B = limit total_weekrev_B 1;
total_weekrev_C = foreach weekly_rev generate 'Product_Name' as 'Product', 'avg_weekly_revenue' as 'min_avg_weekly_revenue', "min" as 'text', "C" as 'order';
total_weekrev_C = order total_weekrev_C by 'min_avg_weekly_revenue' asc;
total_weekrev_C = limit total_weekrev_C 1;
total_weekrev_D = group weekly_rev by 'Product_Name';
total_weekrev_D = foreach total_weekrev_D generate 'Product_Name' as 'Product', avg(sum('avg_weekly_revenue')) over([..] partition by all) as 'avg_avg_weekly_revenue', "avg" as 'text', "D" as 'order';
total_weekrev_D = limit total_weekrev_D 1;
weekrev = cogroup total_weekrev_A by ('Product', 'order') full, total_weekrev_B by ('Product', 'order') full, total_weekrev_C by ('Product', 'order') full, total_weekrev_D by ('Product', 'order');
weekrev = foreach weekrev generate coalesce(total_weekrev_A.'Product', total_weekrev_B.'Product', total_weekrev_C.'Product', total_weekrev_D.'Product') as 'Product', coalesce(total_weekrev_A.'order', total_weekrev_B.'order', total_weekrev_C.'order', total_weekrev_D.'order') as 'order', sum(total_weekrev_A.'avg_weekly_revenue') as 'avg_weekly_revenue', coalesce(sum(total_weekrev_A.'avg_weekly_revenue'),0) as 'avg_weekly_revenue_coalesced', coalesce(sum(total_weekrev_B.'max_avg_weekly_revenue'),0) as 'max_avg_weekly_revenue', coalesce(sum(total_weekrev_C.'min_avg_weekly_revenue'),0) as 'min_avg_weekly_revenue', coalesce(sum(total_weekrev_D.'avg_avg_weekly_revenue'),0) as 'avg_avg_weekly_revenue';
weekrev = group weekrev by ('Product', 'order');
weekrev = foreach weekrev generate 'Product', 'order', sum('avg_weekly_revenue') as 'avg_weekly_revenue', sum('max_avg_weekly_revenue') as 'max_avg_weekly_revenue', sum('avg_avg_weekly_revenue') as 'avg_avg_weekly_revenue', sum('min_avg_weekly_revenue') as 'min_avg_weekly_revenue', (case when sum(sum('max_avg_weekly_revenue')) over([..] partition by all) == sum(sum('min_avg_weekly_revenue')) over([..] partition by all) then  0 else sum('min_avg_weekly_revenue') end) as 'min_range', (case when sum(sum('avg_weekly_revenue_coalesced')) over([..] partition by all) >= sum(sum('avg_avg_weekly_revenue')) over([..] partition by all) then "#00B531" else "#B50E03" end) as 'color';
weekrev = foreach weekrev generate 'Product', 'order', 'avg_weekly_revenue', 'max_avg_weekly_revenue', number_to_string('max_avg_weekly_revenue', "$#,####0.00") as 'max_avg_weekly_revenue_str', 'min_avg_weekly_revenue', number_to_string('min_avg_weekly_revenue', "$#,###0.00") as 'min_avg_weekly_revenue_str', 'avg_avg_weekly_revenue', number_to_string('avg_avg_weekly_revenue', "$#,####0.00") as 'avg_avg_weekly_revenue_str', 'min_range', 'color';
weekrev = order weekrev by 'order';

MasterTable_5.query:
-- Compliance% Calculation
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Assortment_AssortmentProduct_Assortment_Name'=="{{cell(Assortment_lens_1_1.selection, 0, "Assortment_AssortmentProduct_Assortment_Name").asString()}}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp_result = group comp by ('Assortment_AssortmentProduct_Assortment_Name','Product_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
comp_result = foreach comp_result generate 'Assortment_AssortmentProduct_Assortment_Name','Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_Compliance_Percentage';
comp_result = group comp_result by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom');
comp_result = foreach comp_result generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', avg('Avg_Compliance_Percentage') as 'Avg_CompliancePercentage';
-- Order Details
order1 = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
order1 = filter order1 by 'Assortment_AssortmentProduct_Assortment_Name'=="{{cell(Assortment_lens_1_1.selection, 0, "Assortment_AssortmentProduct_Assortment_Name").asString()}}";
order1 = group order1 by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name','OrderId','Order_ActivatedDate_Year','Order_ActivatedDate_Week');
order1 = foreach order1 generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name','OrderId', 'Order_ActivatedDate_Year'+"-"+'Order_ActivatedDate_Week' as 'YearWeek', sum('TotalPrice') as 'Sales',sum('Quantity') as 'Quantity';
order1 = group order1 by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name');
order1 = foreach order1 generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name', (sum('Sales')/unique('OrderId')) as 'avg_order_value', (sum('Sales')/unique('YearWeek')) as 'avg_weekly_revenue', sum('Sales') as 'Sales', sum('Quantity') as 'Quantity';
-- Store Coverage
store_cv = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
store_cv = filter store_cv by 'Assortment_AssortmentProduct_Assortment_Name'=="{{cell(Assortment_lens_1_1.selection, 0, "Assortment_AssortmentProduct_Assortment_Name").asString()}}";
store_cv = group store_cv by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name');
store_cv = foreach store_cv generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name', (unique('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name')/{{cell(Total_Stores__1_1.result, 0, "total_stores").asObject()}}) as 'store_coverage';
-- Share of Shelf
sos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
sos = filter sos by 'Assortment_AssortmentProduct_Assortment_Name'=="{{cell(Assortment_lens_1_1.selection, 0, "Assortment_AssortmentProduct_Assortment_Name").asString()}}";
sos = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = group sos by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
sos = foreach sos generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_SoSCompliance_Percentage';
sos = group sos by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom');
sos = foreach sos generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', avg('Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage';
--OutOfStockCompliance
oos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
oos = filter oos by 'Assortment_AssortmentProduct_Assortment_Name'=="{{cell(Assortment_lens_1_1.selection, 0, "Assortment_AssortmentProduct_Assortment_Name").asString()}}";
oos = filter oos by 'Include_In_Compliance'== "True" and 'Type_Computed' == "OutOfStock";
oos_result = group oos by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
oos_result = foreach oos_result generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_OutOfStock_Compliance';
oos_result = group oos_result by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom');
oos_result = foreach oos_result generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', avg('Avg_OutOfStock_Compliance') as 'Avg_OutOfStock_Compliance';
--FacingsCompliance% Calculation
fcs = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
fcs = filter fcs by 'Assortment_AssortmentProduct_Assortment_Name'=="{{cell(Assortment_lens_1_1.selection, 0, "Assortment_AssortmentProduct_Assortment_Name").asString()}}";
fcs = filter fcs by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs = group fcs by  ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
fcs = foreach fcs generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
fcs = group fcs by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom');
fcs = foreach fcs generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', avg('Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
--PromotionCompliance
promo = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
promo = filter promo by 'Assortment_AssortmentProduct_Assortment_Name'=="{{cell(Assortment_lens_1_1.selection, 0, "Assortment_AssortmentProduct_Assortment_Name").asString()}}";
promo = filter promo by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Promotion";
promo = group promo by ('Assortment_AssortmentProduct_Assortment_Name','Product_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
promo = foreach promo generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_PromoCompliance_Percentage';
promo = group promo by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom');
promo = foreach promo generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', avg('Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage';
--InventoryCompliance
inv = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
inv = filter inv by 'Assortment_AssortmentProduct_Assortment_Name'=="{{cell(Assortment_lens_1_1.selection, 0, "Assortment_AssortmentProduct_Assortment_Name").asString()}}";
inv = filter inv by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Inventory";
inv = group inv by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
inv = foreach inv generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
inv = group inv by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom');
inv = foreach inv generate 'Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom', avg('Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
--final multidataset table 
final_table = cogroup order1 by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name') full, sos by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom') full, oos_result by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom') full, comp_result by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom') full,fcs by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom') full, promo by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom') full, inv by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name_Custom') full, store_cv by ('Assortment_AssortmentProduct_Assortment_Name', 'Product_Name');
final_table = foreach final_table generate coalesce(order1.'Assortment_AssortmentProduct_Assortment_Name', comp_result.'Assortment_AssortmentProduct_Assortment_Name', sos.'Assortment_AssortmentProduct_Assortment_Name',oos_result.'Assortment_AssortmentProduct_Assortment_Name', fcs.'Assortment_AssortmentProduct_Assortment_Name', promo.'Assortment_AssortmentProduct_Assortment_Name', inv.'Assortment_AssortmentProduct_Assortment_Name', store_cv.'Assortment_AssortmentProduct_Assortment_Name') as 'Assortment_AssortmentProduct_Assortment_Name', 

coalesce(order1.'Product_Name', comp_result.'Product_Name_Custom', sos.'Product_Name_Custom',oos_result.'Product_Name_Custom', fcs.'Product_Name_Custom', promo.'Product_Name_Custom', inv.'Product_Name_Custom', store_cv.'Product_Name') as 'Product', 

sum(order1.'avg_order_value') as 'avg_order_value', sum(order1.'avg_weekly_revenue') as 'avg_weekly_revenue',sum(order1.'Sales') as 'total_sales', sum(order1.'Quantity') as 'total_qty', avg(oos_result.'Avg_OutOfStock_Compliance') as 'Avg_OutOfStock_Compliance', avg(sos.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage', avg(comp_result.'Avg_CompliancePercentage') as 'Avg_CompliancePercentage', avg(fcs.'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage', avg(promo.'Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage', avg(inv.'Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage', sum(store_cv.'store_coverage') as 'store_coverage';

