promo_B = limit promo_B 1;
promo_C = foreach promotions generate 'Product_Name_Custom' as 'Product', avg('current_promo') as 'min_Avg_PromoCompliance_Percentage', "min" as 'text', "C" as 'order';
promo_C = order promo_C by 'min_Avg_PromoCompliance_Percentage' asc;
promo_C = limit promo_C 1;
promo_D = foreach promotions generate 'Product_Name_Custom' as 'Product', avg(avg('current_promo')) over([..] partition by all) as 'avg_Avg_PromoCompliance_Percentage', "avg" as 'text', "D" as 'order';
