final_table = foreach final_table generate coalesce(retail_store.'CreatedDate_Year~~~CreatedDate_Week', orders.'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week', oos.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', total_comp.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', promo.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', total_fcs.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', total_sos.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', total_inv.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week') as 'CreatedDate_Year~~~CreatedDate_Week',

sum(orders.'current_sales') as 'total_sales',
sum(orders.'current_quantity') as 'total_qty', 
sum(orders.'current_avg_order_value') as 'avg_order_value', 
sum(orders.'current_avg_week_revenue') as 'avg_weekly_revenue', 
sum(oos.'current_out_of_stock') as 'Avg_OutOfStock_Compliance',
sum(total_comp.'current_comp') as 'Avg_CompliancePercentage', 
sum(promo.'current_promo') as 'Avg_PromoCompliance_Percentage', 
sum(total_fcs.'current_fcs') as 'Avg_FacingsCompliance_Percentage', 
sum(total_sos.'current_sos') as 'Avg_SoSCompliance_Percentage', 
sum(total_inv.'current_inventory') as 'Avg_InventoryCompliance_Percentage', 
sum(orders.'current_store_coverage') as 'store_coverage';
---Promotions---
q = load "${App.Datasets.CG_PromotionChannel36_tp.FullyQualifiedName}";
q =  filter q by date('StartDate_Year', 'StartDate_Month', 'StartDate_Day') in [.."current day"] and date('EndDate_Computed_Year', 'EndDate_Computed_Month', 'EndDate_Computed_Day') in ["180 days ago"..];
q = filter q by 'PromotionId' in {{coalesce(column(PromoChannel_Combine_3.result, ["PromotionId"]),toArray("NA")).asString()}};
q1 = group q by ('StartDate', 'StartDate_Year', 'StartDate_Quarter', 'StartDate_Month', 'StartDate_Week', 'StartDate_Day', 'Promotion_Name', 'Id');
q1 = foreach q1 generate 'Id', 'Promotion_Name', 'StartDate_Year' as 'Year','StartDate_Quarter' as 'Quarter', 'StartDate_Week' as 'Week', 'StartDate_Month' as 'Month', 'StartDate_Day' as 'Day', count(q) as 'A', date_to_epoch(toDate('StartDate' + " 00:00:00")) as Start, string_to_number(StartDate_Year+StartDate_Week) as 'StartYearWeek';
q2 = group q by ('EndDate_Computed', 'EndDate_Computed_Year', 'EndDate_Computed_Quarter', 'EndDate_Computed_Month', 'EndDate_Computed_Week', 'EndDate_Computed_Day', 'Promotion_Name', 'Id');
q2 = foreach q2 generate 'Id', 'Promotion_Name','EndDate_Computed_Year' as 'Year','EndDate_Computed_Quarter' as 'Quarter', 'EndDate_Computed_Month' as 'Month', 'EndDate_Computed_Week' as 'Week', 'EndDate_Computed_Day' as 'Day', count() as 'A', date_to_epoch(toDate('EndDate_Computed' + " 00:00:00")) as End, string_to_number(EndDate_Computed_Year+EndDate_Computed_Week) as 'EndYearWeek';
q = union q1,q2;
q = group q by ('Id', 'Promotion_Name', 'Year','Quarter', 'Month', 'Week', 'Day');
q = foreach q generate 'Id'+"_"+'Promotion_Name' as 'Key','Id', 'Promotion_Name', 'Year','Quarter', 'Month', 'Week', 'Day',sum('A') as 'A', sum(Start) as Start, sum(End) as End, sum('StartYearWeek') as 'StartYearWeek', sum('EndYearWeek') as 'EndYearWeek';
q = fill q by (dateCols=('Year','Week', "Y-W"), partition = 'Key');
q = group q by ('Key', 'Year', 'Week');
q = foreach q generate substr('Key',index_of('Key',"_")+1) as 'Promotion_Name_New',  'Year', 'Week', coalesce(sum(A) , sum(sum('A')) over([..-1] partition by ('Key') order by ('Year', 'Week'))) as 'Value', toString(toDate(max(sum('Start')) over ([..] partition by 'Key')), "yyyy-MM-dd") as Start, toString(toDate(max(sum('End')) over ([..] partition by 'Key')), "yyyy-MM-dd") as End, string_to_number('Year'+'Week') as 'YearWeek_filter',

max(sum('StartYearWeek')) over ([..] partition by 'Key') as 'StartYearWeek', max(sum('EndYearWeek')) over ([..] partition by 'Key') as 'EndYearWeek', case when string_to_number('Year'+'Week') <= max(sum('EndYearWeek')) over ([..] partition by 'Key') and string_to_number('Year'+'Week') >= max(sum('StartYearWeek')) over ([..] partition by 'Key') then 1 else 0 end as 'Promotion_Period_Filter_Flag';
q = filter q by 'Promotion_Period_Filter_Flag' == 1;
q = filter q by 'YearWeek_filter' >= string_to_number("{{cell(date_parser_2.result, 0, "180days_prior_yrweek").asString()}}") and 'YearWeek_filter' <= string_to_number("{{cell(date_parser_2.result, 0, "yrweek_now").asString()}}");
q = filter q by 'Promotion_Name_New'=="{{cell(Promotion_Final_3.selection, 0, "Promotion Name").asString()}}";
q = foreach q generate 'Promotion_Name_New', 'Year', 'Week', 'Value', 'Start' as 'Start', 'End' as 'End';
q = fill q by (dateCols=('Year','Week', "Y-W"));
q = group q by ('Promotion_Name_New', 'Year', 'Week', 'Start', 'End');
q = foreach q generate 'Promotion_Name_New' as 'Promotion Name', 'Year' + "~~~" + 'Week' as 'Year~~~Week', sum('Value') as 'Value', 'Start' as 'Start', 'End' as 'End';
---result_multidataset---
result = cogroup final_table by ('CreatedDate_Year~~~CreatedDate_Week') full, q by ('Year~~~Week');
result = foreach result generate coalesce(final_table.'CreatedDate_Year~~~CreatedDate_Week', q.'Year~~~Week') as 'CreatedDate_Year~~~CreatedDate_Week', first(q.'Promotion Name') as 'Promotion Name', first(q.'Start') as 'Start', first(q.'End') as 'End',

sum(final_table.'total_sales') as 'total_sales',
sum(final_table.'total_qty') as 'total_qty', 
sum(final_table.'avg_order_value') as 'avg_order_value', 
sum(final_table.'avg_weekly_revenue') as 'avg_weekly_revenue', 
sum(final_table.'Avg_OutOfStock_Compliance') as 'Avg_OutOfStock_Compliance',
sum(final_table.'Avg_CompliancePercentage') as 'Avg_CompliancePercentage', 
sum(final_table.'Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage', 
sum(final_table.'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage', 
sum(final_table.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage', 
sum(final_table.'Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage', 
sum(final_table.'store_coverage') as 'store_coverage', min(q.'Value') as 'Value';
result = filter result by '{{cell(static_10.selection,0,"Field_Alias_x").asObject()}}' is not null or 'Value' is not null;
result = foreach result generate 'CreatedDate_Year~~~CreatedDate_Week', 'Promotion Name', 'Start' as 'Start Date', 'End' as 'End Date', coalesce('{{cell(static_10.selection,0,"Field_Alias_x").asObject()}}',0) as '{{cell(static_10.selection,0,"Field_Alias_x").asObject()}}', coalesce('Value',0) as 'Promotion', case when 'Value'==1 then "Promotion is running" else "Promotion has ended" end as 'Promotion Status';
result = order result by 'CreatedDate_Year~~~CreatedDate_Week' asc;

