store_orders = foreach store_orders generate coalesce(store_orders_A.'Assortment_AssortmentProduct_Assortment_Name', store_orders_B.'Assortment_AssortmentProduct_Assortment_Name') as 'Assortment_AssortmentProduct_Assortment_Name', (unique(store_orders_A.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name')/{{cell(Total_Stores__1_1.result, 0, "total_stores").asObject()}}) as 'current_store_coverage', (unique(store_orders_B.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name')/{{cell(Total_Stores__1_1.result, 0, "total_stores").asObject()}}) as 'previous_store_coverage';
store_orders = foreach store_orders generate 'Assortment_AssortmentProduct_Assortment_Name', 'current_store_coverage', 'previous_store_coverage', (('current_store_coverage'-'previous_store_coverage')/('previous_store_coverage')) as 'Percent_Change_in_StoreCoverage';
store_orders = foreach store_orders generate 'Assortment_AssortmentProduct_Assortment_Name', 'current_store_coverage', 'Percent_Change_in_StoreCoverage';
