              total_inv by ('Assortment_AssortmentProduct_Assortment_Name') full,
              store_orders by ('Assortment_AssortmentProduct_Assortment_Name');
final_table = foreach final_table generate
        coalesce(total_sale.'Assortment_AssortmentProduct_Assortment_Name', total_qty.'Assortment_AssortmentProduct_Assortment_Name', total_ordval.'Assortment_AssortmentProduct_Assortment_Name', total_sv.'Assortment_AssortmentProduct_Assortment_Name', total_oos.'Assortment_AssortmentProduct_Assortment_Name', total_comp.'Assortment_AssortmentProduct_Assortment_Name', promo.'Assortment_AssortmentProduct_Assortment_Name', total_fcs.'Assortment_AssortmentProduct_Assortment_Name', total_sos.'Assortment_AssortmentProduct_Assortment_Name', total_inv.'Assortment_AssortmentProduct_Assortment_Name', store_orders.'Assortment_AssortmentProduct_Assortment_Name') as 'Assortment_AssortmentProduct_Assortment_Name', 
        
