new_launched_prod_py_1.query:
orders = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
--orders = filter orders by date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day') in ["180 days ago".."current day"];
orders = foreach orders generate 'Product2Id', 'Product_Name', toDate(substr('Order_ActivatedDate', 1, 10),"yyyy-MM-dd") as 'Order_ActivatedDate', 'TotalPrice',  'OrderId';
prod = load "${App.Datasets.CG_Product31_tp.FullyQualifiedName}";
prod = filter prod by date('CreatedDate_Year', 'CreatedDate_Month', 'CreatedDate_Day') in ["180 days ago".."current day"];
prod = foreach prod generate toDate(substr('CreatedDate', 1, 10),"yyyy-MM-dd") as 'CreatedDate', 'Name', 'Id';
---Revenue & #Orders----
final_A = cogroup prod by ('Id', 'Name') left, orders by ('Product2Id', 'Product_Name');
final_A = foreach final_A generate coalesce(prod.'Name', orders.'Product_Name') as 'Product_Name', sum(orders.'TotalPrice') as 'TotalPrice', unique(orders.'OrderId') as 'Number of Orders';
orders = group orders by ('Product2Id', 'Product_Name');
orders = foreach orders generate 'Product2Id', 'Product_Name', min('Order_ActivatedDate') as 'Order_ActivatedDate';
final = cogroup prod by ('Id','Name') left, orders by ('Product2Id','Product_Name');
---since launched---
final_B = foreach final generate coalesce(prod.'Name', orders.'Product_Name') as 'Product_Name', first(prod.'CreatedDate') as 'CreatedDate', first(orders.'Order_ActivatedDate') as 'Order_ActivatedDate';
final_B = foreach final_B generate 'Product_Name', abs(daysBetween('CreatedDate', 'Order_ActivatedDate')) as 'Days until first Order', 'Order_ActivatedDate';
---since first order---
final_C = foreach final generate coalesce(prod.'Name', orders.'Product_Name') as 'Product_Name', first(orders.'Order_ActivatedDate') as 'Order_ActivatedDate', now() as 'today';
final_C  = foreach final_C generate 'Product_Name', daysBetween('Order_ActivatedDate', 'today') as 'Days Since first order', 'Order_ActivatedDate';
---multidataset---
result = cogroup final_A by 'Product_Name' full, final_B by 'Product_Name' full, final_C by 'Product_Name';
result = foreach result generate coalesce(final_A.'Product_Name', final_B.'Product_Name', final_C.'Product_Name',"NA") as 'Product_Name', sum(final_A.'TotalPrice') as 'TotalPrice', sum(final_A.'Number of Orders') as 'Orders', sum(final_B.'Days until first Order') as 'Days Until First Order', sum(final_C.'Days Since first order') as 'Days After First Order', first(final_B.'Order_ActivatedDate') as 'Order_ActivatedDate', toDate(date_to_epoch(now())-(180*86400)) as '180days_prior_date';
result = filter result by 'Order_ActivatedDate'>='180days_prior_date';
result = order result by 'TotalPrice' desc;

Avg_OOS_compliance_g_1.query:
oos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
oos_A = filter oos by 'Product_Name_Custom' == "{{cell(lens_3_2.selection, 0, "Product_Name_Custom").asString()}}";
oos_A = filter oos_A by 'Include_In_Compliance' == "True" and 'Type_Computed' == "OutOfStock";
oos_A = group oos_A by ('Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
oos_A = foreach oos_A generate 'Product_Name_Custom', avg('Compliance_Percentage') as 'current_out_of_stock';
oos_A = group oos_A by 'Product_Name_Custom';
oos_A = foreach oos_A generate 'Product_Name_Custom' as 'Product', avg('current_out_of_stock') as 'Avg_OutOfStock_Compliance', "select" as 'text', "A" as 'order';
out_of_stock = filter oos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "OutOfStock";
out_of_stock = group out_of_stock by ('Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
out_of_stock = foreach out_of_stock generate 'Product_Name_Custom', avg('Compliance_Percentage') as 'current_out_of_stock';
out_of_stock = group out_of_stock by 'Product_Name_Custom';
oos_B = foreach out_of_stock generate 'Product_Name_Custom' as 'Product', avg('current_out_of_stock') as 'max_Avg_OutOfStock_Compliance', "max" as 'text', "B" as 'order';
oos_B = order oos_B by 'max_Avg_OutOfStock_Compliance' desc;
oos_B = limit oos_B 1;
oos_C = foreach out_of_stock generate 'Product_Name_Custom' as 'Product', avg('current_out_of_stock') as 'min_Avg_OutOfStock_Compliance', "min" as 'text', "C" as 'order';
oos_C = order oos_C by 'min_Avg_OutOfStock_Compliance' asc;
oos_C = limit oos_C 1;
oos_D = foreach out_of_stock generate 'Product_Name_Custom' as 'Product', avg(avg('current_out_of_stock')) over([..] partition by all) as 'avg_Avg_OutOfStock_Compliance', "avg" as 'text', "D" as 'order';
oos_D = limit oos_D 1;
total_oos = cogroup oos_A by ('Product', 'order') full, oos_B by ('Product', 'order') full, oos_C by ('Product', 'order') full, oos_D by ('Product', 'order');
total_oos = foreach total_oos generate coalesce(oos_A.'Product', oos_B.'Product', oos_C.'Product', oos_D.'Product') as 'Product', coalesce(oos_A.'order', oos_B.'order', oos_C.'order', oos_D.'order') as 'order', sum(oos_A.'Avg_OutOfStock_Compliance') as 'Avg_OutOfStock_Compliance', coalesce(sum(oos_A.'Avg_OutOfStock_Compliance'),0) as 'Avg_OutOfStock_Compliance_coalesced', coalesce(sum(oos_B.'max_Avg_OutOfStock_Compliance'), 0) as 'max_Avg_OutOfStock_Compliance', coalesce(sum(oos_C.'min_Avg_OutOfStock_Compliance'), 0) as 'min_Avg_OutOfStock_Compliance', coalesce(sum(oos_D.'avg_Avg_OutOfStock_Compliance'),0) as 'avg_Avg_OutOfStock_Compliance';

total_oos = group total_oos by ('Product', 'order');

total_oos = foreach total_oos generate 'Product', 'order', sum('Avg_OutOfStock_Compliance') as 'Avg_OutOfStock_Compliance', sum('max_Avg_OutOfStock_Compliance') as 'max_Avg_OutOfStock_Compliance', sum('avg_Avg_OutOfStock_Compliance') as 'avg_Avg_OutOfStock_Compliance', sum('min_Avg_OutOfStock_Compliance') as 'min_Avg_OutOfStock_Compliance', (case when sum(sum('max_Avg_OutOfStock_Compliance')) over([..] partition by all) == sum(sum('min_Avg_OutOfStock_Compliance')) over([..] partition by all) then  0 else sum('min_Avg_OutOfStock_Compliance') end) as 'min_range', (case when sum(sum('Avg_OutOfStock_Compliance_coalesced')) over([..] partition by all) >= sum(sum('avg_Avg_OutOfStock_Compliance')) over([..] partition by all) then "#00B531" else "#B50E03" end) as 'color';

total_oos = foreach total_oos generate 'Product', 'order', 'Avg_OutOfStock_Compliance', 'max_Avg_OutOfStock_Compliance', number_to_string('max_Avg_OutOfStock_Compliance', "##0.00") as 'max_Avg_OutOfStock_Compliance_str', 'min_Avg_OutOfStock_Compliance', number_to_string('min_Avg_OutOfStock_Compliance', "##0.00") as 'min_Avg_OutOfStock_Compliance_str', 'avg_Avg_OutOfStock_Compliance', number_to_string('avg_Avg_OutOfStock_Compliance', "##0.00") as 'avg_Avg_OutOfStock_Compliance_str', 'min_range', 'color';
total_oos = order total_oos by 'order';

Promotion_Final_1.query:
q = load "${App.Datasets.CG_PromotionChannel36_tp.FullyQualifiedName}";
q =  filter q by date('StartDate_Year', 'StartDate_Month', 'StartDate_Day') in [dateRange([1970,01,01], {{cell(date_parser_2.result, 0, "Date_Max").asString()}})] and date('EndDate_Computed_Year', 'EndDate_Computed_Month', 'EndDate_Computed_Day') in [dateRange({{cell(date_parser_2.result, 0, "Date_Min").asString()}}, [2100,12,31])];
q = filter q by 'PromotionId' in {{coalesce(column(PromoChannel_Combine_1.result, ["PromotionId"]),toArray("NA")).asString()}};
q1 = group q by ('StartDate', 'StartDate_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}', 'StartDate_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}', 'Promotion_Name', 'Id');
q1 = foreach q1 generate 'Id', 'Promotion_Name', 'StartDate_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}' as 'Year', 'StartDate_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}' as 'Week', count(q) as 'A', date_to_epoch(toDate('StartDate' + " 00:00:00")) as Start, string_to_number(StartDate_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}} + StartDate_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}) as 'StartYearWeek';
q2 = group q by ('EndDate_Computed', 'EndDate_Computed_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}', 'EndDate_Computed_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}', 'Promotion_Name', 'Id');
q2 = foreach q2 generate 'Id', 'Promotion_Name','EndDate_Computed_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}' as 'Year', 'EndDate_Computed_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}' as 'Week', count() as 'A', date_to_epoch(toDate('EndDate_Computed' + " 00:00:00")) as End, string_to_number(EndDate_Computed_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}} + EndDate_Computed_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}) as 'EndYearWeek';
q = union q1,q2;
