Store_Coverage_Gauge_1.query:
str_coverage = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
str_coverage_A = filter str_coverage by 'Product_Name' == "{{cell(lens_3_2.selection, 0, "Product_Name_Custom").asString()}}";
str_coverage_A = group str_coverage_A by 'Product_Name';
str_coverage_A = foreach str_coverage_A generate 'Product_Name' as 'Product', (unique('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name')/{{cell(Total_Stores__1_1.result, 0, "total_stores").asObject()}}) as 'store_coverage', "select" as 'text', "A" as 'order';
coverage = group str_coverage by 'Product_Name';
coverage = foreach coverage generate 'Product_Name', (unique('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name')/{{cell(Total_Stores__1_1.result, 0, "total_stores").asObject()}}) as 'store_coverage';
str_coverage_B = foreach coverage generate 'Product_Name' as 'Product', 'store_coverage' as 'max_store_coverage', "max" as 'text', "B" as 'order';
str_coverage_B = order str_coverage_B by 'max_store_coverage' desc;
str_coverage_B = limit str_coverage_B 1;
str_coverage_C = foreach coverage generate 'Product_Name' as 'Product', 'store_coverage' as 'min_store_coverage', "min" as 'text', "C" as 'order';
str_coverage_C = order str_coverage_C by 'min_store_coverage' asc;
str_coverage_C = limit str_coverage_C 1;
str_coverage_D = group coverage by 'Product_Name';
str_coverage_D = foreach str_coverage_D generate 'Product_Name' as 'Product', avg(sum('store_coverage')) over([..] partition by all) as 'avg_store_coverage', "avg" as 'text', "D" as 'order';
str_coverage_D = limit str_coverage_D 1;
total_coverage = cogroup str_coverage_A by ('Product', 'order') full, str_coverage_B by ('Product', 'order') full, str_coverage_C by ('Product', 'order') full, str_coverage_D by ('Product', 'order');
total_coverage = foreach total_coverage generate coalesce(str_coverage_A.'Product', str_coverage_B.'Product', str_coverage_C.'Product', str_coverage_D.'Product') as 'Product', coalesce(str_coverage_A.'order', str_coverage_B.'order', str_coverage_C.'order', str_coverage_D.'order') as 'order', sum(str_coverage_A.'store_coverage') as 'store_coverage', coalesce(sum(str_coverage_A.'store_coverage'),0) as 'store_coverage_coalesced', coalesce(sum(str_coverage_B.'max_store_coverage'),0) as 'max_store_coverage', coalesce(sum(str_coverage_C.'min_store_coverage'),0) as 'min_store_coverage', sum(str_coverage_D.'avg_store_coverage') as 'avg_store_coverage';
total_coverage = group total_coverage by ('Product', 'order');
total_coverage = foreach total_coverage generate 'Product', 'order', sum('store_coverage') as 'store_coverage', sum('max_store_coverage') as 'max_store_coverage', sum('avg_store_coverage') as 'avg_store_coverage', sum('min_store_coverage') as 'min_store_coverage', (case when sum(sum('max_store_coverage')) over([..] partition by all) == sum(sum('min_store_coverage')) over([..] partition by all) then  0 else sum('min_store_coverage') end) as 'min_range', (case when sum(sum('store_coverage_coalesced')) over([..] partition by all) >= sum(sum('avg_store_coverage')) over([..] partition by all) then "#00B531" else "#B50E03" end) as 'color';
total_coverage = foreach total_coverage generate 'Product', 'order', 'store_coverage', 'max_store_coverage', number_to_string(('max_store_coverage'*100), "##0.00") as 'max_store_coverage_str', 'min_store_coverage', number_to_string(('min_store_coverage'*100), "##0.00") as 'min_store_coverage_str', 'avg_store_coverage', number_to_string(('avg_store_coverage'*100), "##0.00") as 'avg_store_coverage_str', 'min_range', 'color';
total_coverage = order total_coverage by 'order';

