---Promotions---
q = load "${App.Datasets.CG_PromotionChannel36_tp.FullyQualifiedName}";
q =  filter q by date('StartDate_Year', 'StartDate_Month', 'StartDate_Day') in [dateRange([1970,01,01], {{cell(date_parser_2.result, 0, "Date_Max").asString()}})] and date('EndDate_Computed_Year', 'EndDate_Computed_Month', 'EndDate_Computed_Day') in [dateRange({{cell(date_parser_2.result, 0, "Date_Min").asString()}}, [2100,12,31])];
q = filter q by 'PromotionId' in {{coalesce(column(PromoChannel_Combine_1.result, ["PromotionId"]),toArray("NA")).asString()}};
q1 = group q by ('StartDate', 'StartDate_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}', 'StartDate_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}', 'Promotion_Name', 'Id');
q1 = foreach q1 generate 'Id', 'Promotion_Name', 'StartDate_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}' as 'Year', 'StartDate_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}' as 'Week', count(q) as 'A', date_to_epoch(toDate('StartDate' + " 00:00:00")) as Start, string_to_number(StartDate_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}} + StartDate_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}) as 'StartYearWeek';
q2 = group q by ('EndDate_Computed', 'EndDate_Computed_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}', 'EndDate_Computed_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}', 'Promotion_Name', 'Id');
q2 = foreach q2 generate 'Id', 'Promotion_Name','EndDate_Computed_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}' as 'Year', 'EndDate_Computed_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}' as 'Week', count() as 'A', date_to_epoch(toDate('EndDate_Computed' + " 00:00:00")) as End, string_to_number(EndDate_Computed_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}} + EndDate_Computed_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}) as 'EndYearWeek';
q = union q1,q2;
q = group q by ('Id', 'Promotion_Name', 'Year', 'Week');
q = foreach q generate 'Id'+"_"+'Promotion_Name' as 'Key','Id', 'Promotion_Name', 'Year', 'Week', sum('A') as 'A', sum(Start) as Start, sum(End) as End, sum('StartYearWeek') as 'StartYearWeek', sum('EndYearWeek') as 'EndYearWeek';
q = fill q by (dateCols=('Year','Week', "Y-W"), partition = 'Key');
q = group q by ('Key', 'Year', 'Week');
q = foreach q generate substr('Key',index_of('Key',"_")+1) as 'Promotion_Name_New', 'Year', 'Week', 'Year' + "~~~" + 'Week' as 'Year~~~Week', coalesce(sum(A) , sum(sum('A')) over([..-1] partition by ('Key') order by ('Year', 'Week')),0) as 'Value', toString(toDate(max(sum('Start')) over ([..] partition by 'Key')), "yyyy-MM-dd") as Start, toString(toDate(max(sum('End')) over ([..] partition by 'Key')), "yyyy-MM-dd") as End, string_to_number('Year'+'Week') as 'YearWeek_filter',

max(sum('StartYearWeek')) over ([..] partition by 'Key') as 'StartYearWeek', max(sum('EndYearWeek')) over ([..] partition by 'Key') as 'EndYearWeek', case when string_to_number('Year'+'Week') <= max(sum('EndYearWeek')) over ([..] partition by 'Key') and string_to_number('Year'+'Week') >= max(sum('StartYearWeek')) over ([..] partition by 'Key') then 1 else 0 end as 'Promotion_Period_Filter_Flag';
q = filter q by 'Promotion_Period_Filter_Flag' == 1;
q = filter q by 'YearWeek_filter' >= string_to_number("{{cell(date_parser_2.result, 0, "min_YearWeek").asString()}}") and 'YearWeek_filter' <= string_to_number("{{cell(date_parser_2.result, 0, "max_YearWeek").asString()}}");
q = foreach q generate 'Promotion_Name_New', 'Year', 'Week', 'Value', 'Start' as 'Start', 'End' as 'End';
q = fill q by (dateCols=('Year','Week', "Y-W"));
q = group q by ('Promotion_Name_New', 'Year', 'Week', 'Start', 'End');
q = foreach q generate 'Promotion_Name_New', 'Year' + "~~~" + 'Week' as 'Year~~~Week', sum('Value') as 'Value', 'Start' as 'Start', 'End' as 'End';
q = filter q by 'Promotion_Name_New'=="{{cell(Promotion_Final_1.selection, 0, "Promotion Name").asString()}}";
q = foreach q generate 'Promotion_Name_New' as 'Promotion Name', 'Year~~~Week', 'Value', 'Start' as 'Start', 'End' as 'End';

---result_multidataset---
