sum(final_table.'total_sales') as 'total_sales', sum(final_table.'total_qty') as 'total_qty', sum(final_table.'avg_order_value') as 'avg_order_value', sum(final_table.'avg_weekly_revenue') as 'avg_weekly_revenue', min(q.'Value') as 'Value';
result = filter result by '{{cell(static_4.selection,0,"Api_name_y").asObject()}}' is not null or 'Value' is not null;
result = foreach result generate 'CreatedDate_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}~~~CreatedDate_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}', 'Promotion Name', 'Start' as 'Start Date', 'End' as 'End Date', coalesce('{{cell(static_4.selection,0,"Api_name_y").asObject()}}',0) as '{{cell(static_4.selection,0,"Api_name_y").asObject()}}', coalesce('Value',0) as 'Promotion', case when 'Value'==1 then "Promotion is running" else "Promotion has ended" end as 'Promotion Status';
result = order result by 'CreatedDate_Year{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}~~~CreatedDate_Week{{cell(date_parser_2.result, 0, "Is_Fiscal").asString()}}' asc;

