final_table = group final_table by ('ProductCategory_Name_Custom','row_count','rank_desc');
final_table = foreach final_table generate 'ProductCategory_Name_Custom', coalesce({{cell(static_4.selection,0,"Field_value_y").asObject()}},0)  as '{{cell(static_4.selection,0,"Api_name_y").asObject()}}', coalesce({{cell(static_5.selection,0,"Field_value_x").asObject()}},0)  as '{{cell(static_5.selection,0,"Api_name_x").asObject()}}', sum('row_count') over([..] partition by all) as 'prod_cat_count', 'rank_desc';

final_table = foreach final_table generate 'ProductCategory_Name_Custom', '{{cell(static_4.selection,0,"Api_name_y").asObject()}}', '{{cell(static_5.selection,0,"Api_name_x").asObject()}}', 'prod_cat_count',  'rank_desc', (case when 'prod_cat_count'>2 then round({{cell(static_7.selection, 0, "max").asObject()}}*'prod_cat_count') else ceil({{cell(static_7.selection, 0, "max").asObject()}}*'prod_cat_count') end) as 'final_limit_max', (case when 'prod_cat_count'>2 then round({{cell(static_7.selection, 0, "min").asObject()}}*'prod_cat_count') else ceil({{cell(static_7.selection, 0, "min").asObject()}}*'prod_cat_count') end) as 'final_limit_min';

final_table = filter final_table by 'rank_desc'>'final_limit_min' and 'rank_desc'<='final_limit_max';
final_table = foreach final_table generate 'ProductCategory_Name_Custom', '{{cell(static_5.selection,0,"Api_name_x").asObject()}}', '{{cell(static_4.selection,0,"Api_name_y").asObject()}}';

