when (daysBetween(toDate('selected_years_min_FiscalDate'), 'min_Date')/7) < 0 then (53+round(daysBetween(toDate('selected_years_min_FiscalDate'), 'min_Date')/7)) end as 'FY_minWeek_num', 

case 
when (daysBetween(toDate('selected_years_max_FiscalDate'), 'max_Date')/7) <= 1 and (daysBetween(toDate('selected_years_max_FiscalDate'), 'max_Date')/7) > 0.2  then 1
when (daysBetween(toDate('selected_years_max_FiscalDate'), 'max_Date')/7) > 1 then ceil(daysBetween(toDate('selected_years_max_FiscalDate'), 'max_Date')/7)
when (daysBetween(toDate('selected_years_max_FiscalDate'), 'max_Date')/7) <= 0.2 then (53+round(daysBetween(toDate('selected_years_max_FiscalDate'), 'max_Date')/7)) end as 'FY_maxWeek_num','Is_Fiscal';
q = foreach q generate 'min_Date', 'max_Date', 'min_Date_sec_epoch', 'max_Date_sec_epoch', 'min_current_fiscalYr_Date', 'min_current_CalendarYr_Date', 'Date_Min', 'Date_Max', 'Selected_Min_date', 'Selected_Max_date', number_to_string('FY_Year_min_num',"#")+number_to_string('FY_minWeek_num',"0#") as 'Fiscal_YearWeek_min', number_to_string('FY_Year_max_num',"#")+number_to_string('FY_maxWeek_num',"0#") as 'Fiscal_YearWeek_max', selected_years_min_FiscalDate, selected_years_max_FiscalDate, toDate(date_to_epoch(month_last_day(toDate(date_to_epoch(month_last_day(now())) - 1036800 + ((string_to_number(substr("month,-6", index_of("month,-6", "month")+6 ))-1) * 2678400)))) + 86400) as 'six_months_prior_dt', toDate(date_to_epoch(now())-(180*86400)) as '180days_prior_dt', 'Is_Fiscal';
q = foreach q generate 'min_Date', 'max_Date', 'min_Date_sec_epoch', 'max_Date_sec_epoch', 'min_current_fiscalYr_Date', 'min_current_CalendarYr_Date', 'Date_Min', 'Date_Max', 'Selected_Min_date' as 'min_Calendar_YearWeek', 'Selected_Max_date' as 'max_Calendar_YearWeek', (case when 'Is_Fiscal' == "_Fiscal" then 'Fiscal_YearWeek_min' else 'Selected_Min_date' end) as 'min_YearWeek', (case when 'Is_Fiscal' == "_Fiscal" then 'Fiscal_YearWeek_max' else 'Selected_Max_date' end) as 'max_YearWeek', toString('180days_prior_dt', "yyyy") + "" + number_to_string(ceil((date_to_epoch('180days_prior_dt') - date_to_epoch(toDate(toString('180days_prior_dt', "yyyy")+"-01-01", "yyyy-MM-dd")) + 86399)/(86400*7)),"0#") as '180days_prior_yrweek', toString(now(), "yyyy") + "" + number_to_string(ceil((date_to_epoch(now()) - date_to_epoch(toDate(toString(now(), "yyyy")+"-01-01", "yyyy-MM-dd")) + 86399)/(86400*7)),"0#") as 'yrweek_now','Is_Fiscal', (case when 'Is_Fiscal' == "_Fiscal" then "Fiscal " else "" end) as 'Fiscal_label';

