Avg_SOS_compliance_g_1.query:
sos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
sos_A = filter sos by 'Product_Name_Custom' == "{{cell(lens_3_2.selection, 0, "Product_Name_Custom").asString()}}";
sos_A = filter sos_A by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos_A = group sos_A by ('Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
sos_A = foreach sos_A generate 'Product_Name_Custom', avg('Compliance_Percentage') as 'current_sos';
sos_A = group sos_A by 'Product_Name_Custom';
sos_A = foreach sos_A generate 'Product_Name_Custom' as 'Product', avg('current_sos') as 'Avg_SoSCompliance_Percentage', "select" as 'text', "A" as 'order';
share_of_shelf = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
share_of_shelf = group share_of_shelf by ('Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
share_of_shelf = foreach share_of_shelf generate 'Product_Name_Custom', avg('Compliance_Percentage') as 'current_sos';
share_of_shelf = group share_of_shelf by 'Product_Name_Custom';
sos_B = foreach share_of_shelf generate 'Product_Name_Custom' as 'Product', avg('current_sos') as 'max_Avg_SoSCompliance_Percentage', "max" as 'text', "B" as 'order';
sos_B = order sos_B by 'max_Avg_SoSCompliance_Percentage' desc;
sos_B = limit sos_B 1;
sos_C = foreach share_of_shelf generate 'Product_Name_Custom' as 'Product', avg('current_sos') as 'min_Avg_SoSCompliance_Percentage', "min" as 'text', "C" as 'order';
sos_C = order sos_C by 'min_Avg_SoSCompliance_Percentage' asc;
sos_C = limit sos_C 1;
sos_D = foreach share_of_shelf generate 'Product_Name_Custom' as 'Product', avg(avg('current_sos')) over([..] partition by all) as 'avg_Avg_SoSCompliance_Percentage', "avg" as 'text', "D" as 'order';
sos_D = limit sos_D 1;
total_sos = cogroup sos_A by ('Product', 'order') full, sos_B by ('Product', 'order') full, sos_C by ('Product', 'order') full, sos_D by ('Product', 'order');
total_sos = foreach total_sos generate coalesce(sos_A.'Product', sos_B.'Product', sos_C.'Product', sos_D.'Product') as 'Product', coalesce(sos_A.'order', sos_B.'order', sos_C.'order', sos_D.'order') as 'order', sum(sos_A.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage', coalesce(sum(sos_A.'Avg_SoSCompliance_Percentage'),0) as 'Avg_SoSCompliance_Percentage_coalesced', coalesce(sum(sos_B.'max_Avg_SoSCompliance_Percentage'), 0) as 'max_Avg_SoSCompliance_Percentage', coalesce(sum(sos_C.'min_Avg_SoSCompliance_Percentage'), 0) as 'min_Avg_SoSCompliance_Percentage', sum(sos_D.'avg_Avg_SoSCompliance_Percentage') as 'avg_Avg_SoSCompliance_Percentage';
total_sos = group total_sos by ('Product', 'order');
total_sos = foreach total_sos generate 'Product', 'order', sum('Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage', sum('max_Avg_SoSCompliance_Percentage') as 'max_Avg_SoSCompliance_Percentage', sum('avg_Avg_SoSCompliance_Percentage') as 'avg_Avg_SoSCompliance_Percentage', sum('min_Avg_SoSCompliance_Percentage') as 'min_Avg_SoSCompliance_Percentage', (case when sum(sum('max_Avg_SoSCompliance_Percentage')) over([..] partition by all) == sum(sum('min_Avg_SoSCompliance_Percentage')) over([..] partition by all) then 0 else sum('min_Avg_SoSCompliance_Percentage') end) as 'min_range', (case when sum(sum('Avg_SoSCompliance_Percentage_coalesced')) over([..] partition by all) >= sum(sum('avg_Avg_SoSCompliance_Percentage')) over([..] partition by all) then "#00B531" else "#B50E03" end) as 'color';

total_sos = foreach total_sos generate 'Product', 'order', 'Avg_SoSCompliance_Percentage', 'max_Avg_SoSCompliance_Percentage', number_to_string('max_Avg_SoSCompliance_Percentage', "##0.00") as 'max_Avg_SoSCompliance_Percentage_str', 'min_Avg_SoSCompliance_Percentage', number_to_string('min_Avg_SoSCompliance_Percentage', "##0.00") as 'min_Avg_SoSCompliance_Percentage_str', 'avg_Avg_SoSCompliance_Percentage', number_to_string('avg_Avg_SoSCompliance_Percentage', "##0.00") as 'avg_Avg_SoSCompliance_Percentage_str', 'min_range', 'color';
total_sos = order total_sos by 'order';

Assortment_lens_1_1.query:
-- Compliance% Calculation
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp_result = group comp by ('Assortment_AssortmentProduct_Assortment_Name','AssessmentTask_Visit_RetailStore_Name');
comp_result = foreach comp_result generate 'Assortment_AssortmentProduct_Assortment_Name', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_Compliance_Percentage';
comp_result = group comp_result by ('Assortment_AssortmentProduct_Assortment_Name');
comp_result = foreach comp_result generate 'Assortment_AssortmentProduct_Assortment_Name', avg('Avg_Compliance_Percentage') as 'Avg_CompliancePercentage';

-- Order Details
order1 = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
order1 = group order1 by ('Assortment_AssortmentProduct_Assortment_Name','OrderId','Order_ActivatedDate_Year','Order_ActivatedDate_Week');
order1 = foreach order1 generate 'Assortment_AssortmentProduct_Assortment_Name', 'OrderId', 'Order_ActivatedDate_Year'+"-"+'Order_ActivatedDate_Week' as 'YearWeek', sum('TotalPrice') as 'Sales',sum('Quantity') as 'Quantity';
order1 = group order1 by ('Assortment_AssortmentProduct_Assortment_Name');
order1 = foreach order1 generate 'Assortment_AssortmentProduct_Assortment_Name', (sum('Sales')/unique('OrderId')) as 'avg_order_value', (sum('Sales')/unique('YearWeek')) as 'avg_weekly_revenue', sum('Sales') as 'Sales', sum('Quantity') as 'Quantity';

-- Store Coverage
store_cv = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
store_cv = group store_cv by ('Assortment_AssortmentProduct_Assortment_Name');
store_cv = foreach store_cv generate 'Assortment_AssortmentProduct_Assortment_Name', (unique('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name')/{{cell(Total_Stores__1_1.result, 0, "total_stores").asObject()}}) as 'store_coverage';

-- Share of Shelf
sos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
sos = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = group sos by ('Assortment_AssortmentProduct_Assortment_Name', 'AssessmentTask_Visit_RetailStore_Name');
sos = foreach sos generate 'Assortment_AssortmentProduct_Assortment_Name', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_SoSCompliance_Percentage';
sos = group sos by ('Assortment_AssortmentProduct_Assortment_Name');
sos = foreach sos generate 'Assortment_AssortmentProduct_Assortment_Name', avg('Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage';

--OutOfStockCompliance
oos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
oos = filter oos by 'Include_In_Compliance'== "True" and 'Type_Computed' == "OutOfStock";
oos_result = group oos by ('Assortment_AssortmentProduct_Assortment_Name', 'AssessmentTask_Visit_RetailStore_Name');
oos_result = foreach oos_result generate 'Assortment_AssortmentProduct_Assortment_Name', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_OutOfStock_Compliance';
oos_result = group oos_result by ('Assortment_AssortmentProduct_Assortment_Name');
oos_result = foreach oos_result generate 'Assortment_AssortmentProduct_Assortment_Name', avg('Avg_OutOfStock_Compliance') as 'Avg_OutOfStock_Compliance';

--FacingsCompliance% Calculation
fcs = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
fcs = filter fcs by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs = group fcs by  ('Assortment_AssortmentProduct_Assortment_Name', 'AssessmentTask_Visit_RetailStore_Name');
fcs = foreach fcs generate 'Assortment_AssortmentProduct_Assortment_Name', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_FacingsCompliance_Percentage';
fcs = group fcs by ('Assortment_AssortmentProduct_Assortment_Name');
fcs = foreach fcs generate 'Assortment_AssortmentProduct_Assortment_Name', avg('Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage';

--PromotionCompliance
promo = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
promo = filter promo by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Promotion";
promo = group promo by ('Assortment_AssortmentProduct_Assortment_Name','AssessmentTask_Visit_RetailStore_Name');
promo = foreach promo generate 'Assortment_AssortmentProduct_Assortment_Name', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_PromoCompliance_Percentage';
promo = group promo by ('Assortment_AssortmentProduct_Assortment_Name');
promo = foreach promo generate 'Assortment_AssortmentProduct_Assortment_Name', avg('Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage';

--InventoryCompliance
inv = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
inv = filter inv by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Inventory";
inv = group inv by ('Assortment_AssortmentProduct_Assortment_Name','AssessmentTask_Visit_RetailStore_Name');
inv = foreach inv generate 'Assortment_AssortmentProduct_Assortment_Name', 'AssessmentTask_Visit_RetailStore_Name' as 'Store_Name', avg('Compliance_Percentage') as 'Avg_InventoryCompliance_Percentage';
inv = group inv by ('Assortment_AssortmentProduct_Assortment_Name');
inv = foreach inv generate 'Assortment_AssortmentProduct_Assortment_Name', avg('Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage';

--final multidataset table 
final_table = cogroup order1 by ('Assortment_AssortmentProduct_Assortment_Name') full, sos by ('Assortment_AssortmentProduct_Assortment_Name') full, oos_result by ('Assortment_AssortmentProduct_Assortment_Name') full, comp_result by ('Assortment_AssortmentProduct_Assortment_Name') full,fcs by ('Assortment_AssortmentProduct_Assortment_Name') full, promo by ('Assortment_AssortmentProduct_Assortment_Name') full, inv by ('Assortment_AssortmentProduct_Assortment_Name') full, store_cv by ('Assortment_AssortmentProduct_Assortment_Name');
final_table = foreach final_table generate coalesce(order1.'Assortment_AssortmentProduct_Assortment_Name', comp_result.'Assortment_AssortmentProduct_Assortment_Name', sos.'Assortment_AssortmentProduct_Assortment_Name',oos_result.'Assortment_AssortmentProduct_Assortment_Name', fcs.'Assortment_AssortmentProduct_Assortment_Name', promo.'Assortment_AssortmentProduct_Assortment_Name', inv.'Assortment_AssortmentProduct_Assortment_Name', store_cv.'Assortment_AssortmentProduct_Assortment_Name') as 'Assortment_AssortmentProduct_Assortment_Name', sum(order1.'avg_order_value') as 'avg_order_value', sum(order1.'avg_weekly_revenue') as 'avg_weekly_revenue',sum(order1.'Sales') as 'total_sales', sum(order1.'Quantity') as 'total_qty', avg(oos_result.'Avg_OutOfStock_Compliance') as 'Avg_OutOfStock_Compliance', avg(sos.'Avg_SoSCompliance_Percentage') as 'Avg_SoSCompliance_Percentage', avg(comp_result.'Avg_CompliancePercentage') as 'Avg_CompliancePercentage', avg(fcs.'Avg_FacingsCompliance_Percentage') as 'Avg_FacingsCompliance_Percentage', avg(promo.'Avg_PromoCompliance_Percentage') as 'Avg_PromoCompliance_Percentage', avg(inv.'Avg_InventoryCompliance_Percentage') as 'Avg_InventoryCompliance_Percentage', sum(store_cv.'store_coverage') as 'store_coverage';

final_table = group final_table by 'Assortment_AssortmentProduct_Assortment_Name';

final_table = foreach final_table generate 'Assortment_AssortmentProduct_Assortment_Name', {{cell(static_4.selection,0,"Field_value_y").asObject()}}  as '{{cell(static_4.selection,0,"Api_name_y").asObject()}}', {{cell(static_5.selection,0,"Field_value_x").asObject()}}  as '{{cell(static_5.selection,0,"Api_name_x").asObject()}}', dense_rank() over([..] partition by all order by coalesce({{cell(static_4.selection,0,"Field_value_y").asObject()}},0) desc) as 'rank_desc',count() as 'row_count';
final_table = group final_table by ('Assortment_AssortmentProduct_Assortment_Name','row_count','rank_desc');

final_table = foreach final_table generate 'Assortment_AssortmentProduct_Assortment_Name', coalesce({{cell(static_4.selection,0,"Field_value_y").asObject()}},0)  as '{{cell(static_4.selection,0,"Api_name_y").asObject()}}', coalesce({{cell(static_5.selection,0,"Field_value_x").asObject()}},0)  as '{{cell(static_5.selection,0,"Api_name_x").asObject()}}', sum('row_count') over([..] partition by all) as 'prod_cat_count', 'rank_desc';

final_table = foreach final_table generate 'Assortment_AssortmentProduct_Assortment_Name', '{{cell(static_4.selection,0,"Api_name_y").asObject()}}', '{{cell(static_5.selection,0,"Api_name_x").asObject()}}', 'prod_cat_count',  'rank_desc', (case when 'prod_cat_count'>2 then round({{cell(static_7.selection, 0, "max").asObject()}}*'prod_cat_count') else ceil({{cell(static_7.selection, 0, "max").asObject()}}*'prod_cat_count') end) as 'final_limit_max', (case when 'prod_cat_count'>2 then round({{cell(static_7.selection, 0, "min").asObject()}}*'prod_cat_count') else ceil({{cell(static_7.selection, 0, "min").asObject()}}*'prod_cat_count') end) as 'final_limit_min';
final_table = filter final_table by 'rank_desc'>'final_limit_min' and 'rank_desc'<='final_limit_max';

final_table = foreach final_table generate 'Assortment_AssortmentProduct_Assortment_Name', '{{cell(static_5.selection,0,"Api_name_x").asObject()}}', '{{cell(static_4.selection,0,"Api_name_y").asObject()}}';

