retail = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
retail = filter retail by 'ProductCategory_Name_Custom'=="{{cell(lens_1.selection, 0, "ProductCategory_Name_Custom").asString()}}";
total_oos = filter retail by 'Include_In_Compliance' == "True" and 'Type_Computed' == "OutOfStock";
oos_A = filter total_oos by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
oos_B = filter total_oos by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
total_oos = group oos_A by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name') full, oos_B by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
total_oos = foreach total_oos generate coalesce(oos_A.'ProductCategory_Name_Custom', oos_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', 
coalesce(oos_A.'AssessmentTask_Visit_RetailStore_Name', oos_B.'AssessmentTask_Visit_RetailStore_Name') as 'AssessmentTask_Visit_RetailStore_Name',
avg(oos_A.'Compliance_Percentage') as 'current_out_of_stock', avg(oos_B.'Compliance_Percentage') as 'previous_out_of_stock';
total_oos = group total_oos by 'ProductCategory_Name_Custom';
total_oos = foreach total_oos generate 'ProductCategory_Name_Custom', avg('current_out_of_stock') as 'current_out_of_stock', avg('previous_out_of_stock') as 'previous_out_of_stock';
total_oos = foreach total_oos generate 'ProductCategory_Name_Custom', 'current_out_of_stock', 'previous_out_of_stock', (('current_out_of_stock'-'previous_out_of_stock')/('previous_out_of_stock')) as 'PoP_Out_of_Stock_percent';
total_oos = foreach total_oos generate 'ProductCategory_Name_Custom','current_out_of_stock','PoP_Out_of_Stock_percent';
-- Decline in Sales(CG_OrderItem)
orders = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
orders = filter orders by 'Product_ProductCategory_ProductCategory_Name'=="{{cell(lens_1.selection, 0, "ProductCategory_Name_Custom").asString()}}";
sales_A = filter orders by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
sales_B = filter orders by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
total_sale = group sales_A by ('Product_ProductCategory_ProductCategory_Name') full, sales_B by ('Product_ProductCategory_ProductCategory_Name');
total_sale = foreach total_sale generate coalesce(sales_A.'Product_ProductCategory_ProductCategory_Name', sales_B.'Product_ProductCategory_ProductCategory_Name') as 'Product_ProductCategory_ProductCategory_Name', sum(sales_A.'TotalPrice') as 'current_sales', sum(sales_B.'TotalPrice') as 'previous_sales';
total_sale = foreach total_sale generate 'Product_ProductCategory_ProductCategory_Name','current_sales', 'previous_sales', (('current_sales'-'previous_sales')/('previous_sales')) as 'Percent_change_in_Sales';
total_sale = foreach total_sale generate 'Product_ProductCategory_ProductCategory_Name', 'current_sales', 'Percent_change_in_Sales';
--Decline in Quantity(CG_OrderItem)
qty_A = filter orders by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
qty_B = filter orders by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
total_qty = group qty_A by ('Product_ProductCategory_ProductCategory_Name') full, qty_B by ('Product_ProductCategory_ProductCategory_Name');
total_qty = foreach total_qty generate coalesce(qty_A.'Product_ProductCategory_ProductCategory_Name', qty_B.'Product_ProductCategory_ProductCategory_Name') as 'Product_ProductCategory_ProductCategory_Name', sum(qty_A.'Quantity') as 'current_quantity', sum(qty_B.'Quantity') as 'previous_quantity';
total_qty = foreach total_qty generate 'Product_ProductCategory_ProductCategory_Name','current_quantity', 'previous_quantity', (('current_quantity'-'previous_quantity')/('previous_quantity')) as 'Percent_Change_in_Quantity';
total_qty = foreach total_qty generate 'Product_ProductCategory_ProductCategory_Name','current_quantity','Percent_Change_in_Quantity';
---Decline in AvgOrderValue
ordval_A = filter orders by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
ordval_B = filter orders by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
total_ordval = group ordval_A by ('Product_ProductCategory_ProductCategory_Name') full, ordval_B by ('Product_ProductCategory_ProductCategory_Name');
total_ordval = foreach total_ordval generate coalesce(ordval_A.'Product_ProductCategory_ProductCategory_Name', ordval_B.'Product_ProductCategory_ProductCategory_Name') as 'Product_ProductCategory_ProductCategory_Name', (sum(ordval_A.'TotalPrice')/unique(ordval_A.'OrderId')) as 'current_avg_order_value', (sum(ordval_B.'TotalPrice')/unique(ordval_B.'OrderId')) as 'previous_avg_order_value';
total_ordval = foreach total_ordval generate 'Product_ProductCategory_ProductCategory_Name', 'current_avg_order_value', 'previous_avg_order_value', (('current_avg_order_value'-'previous_avg_order_value')/('previous_avg_order_value')) as 'Percent_Change_in_AvgOrderValue';
total_ordval = foreach total_ordval generate 'Product_ProductCategory_ProductCategory_Name','current_avg_order_value','Percent_Change_in_AvgOrderValue';
---Decline in SalesVelocity
sv_A = filter orders by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
sv_B = filter orders by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
total_sv = group sv_A by ('Product_ProductCategory_ProductCategory_Name', 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week') full, sv_B by ('Product_ProductCategory_ProductCategory_Name', 'Order_ActivatedDate_Year', 'Order_ActivatedDate_Week');
total_sv = foreach total_sv generate coalesce(sv_A.'Product_ProductCategory_ProductCategory_Name', sv_B.'Product_ProductCategory_ProductCategory_Name') as 'Product_ProductCategory_ProductCategory_Name', sv_A.'Order_ActivatedDate_Year'+"-"+ sv_A.'Order_ActivatedDate_Week' as 'CurrentPeriod_YearWeek', sv_B.'Order_ActivatedDate_Year'+"-"+ sv_B.'Order_ActivatedDate_Week' as 'PreviousPeriod_YearWeek', sum(sv_A.'TotalPrice') as 'current_sales', sum(sv_B.'TotalPrice') as 'previous_sales';
total_sv = group total_sv by 'Product_ProductCategory_ProductCategory_Name';
total_sv = foreach total_sv generate 'Product_ProductCategory_ProductCategory_Name', sum('current_sales')/unique('CurrentPeriod_YearWeek') as 'current_avg_week_revenue', sum('previous_sales')/unique('PreviousPeriod_YearWeek') as 'previous_avg_week_revenue';
total_sv = foreach total_sv generate 'Product_ProductCategory_ProductCategory_Name', 'current_avg_week_revenue', 'previous_avg_week_revenue', (('current_avg_week_revenue'-'previous_avg_week_revenue')/('previous_avg_week_revenue')) as 'Percent_Change_in_WeeklyRev';
total_sv = foreach total_sv generate 'Product_ProductCategory_ProductCategory_Name','current_avg_week_revenue','Percent_Change_in_WeeklyRev';
---Decline in Compliance (CG_RetailVisitKpi)
comp = filter retail by 'Include_In_Compliance' == "True";
comp_A = filter comp by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
comp_B = filter comp by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
total_comp = group comp_A by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name') full, comp_B by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
total_comp = foreach total_comp generate coalesce(comp_A.'ProductCategory_Name_Custom', comp_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(comp_A.'AssessmentTask_Visit_RetailStore_Name', comp_B.'AssessmentTask_Visit_RetailStore_Name') as 'AssessmentTask_Visit_RetailStore_Name', avg(comp_A.'Compliance_Percentage') as 'current_comp', avg(comp_B.'Compliance_Percentage') as 'previous_comp';
total_comp = group total_comp by 'ProductCategory_Name_Custom';
total_comp = foreach total_comp generate 'ProductCategory_Name_Custom', avg('current_comp') as 'current_comp', avg('previous_comp') as 'previous_comp';
total_comp = foreach total_comp generate 'ProductCategory_Name_Custom', 'current_comp', 'previous_comp', (('current_comp'-'previous_comp')/('previous_comp')) as 'Compliance_Percentate_Change_vs_Previous_Period';
total_comp = foreach total_comp generate 'ProductCategory_Name_Custom', 'current_comp', 'Compliance_Percentate_Change_vs_Previous_Period';
---Decline in Promotions (CG_RetailVisitKpi)
promo = filter retail by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Promotion";
promo_A = filter promo by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
promo_B = filter promo by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
promo = group promo_A by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name') full, promo_B by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
promo = foreach promo generate coalesce(promo_A.'ProductCategory_Name_Custom', promo_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(promo_A.'AssessmentTask_Visit_RetailStore_Name', promo_B.'AssessmentTask_Visit_RetailStore_Name') as 'AssessmentTask_Visit_RetailStore_Name', avg(promo_A.'Compliance_Percentage') as 'current_promo', avg(promo_B.'Compliance_Percentage') as 'previous_promo';
promo = group promo by 'ProductCategory_Name_Custom';
promo = foreach promo generate 'ProductCategory_Name_Custom', avg('current_promo') as 'current_promo', avg('previous_promo') as 'previous_promo';
promo = foreach promo generate 'ProductCategory_Name_Custom', 'current_promo', 'previous_promo', (('current_promo'-'previous_promo')/'previous_promo') as 'PoP_Promotion_Compliance_percent';
promo = foreach promo generate 'ProductCategory_Name_Custom','current_promo','PoP_Promotion_Compliance_percent';
---Decline in FacingCompliance (CG_RetailVisitKpi)
fcs = filter retail by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs_A = filter fcs by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
fcs_B = filter fcs by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
total_fcs = group fcs_A by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name') full, fcs_B by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
total_fcs = foreach total_fcs generate coalesce(fcs_A.'ProductCategory_Name_Custom', fcs_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(fcs_A.'AssessmentTask_Visit_RetailStore_Name', fcs_B.'AssessmentTask_Visit_RetailStore_Name') as 'AssessmentTask_Visit_RetailStore_Name', avg(fcs_A.'Compliance_Percentage') as 'current_fcs', avg(fcs_B.'Compliance_Percentage') as 'previous_fcs';
total_fcs = group total_fcs by 'ProductCategory_Name_Custom';
total_fcs = foreach total_fcs generate 'ProductCategory_Name_Custom', avg('current_fcs') as 'current_fcs', avg('previous_fcs') as 'previous_fcs';
total_fcs = foreach total_fcs generate 'ProductCategory_Name_Custom', 'current_fcs', 'previous_fcs', (('current_fcs'-'previous_fcs')/('previous_fcs')) as 'PoP_Facings_Compliance_percent';
total_fcs = foreach total_fcs generate 'ProductCategory_Name_Custom','current_fcs', 'PoP_Facings_Compliance_percent';
---Decline in SoSCompliance (CG_RetailVisitKpi)
sos = filter retail by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos_A = filter sos by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
sos_B = filter sos by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
total_sos = group sos_A by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name') full, sos_B by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
total_sos = foreach total_sos generate coalesce(sos_A.'ProductCategory_Name_Custom', sos_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(sos_A.'AssessmentTask_Visit_RetailStore_Name', sos_B.'AssessmentTask_Visit_RetailStore_Name') as 'AssessmentTask_Visit_RetailStore_Name', avg(sos_A.'Compliance_Percentage') as 'current_sos', avg(sos_B.'Compliance_Percentage') as 'previous_sos';
total_sos = group total_sos by 'ProductCategory_Name_Custom';
total_sos = foreach total_sos generate 'ProductCategory_Name_Custom', avg('current_sos') as 'current_sos', avg('previous_sos') as 'previous_sos';
total_sos = foreach total_sos generate 'ProductCategory_Name_Custom', 'current_sos', 'previous_sos', (('current_sos'-'previous_sos')/('previous_sos')) as 'PoP_Share_of_Shelf_Compliance_percent';
total_sos = foreach total_sos generate 'ProductCategory_Name_Custom', 'current_sos', 'PoP_Share_of_Shelf_Compliance_percent';
---Decline in InventoryCompliance (CG_RetailVisitKpi)
inv = filter retail by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Inventory";
inv_A = filter inv by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
inv_B = filter inv by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day')")}};
total_inv = group inv_A by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name') full, inv_B by ('ProductCategory_Name_Custom','AssessmentTask_Visit_RetailStore_Name');
total_inv = foreach total_inv generate coalesce(inv_A.'ProductCategory_Name_Custom', inv_B.'ProductCategory_Name_Custom') as 'ProductCategory_Name_Custom', coalesce(inv_A.'AssessmentTask_Visit_RetailStore_Name', inv_B.'AssessmentTask_Visit_RetailStore_Name') as 'AssessmentTask_Visit_RetailStore_Name', avg(inv_A.'Compliance_Percentage') as 'current_inventory', avg(inv_B.'Compliance_Percentage') as 'previous_inventory';
total_inv = group total_inv by 'ProductCategory_Name_Custom';
total_inv = foreach total_inv generate 'ProductCategory_Name_Custom', avg('current_inventory') as 'current_inventory', avg('previous_inventory') as 'previous_inventory';
total_inv = foreach total_inv generate 'ProductCategory_Name_Custom', 'current_inventory', 'previous_inventory', (('current_inventory'-'previous_inventory')/('previous_inventory')) as 'PoP_Inventory_Comp_percent';
total_inv = foreach total_inv generate 'ProductCategory_Name_Custom', 'current_inventory', 'PoP_Inventory_Comp_percent';
---Decline in StoreCoverage
store_orders_A = filter orders by {{row(Current_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
store_orders_B = filter orders by {{row(Comparative_Period_1.selection, [0], ["min", "max"]).asDateRange("date('Order_ActivatedDate_Year', 'Order_ActivatedDate_Month', 'Order_ActivatedDate_Day')")}};
store_orders = group store_orders_A by ('Product_ProductCategory_ProductCategory_Name') full, store_orders_B by ('Product_ProductCategory_ProductCategory_Name');
store_orders = foreach store_orders generate coalesce(store_orders_A.'Product_ProductCategory_ProductCategory_Name', store_orders_B.'Product_ProductCategory_ProductCategory_Name') as 'Product_ProductCategory_ProductCategory_Name', (unique(store_orders_A.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name')/{{cell(Total_Stores__1_1.result, 0, "total_stores").asObject()}}) as 'current_store_coverage', (unique(store_orders_B.'Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name')/{{cell(Total_Stores__1_1.result, 0, "total_stores").asObject()}}) as 'previous_store_coverage';
store_orders = foreach store_orders generate 'Product_ProductCategory_ProductCategory_Name', 'current_store_coverage', 'previous_store_coverage', (('current_store_coverage'-'previous_store_coverage')/('previous_store_coverage')) as 'Percent_Change_in_StoreCoverage';
store_orders = foreach store_orders generate 'Product_ProductCategory_ProductCategory_Name', 'current_store_coverage', 'Percent_Change_in_StoreCoverage';
--final multidataset table
final_table = cogroup total_sale by  ('Product_ProductCategory_ProductCategory_Name') full,
              total_qty by  ('Product_ProductCategory_ProductCategory_Name') full,
              total_ordval by ('Product_ProductCategory_ProductCategory_Name') full,
              total_sv by  ('Product_ProductCategory_ProductCategory_Name') full,
              total_oos by ('ProductCategory_Name_Custom') full,
              total_comp by ('ProductCategory_Name_Custom') full,
              promo by ('ProductCategory_Name_Custom') full,
              total_fcs by  ('ProductCategory_Name_Custom') full,
              total_sos by ('ProductCategory_Name_Custom') full,
              total_inv by ('ProductCategory_Name_Custom') full,
              store_orders by ('Product_ProductCategory_ProductCategory_Name');
final_table = foreach final_table generate
        coalesce(total_sale.'Product_ProductCategory_ProductCategory_Name', total_qty.'Product_ProductCategory_ProductCategory_Name', total_ordval.'Product_ProductCategory_ProductCategory_Name', total_sv.'Product_ProductCategory_ProductCategory_Name', total_oos.'ProductCategory_Name_Custom', total_comp.'ProductCategory_Name_Custom', promo.'ProductCategory_Name_Custom', total_fcs.'ProductCategory_Name_Custom', total_sos.'ProductCategory_Name_Custom', total_inv.'ProductCategory_Name_Custom', store_orders.'Product_ProductCategory_ProductCategory_Name') as 'ProductCategory_Name_Custom', 
        
         sum(total_sale.'current_sales') as 'total_sales',
         sum(total_sale.'Percent_change_in_Sales') as 'Percent_change_in_Sales',
         sum(total_qty.'current_quantity') as 'total_qty',
         sum(total_qty.'Percent_Change_in_Quantity') as 'Percent_Change_in_Quantity',
         sum(total_ordval.'current_avg_order_value') as 'avg_order_value',
         sum(total_ordval.'Percent_Change_in_AvgOrderValue') as 'Percent_Change_in_AvgOrderValue',
         sum(total_sv.'current_avg_week_revenue') as 'avg_weekly_revenue',
         sum(total_sv.'Percent_Change_in_WeeklyRev') as 'Percent_Change_in_WeeklyRev',
         sum(total_oos.'current_out_of_stock') as 'Avg_OutOfStock_Compliance',
         sum(total_oos.'PoP_Out_of_Stock_percent') as 'PoP_Out_of_Stock_percent',
         avg(total_comp.'current_comp') as 'Avg_CompliancePercentage',
         avg(total_comp.'Compliance_Percentate_Change_vs_Previous_Period') as 'Compliance_Percentate_Change_vs_Previous_Period',
         avg(promo.'current_promo') as 'Avg_PromoCompliance_Percentage',
         avg(promo.'PoP_Promotion_Compliance_percent') as 'PoP_Promotion_Compliance_percent',
         avg(total_fcs.'current_fcs') as 'Avg_FacingsCompliance_Percentage', 
         avg(total_fcs.'PoP_Facings_Compliance_percent') as 'PoP_Facings_Compliance_percent',  
         avg(total_sos.'current_sos') as 'Avg_SoSCompliance_Percentage',
         avg(total_sos.'PoP_Share_of_Shelf_Compliance_percent') as 'PoP_Share_of_Shelf_Compliance_percent',  
         avg(total_inv.'current_inventory') as 'Avg_InventoryCompliance_Percentage',
         avg(total_inv.'PoP_Inventory_Comp_percent') as 'PoP_Inventory_Comp_percent',
         sum(store_orders.'current_store_coverage') as 'store_coverage',
         sum(store_orders.'Percent_Change_in_StoreCoverage') as 'Percent_Change_in_StoreCoverage';

