Quantity_Gauge_1.query:
total_qty = load "${App.Datasets.CG_OrderItem36_tp.FullyQualifiedName}";
total_qty_A = filter total_qty by 'Product_Name' == "{{cell(lens_3_2.selection, 0, "Product_Name_Custom").asString()}}";
total_qty_A = group total_qty_A by 'Product_Name';
total_qty_A = foreach total_qty_A generate 'Product_Name' as 'Product', sum('Quantity') as 'Quantity', "select" as 'text', "A" as 'order';
total_qty_B = group total_qty by 'Product_Name';
total_qty_B = foreach total_qty_B generate 'Product_Name' as 'Product', sum('Quantity') as 'max_Quantity', "max" as 'text', "B" as 'order';
total_qty_B = order total_qty_B by 'max_Quantity' desc;
total_qty_B = limit total_qty_B 1;
total_qty_C = group total_qty by 'Product_Name';
total_qty_C = foreach total_qty_C generate 'Product_Name' as 'Product', sum('Quantity') as 'min_Quantity', "min" as 'text', "C" as 'order';
total_qty_C = order total_qty_C by 'min_Quantity' asc;
total_qty_C = limit total_qty_C 1;
total_qty_D = group total_qty by 'Product_Name';
total_qty_D = foreach total_qty_D generate 'Product_Name' as 'Product', avg(sum('Quantity')) over([..] partition by all) as 'avg_Quantity', "avg" as 'text', "D" as 'order';
total_qty_D = limit total_qty_D 1;
qty = cogroup total_qty_A by ('Product', 'order') full, total_qty_B by ('Product', 'order') full, total_qty_C by ('Product', 'order') full, total_qty_D by ('Product', 'order');
qty = foreach qty generate coalesce(total_qty_A.'Product', total_qty_B.'Product', total_qty_C.'Product', total_qty_D.'Product') as 'Product', coalesce(total_qty_A.'order', total_qty_B.'order', total_qty_C.'order', total_qty_D.'order') as 'order', sum(total_qty_A.'Quantity') as 'Quantity', coalesce(sum(total_qty_A.'Quantity'),0) as 'Quantity_coalesced', coalesce(sum(total_qty_B.'max_Quantity'), 0) as 'max_Quantity', coalesce(sum(total_qty_C.'min_Quantity'),0) as 'min_Quantity', coalesce(sum(total_qty_D.'avg_Quantity'),0) as 'avg_Quantity';
qty = group qty by ('Product', 'order');
qty = foreach qty generate 'Product', 'order', sum('Quantity') as 'Quantity', sum('max_Quantity') as 'max_Quantity', sum('avg_Quantity') as 'avg_Quantity', sum('min_Quantity') as 'min_Quantity', (case when sum(sum('max_Quantity')) over([..] partition by all) == sum(sum('min_Quantity')) over([..] partition by all) then  0 else sum('min_Quantity') end) as 'min_range', (case when sum(sum('Quantity_coalesced')) over([..] partition by all) >= sum(sum('avg_Quantity')) over([..] partition by all) then "#00B531" else "#B50E03" end) as 'color';
qty = foreach qty generate 'Product', 'order', 'Quantity', 'max_Quantity', number_to_string('max_Quantity', "#,####0") as 'max_Quantity_str', 'min_Quantity', number_to_string('min_Quantity', "#,####0") as 'min_Quantity_str', 'avg_Quantity', number_to_string('avg_Quantity', "#,####0") as 'avg_Quantity_str', 'min_range', 'color';
qty = order qty by 'order';

