orders = foreach orders generate 'Order_ActivatedDate_Year' + "~~~" + 'Order_ActivatedDate_Week' as 'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week', sum('TotalPrice') as 'current_sales', sum('Quantity') as 'current_quantity', (sum('TotalPrice')/unique('OrderId')) as 'current_avg_order_value', (sum('TotalPrice')/unique('Order_ActivatedDate_Week')) as 'current_avg_week_revenue', (unique('Order_AssessmentTaskOrder_AssessmentTask_Visit_RetailStore_Name')/{{cell(Total_Stores__1_1.result, 0, "total_stores").asObject()}}) as 'current_store_coverage';
---Decline in Compliance (CG_RetailVisitKpi)
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp = filter comp by 'Product_Name_Custom'=="{{cell(new_launched_prod_py_1.selection, 0, "Product_Name").asString()}}";
comp = filter comp by 'Include_In_Compliance' == "True";
comp = filter comp by date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day') in ["180 days ago".."current day"];
total_comp = group comp by ('ActualVisitStartTime_Year','ActualVisitStartTime_Week','AssessmentTask_Visit_RetailStore_Name');
total_comp = foreach total_comp generate 'ActualVisitStartTime_Year' + "~~~" + 'ActualVisitStartTime_Week' as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', 'AssessmentTask_Visit_RetailStore_Name', avg('Compliance_Percentage') as 'current_comp';
total_comp = group total_comp by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week';
total_comp = foreach total_comp generate 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', avg('current_comp') as 'current_comp';
---Decline in Promotions (CG_RetailVisitKpi)
promo = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
promo = filter promo by 'Product_Name_Custom'=="{{cell(new_launched_prod_py_1.selection, 0, "Product_Name").asString()}}";
promo = filter promo by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Promotion";
promo = filter promo by date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day') in ["180 days ago".."current day"];
promo = group promo by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week','AssessmentTask_Visit_RetailStore_Name');
promo = foreach promo generate 'ActualVisitStartTime_Year' + "~~~" + 'ActualVisitStartTime_Week' as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', 'AssessmentTask_Visit_RetailStore_Name', avg('Compliance_Percentage') as 'current_promo';
promo = group promo by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week';
promo = foreach promo generate 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', avg('current_promo') as 'current_promo';
---Decline in FacingCompliance (CG_RetailVisitKpi)
fcs = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
fcs = filter fcs by 'Product_Name_Custom'=="{{cell(new_launched_prod_py_1.selection, 0, "Product_Name").asString()}}";
fcs = filter fcs by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Facing";
fcs = filter fcs by date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day') in ["180 days ago".."current day"];
total_fcs = group fcs by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week','AssessmentTask_Visit_RetailStore_Name');
total_fcs = foreach total_fcs generate 'ActualVisitStartTime_Year' + "~~~" + 'ActualVisitStartTime_Week' as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', 'AssessmentTask_Visit_RetailStore_Name', avg('Compliance_Percentage') as 'current_fcs';
total_fcs = group total_fcs by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week';
total_fcs = foreach total_fcs generate 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', avg('current_fcs') as 'current_fcs';
---Decline in SoSCompliance (CG_RetailVisitKpi)
sos = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
sos = filter sos by 'Product_Name_Custom'=="{{cell(new_launched_prod_py_1.selection, 0, "Product_Name").asString()}}";
sos = filter sos by 'Include_In_Compliance' == "True" and 'Type_Computed' == "ShareOfShelf";
sos = filter sos by date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day') in ["180 days ago".."current day"];
total_sos = group sos by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week','AssessmentTask_Visit_RetailStore_Name');
total_sos = foreach total_sos generate 'ActualVisitStartTime_Year' + "~~~" + 'ActualVisitStartTime_Week' as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', 'AssessmentTask_Visit_RetailStore_Name', avg('Compliance_Percentage') as 'current_sos';
total_sos = group total_sos by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week';
total_sos = foreach total_sos generate 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', avg('current_sos') as 'current_sos';
---Decline in InventoryCompliance (CG_RetailVisitKpi)
inv = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
inv = filter inv by 'Product_Name_Custom'=="{{cell(new_launched_prod_py_1.selection, 0, "Product_Name").asString()}}";
inv = filter inv by 'Include_In_Compliance' == "True" and 'Type_Computed' == "Inventory";
inv = filter inv by date('ActualVisitStartTime_Year', 'ActualVisitStartTime_Month', 'ActualVisitStartTime_Day') in ["180 days ago".."current day"];
total_inv = group inv by ('ActualVisitStartTime_Year', 'ActualVisitStartTime_Week','AssessmentTask_Visit_RetailStore_Name');
total_inv = foreach total_inv generate 'ActualVisitStartTime_Year' + "~~~" + 'ActualVisitStartTime_Week' as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', 'AssessmentTask_Visit_RetailStore_Name', avg('Compliance_Percentage') as 'current_inventory';
total_inv = group total_inv by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week';
total_inv = foreach total_inv generate 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', avg('current_inventory') as 'current_inventory';
--Metric_multidatset---
final_table = cogroup
             orders by ('Order_ActivatedDate_Year~~~Order_ActivatedDate_Week') full,
             oos by ('ActualVisitStartTime_Year~~~ActualVisitStartTime_Week') full,
             total_comp by ('ActualVisitStartTime_Year~~~ActualVisitStartTime_Week') full,
             promo by ('ActualVisitStartTime_Year~~~ActualVisitStartTime_Week') full,
             total_fcs by ('ActualVisitStartTime_Year~~~ActualVisitStartTime_Week') full,
             total_sos by ('ActualVisitStartTime_Year~~~ActualVisitStartTime_Week') full,
             total_inv by ('ActualVisitStartTime_Year~~~ActualVisitStartTime_Week');
final_table = foreach final_table generate coalesce(orders.'Order_ActivatedDate_Year~~~Order_ActivatedDate_Week', oos.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', total_comp.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', promo.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', total_fcs.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', total_sos.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', total_inv.'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week')  as 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week',

sum(orders.'current_sales') as 'total_sales',
sum(orders.'current_quantity') as 'total_qty', 
sum(orders.'current_avg_order_value') as 'avg_order_value', 
sum(orders.'current_avg_week_revenue') as 'avg_weekly_revenue', 
sum(oos.'current_out_of_stock') as 'Avg_OutOfStock_Compliance',
sum(total_comp.'current_comp') as 'Avg_CompliancePercentage', 
sum(promo.'current_promo') as 'Avg_PromoCompliance_Percentage', 
sum(total_fcs.'current_fcs') as 'Avg_FacingsCompliance_Percentage', 
sum(total_sos.'current_sos') as 'Avg_SoSCompliance_Percentage', 
sum(total_inv.'current_inventory') as 'Avg_InventoryCompliance_Percentage', 
sum(orders.'current_store_coverage') as 'store_coverage';

final_table = group final_table by 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week';
final_table = foreach final_table generate 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', avg(sum('total_sales')) over([..] partition by all) as 'total_sales_refline', avg(sum('total_qty')) over([..] partition by all) as 'total_qty_refline', avg(sum('avg_order_value')) over([..] partition by all) as 'avg_order_value_refline', avg(sum('avg_weekly_revenue')) over([..] partition by all) as 'avg_weekly_revenue_refline', avg(sum('Avg_OutOfStock_Compliance')) over([..] partition by all) as 'Avg_OutOfStock_Compliance_refline', avg(sum('Avg_CompliancePercentage')) over([..] partition by all) as 'Avg_CompliancePercentage_refline', avg(sum('Avg_PromoCompliance_Percentage')) over([..] partition by all) as 'Avg_PromoCompliance_Percentage_refline', avg(sum('Avg_FacingsCompliance_Percentage')) over([..] partition by all) as 'Avg_FacingsCompliance_Percentage_refline', avg(sum('Avg_SoSCompliance_Percentage')) over([..] partition by all) as 'Avg_SoSCompliance_Percentage_refline', avg(sum('Avg_InventoryCompliance_Percentage')) over([..] partition by all) as 'Avg_InventoryCompliance_Percentage_refline', (avg(sum('store_coverage')) over([..] partition by all)*100) as 'store_coverage_refline';

final_table = foreach final_table generate 'ActualVisitStartTime_Year~~~ActualVisitStartTime_Week', '{{cell(static_10.selection, 0, "refline_name").asString()}}' as 'Reference Line', number_to_string('{{cell(static_10.selection, 0, "refline_name").asString()}}', "#,###0.00") as 'refline_value_label', (case when "{{cell(static_10.selection, 0, "unit").asString()}}"=="$" then "$" else "" end) as 'refline_text_prefix', (case when "{{cell(static_10.selection, 0, "unit").asString()}}"=="%" then "%" else "" end) as 'refline_text_suffix';
final_table = limit final_table 1;

Avg_store_compliance_1.query:
comp = load "${App.Datasets.CG_RetailVisitKpi37_tp.FullyQualifiedName}";
comp_A = filter comp by 'Product_Name_Custom' == "{{cell(lens_3_2.selection, 0, "Product_Name_Custom").asString()}}";
comp_A = filter comp_A by 'Include_In_Compliance' == "True";
comp_A = group comp_A by ('Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
comp_A = foreach comp_A generate 'Product_Name_Custom', avg('Compliance_Percentage') as 'current_comp';
comp_A = group comp_A by 'Product_Name_Custom';
comp_A = foreach comp_A generate 'Product_Name_Custom' as 'Product', avg('current_comp') as 'Avg_CompliancePercentage', "select" as 'text', "A" as 'order';
store_comp = filter comp by 'Include_In_Compliance' == "True";
store_comp = group store_comp by ('Product_Name_Custom', 'AssessmentTask_Visit_RetailStore_Name');
store_comp = foreach store_comp generate 'Product_Name_Custom', avg('Compliance_Percentage') as 'current_comp';
store_comp = group store_comp by 'Product_Name_Custom';
comp_B = foreach store_comp generate 'Product_Name_Custom' as 'Product', avg('current_comp') as 'max_Avg_CompliancePercentage', "max" as 'text', "B" as 'order';
comp_B = order comp_B by 'max_Avg_CompliancePercentage' desc;
comp_B = limit comp_B 1;
comp_C = foreach store_comp generate 'Product_Name_Custom' as 'Product', avg('current_comp') as 'min_Avg_CompliancePercentage', "min" as 'text', "C" as 'order';
comp_C = order comp_C by 'min_Avg_CompliancePercentage' asc;
comp_C = limit comp_C 1;
comp_D = foreach store_comp generate 'Product_Name_Custom' as 'Product', avg(avg('current_comp')) over([..] partition by all) as 'avg_Avg_CompliancePercentage', "avg" as 'text', "D" as 'order';
comp_D = limit comp_D 1;
comp = cogroup comp_A by ('Product', 'order') full, comp_B by ('Product', 'order') full, comp_C by ('Product', 'order') full, comp_D by ('Product', 'order');
comp = foreach comp generate coalesce(comp_A.'Product', comp_B.'Product', comp_C.'Product', comp_D.'Product') as 'Product', coalesce(comp_A.'order', comp_B.'order', comp_C.'order', comp_D.'order') as 'order', sum(comp_A.'Avg_CompliancePercentage') as 'Avg_CompliancePercentage', coalesce(sum(comp_A.'Avg_CompliancePercentage'),0) as 'Avg_CompliancePercentage_coalesced', coalesce(sum(comp_B.'max_Avg_CompliancePercentage'),0) as 'max_Avg_CompliancePercentage', coalesce(sum(comp_C.'min_Avg_CompliancePercentage'),0) as 'min_Avg_CompliancePercentage', coalesce(sum(comp_D.'avg_Avg_CompliancePercentage'),0) as 'avg_Avg_CompliancePercentage';

comp = group comp by ('Product', 'order');

comp = foreach comp generate 'Product', 'order', sum('Avg_CompliancePercentage') as 'Avg_CompliancePercentage', sum('max_Avg_CompliancePercentage') as 'max_Avg_CompliancePercentage', sum('avg_Avg_CompliancePercentage') as 'avg_Avg_CompliancePercentage', sum('min_Avg_CompliancePercentage') as 'min_Avg_CompliancePercentage', (case when sum(sum('max_Avg_CompliancePercentage')) over([..] partition by all) == sum(sum('min_Avg_CompliancePercentage')) over([..] partition by all) then  0 else sum('min_Avg_CompliancePercentage') end) as 'min_range', (case when sum(sum('Avg_CompliancePercentage_coalesced')) over([..] partition by all) >= sum(sum('avg_Avg_CompliancePercentage')) over([..] partition by all) then "#00B531" else "#B50E03" end) as 'color';

comp = foreach comp generate 'Product', 'order', 'Avg_CompliancePercentage', 'max_Avg_CompliancePercentage', number_to_string('max_Avg_CompliancePercentage', "##0.00") as 'max_Avg_CompliancePercentage_str', 'min_Avg_CompliancePercentage', number_to_string('min_Avg_CompliancePercentage', "##0.00") as 'min_Avg_CompliancePercentage_str', 'avg_Avg_CompliancePercentage', number_to_string('avg_Avg_CompliancePercentage', "##0.00") as 'avg_Avg_CompliancePercentage_str', 'min_range', 'color';
comp = order comp by 'order';
