AnnualIncome_Min_Max_Avg_1.query:
q = load "${App.Datasets.RBDemoAccount.FullyQualifiedName}";
q = filter q by 'RECORDTYPENAME' != "Household";
q = group q by ('ID', 'NAME');
q = foreach q generate q.'ID' as 'Id', q.'NAME' as 'Name', sum(q.'PRIMARYCONTACTANNUALINCOME') as 'sum_Annual_Income', coalesce(min(sum(q.'PRIMARYCONTACTANNUALINCOME')) over ([..] partition by all),0) as 'min_All_Annual_Income', coalesce(max(sum(q.'PRIMARYCONTACTANNUALINCOME')) over ([..] partition by all),0) as 'max_All_Annual_Income', coalesce(avg(sum(q.'PRIMARYCONTACTANNUALINCOME')) over ([..] partition by all),0) as 'avg_All_Annual_Income';
q = limit q 1;

Customer_Share_of_Annual_Income_1.query:
q = load "${App.Datasets.RBDemoAccount.FullyQualifiedName}";
q = group q by all;
q = foreach q generate {{coalesce(cell(Annual_Income_KPIs_1.result,0,'Percentage_of_Total_Annual_Income'),0).asString()}} as 'Customer_Share_of_Annual_Income';
q = limit q 1;

Account_Balance_by_RecordType_1.query:
q = load "${App.Datasets.RBDemoFinancialAccount.FullyQualifiedName}";
q = filter q by 'FINANCIALACCOUNTTYPE' in ["Managed Account", "Savings"];
q = group q by 'RECORDTYPENAME';
q = foreach q generate 'RECORDTYPENAME' as 'RecordType.Name', sum('DepositAndInvestmentBalanceAmount') as 'Deposits & Investments Balance';
q = order q by 'Deposits & Investments Balance' desc;
q = limit q 2000;

Customer_Share_of_Deposit_and_Investment.query:
q = load "${App.Datasets.RBDemoFinancialAccount.FullyQualifiedName}";
q = group q by all;
q = foreach q generate {{coalesce(cell(Account_Balance_KPIs_1.result,0,'Percent_of_Total_Deposit_and_Investment_Balance'),0).asString()}} as 'Customer_Share_of_Deposit_and_Investment_Balance';
q = limit q 1;

Activity_KPIs_1.query:
q = load "${App.Datasets.RBDemoActivity.FullyQualifiedName}";
q = filter q by date('ACTIVITYDATE_Year', 'ACTIVITYDATE_Month', 'ACTIVITYDATE_Day') in ["6 months ago".."current day"];
q = filter q by 'ACCOUNTRECORDTYPENAME' != "Household";
result = group q by all;
result = foreach result generate count(q) as 'A';
result = limit result 2000;

Account_Balance_KPIs_1.query:
q = load "${App.Datasets.RBDemoFinancialAccount.FullyQualifiedName}";
q = filter q by 'FINANCIALACCOUNTTYPE' in ["Savings", "Managed Accounts"];
q = group q by ('ACCOUNTNAME', 'PRIMARYOWNER');
q = foreach q generate q.'ACCOUNTNAME' as 'Account.Name', q.'PRIMARYOWNER' as 'FinServ__PrimaryOwner__c', sum(q.'DepositAndInvestmentBalanceAmount') as 'sum_Deposit_and_Investment_Balance_Amount', dense_rank() over([..] partition by all order by sum(q.'DepositAndInvestmentBalanceAmount') desc) as 'Customer_Rank', sum(sum(q.'DepositAndInvestmentBalanceAmount')) over ([..] partition by all) as 'sum_Total_Deposit_and_Investment_Balance', coalesce(sum(q.'DepositAndInvestmentBalanceAmount') / sum(sum(q.'DepositAndInvestmentBalanceAmount')) over ([..] partition by all),0) as 'Percent_of_Total_Deposit_and_Investment_Balance';

Leads_KPIs_1.query:
q = load "${App.Datasets.RBDemoLead.FullyQualifiedName}";
q = filter q by date('CREATEDDATE_Year', 'CREATEDDATE_Month', 'CREATEDDATE_Day') in ["6 months ago".."current day"];
q = filter q by 'ISREFERRAL' == "True";
result = group q by all;
result = foreach result generate count(q) as 'A';
result = limit result 2000;

Accounts_Count_1.query:
q = load "${App.Datasets.RBDemoAccount.FullyQualifiedName}";
q = filter q by 'RECORDTYPENAME' != "Household";
q = group q by all;
q = foreach q generate count() as 'count';
q = limit q 2000;

Account_ID_1.query:
q = load "${App.Datasets.RBDemoAccount.FullyQualifiedName}";
q = group q by 'ID';
q = foreach q generate 'ID' as 'Id', count() as 'count';
q = order q by 'Id' asc;
q = limit q 1;

Annual_Income_KPIs_1.query:
q = load "${App.Datasets.RBDemoAccount.FullyQualifiedName}";
q = filter q by 'RECORDTYPENAME' != "Household";
q = group q by ('ID', 'NAME');
q = foreach q generate q.'ID' as 'Id', q.'NAME' as 'Name', sum(q.'PRIMARYCONTACTANNUALINCOME') as 'sum_Annual_Income', dense_rank() over([..] partition by all order by sum(q.'PRIMARYCONTACTANNUALINCOME') desc) as 'Customer_Rank', sum(sum(q.'PRIMARYCONTACTANNUALINCOME')) over ([..] partition by all) as 'Total_Annual_Income', coalesce(sum(q.'PRIMARYCONTACTANNUALINCOME') / sum(sum(q.'PRIMARYCONTACTANNUALINCOME')) over ([..] partition by all),0) as 'Percentage_of_Total_Annual_Income';
q = filter q by 'Id' == "{{coalesce(cell(Account_ID_1.result,0,'Id'), 'NA').asObject()}}";
q = limit q 1;

