Reversed_Fees_Over_Time_1.query:
q = load "${App.Datasets.RBDemoChargesAndFees.FullyQualifiedName}";
q = group q by ('TRANSACTIONDATE_Year', 'TRANSACTIONDATE_Month', 'TRANSACTIONTYPE');
q = foreach q generate 'TRANSACTIONDATE_Year' + "~~~" + 'TRANSACTIONDATE_Month' as 'TRANSACTIONDATE_Year~~~TRANSACTIONDATE_Month', case when 'TRANSACTIONTYPE' == "Credit" then "True" else "False" end as 'TRANSACTIONTYPE', sum('FEES') as 'sum_Fees';
q = order q by ('TRANSACTIONDATE_Year~~~TRANSACTIONDATE_Month' asc, 'TRANSACTIONTYPE' asc);
q = limit q 2000;

By_Customer_Balance_Category_1.query:
q = load "${App.Datasets.RBDemoChargesAndFees.FullyQualifiedName}";
q_A = filter q by 'TRANSACTIONTYPE' == "Debit";
q_B = filter q by 'TRANSACTIONTYPE' == "Credit";
q_C = filter q by 'TRANSACTIONTYPE' == "Debit";
result = group q by 'ACCOUNTNAME' full, q_A by 'ACCOUNTNAME' full, q_B by 'ACCOUNTNAME';
result = foreach result generate coalesce(q_A.'ACCOUNTNAME', q_B.'ACCOUNTNAME', q.'ACCOUNTNAME') as 'ACCOUNTNAME', count(q_A) as 'count_Fees_Charged', count(q_B) as 'count_Fees_Reversed', unique(q_A.'ACCOUNTNAME') as 'Customers_Charged_Fees', case when min(q.'BALANCEAMOUNT') < 50000 then "1.Less than 50k" when min(q.'BALANCEAMOUNT') >= 50000 and min(q.'BALANCEAMOUNT')< 250000 then "2.Between 50k and 250k" when min(q.'BALANCEAMOUNT') >= 250000 and min(q.'BALANCEAMOUNT')< 500000 then "3.Between 250k and 500k" when min(q.'BALANCEAMOUNT') <= 500000 and min(q.'BALANCEAMOUNT')< 1000000 then "4.Between 500k and 1M" else "5.More than 1M" end as 'Balance_Category';
result = group result by 'Balance_Category';
result = foreach result generate 'Balance_Category' as 'Balance Category', sum('count_Fees_Charged') as '# Fees Charged', sum('count_Fees_Reversed') as '# of Fees Reversed', sum('Customers_Charged_Fees') as '# of Customers with Fees';
result = order result by ('Balance Category' asc);
result = limit result 2000;

Times_Charged_Fees_1.query:
q = load "${App.Datasets.RBDemoChargesAndFees.FullyQualifiedName}";
q = filter q by 'TRANSACTIONTYPE' == "Debit";
result = group q by 'ACCOUNTNAME';
result = foreach result generate q.'ACCOUNTNAME' as 'ACCOUNTNAME', count(q) as 'Times_Charged_Fees_Num', number_to_string(count(q), "#") as 'Times_Charged_Fees', unique(q.'ACCOUNTNAME') as 'Customers';
result = group result by 'Times_Charged_Fees';
result = foreach result generate 'Times_Charged_Fees' as 'Times Charged Fees', sum('Customers') as '# Customers', min('Times_Charged_Fees_Num') as 'Times_Charged_Fees_Num';
result = order result by ('Times_Charged_Fees_Num' asc);
result = limit result 2000;

KPIs_1.query:
q = load "${App.Datasets.RBDemoChargesAndFees.FullyQualifiedName}";
q_A = filter q by 'TRANSACTIONTYPE' == "Debit";
q_C = filter q by 'TRANSACTIONTYPE' == "Credit";
result = group q_A by all full, q_C by all;
result = foreach result generate count(q_A) as '# Fees Charged', sum(q_A.'FEES') as 'Total Fee Amount', count(q_C) as '# Fee Reversed', sum(q_C.'FEES') as 'Total Amount Reversed', unique(q_A.'ACCOUNTNAME') as '# Customers Charged Fees';
result = foreach result generate '# Fees Charged', 'Total Fee Amount', '# Fee Reversed', 'Total Amount Reversed', '# Customers Charged Fees', coalesce('Total Amount Reversed'/'Total Fee Amount',0) as '% Fees Reversed';
result = limit result 2000;
