High_Low_Value_Customers_1.query:
-- Load "RBDemoFinancialAccount"
fin_account = load "${App.Datasets.RBDemoFinancialAccount.FullyQualifiedName}";
-- Load "RBDemoActivity"
activities = load "${App.Datasets.RBDemoActivity.FullyQualifiedName}";
activities = filter activities by 'ACCOUNTRECORDTYPENAME' != "Household";
-- Group all streams
result = group fin_account by 'ACCOUNTBALANCEBUCKET' full, activities by 'ACCOUNTBALANCEBUCKET';
result = foreach result generate coalesce(fin_account.'ACCOUNTBALANCEBUCKET', activities.'ACCOUNTBALANCEBUCKET') as 'ACCOUNTBALANCEBUCKET', unique(fin_account.'PRIMARYOWNER') as 'Customers', count(activities) as 'Activities', sum(fin_account.'BALANCEAMOUNT') as 'Total_Balance';
result_final = group result by 'ACCOUNTBALANCEBUCKET';
result_final = foreach result_final generate 'ACCOUNTBALANCEBUCKET' as 'ACCOUNTBALANCEBUCKET', round(sum('Total_Balance') / sum(sum('Total_Balance')) over ([..] partition by all), 2) as '% of Balance', round(sum('Activities') / sum(sum('Activities')) over ([..] partition by all), 2) as '% of Activities', round(sum('Customers') / sum(sum('Customers')) over ([..] partition by all), 2) as '% of Customers', sum('Total_Balance') as 'Total Balance', sum('Activities') as '# of Activities', sum('Customers') as '# of Customers';

KPIs_Activities_1.query:
q = load "${App.Datasets.RBDemoActivity.FullyQualifiedName}";
q= filter q by 'ACCOUNTRECORDTYPENAME' !="Household";
q_B = filter q by 'TYPE' == "Call";
q_C = filter q by 'TYPE' == "Email";
q_D = filter q by 'TYPE' matches "Other";
result = group q by all full, q_B by all full, q_C by all full, q_D by all;
result = foreach result generate count(q) as 'Activities', count(q_B) as 'Calls', count(q_C) as 'Emails', count(q_D) as 'Others';
result = limit result 2000;
