Activities_Completed_by_Time_1.query:
q = load "${App.Datasets.RBDemoActivity.FullyQualifiedName}";
q = filter q by 'ACCOUNTRECORDTYPENAME' !="Household";
q = filter q by 'STATUS' == "Completed";
q_A = filter q by date('ACTIVITYDATE_Year', 'ACTIVITYDATE_Month', 'ACTIVITYDATE_Day') in ["90 days ago".."current day"];
q_B = filter q by date('ACTIVITYDATE_Year', 'ACTIVITYDATE_Month', 'ACTIVITYDATE_Day') in ["180 days ago".."current day - 91 days"];
result = group q_A by ('ACTIVITYDATE_Year', 'ACTIVITYDATE_Week') full, q_B by ('ACTIVITYDATE_Year', 'ACTIVITYDATE_Week');
result = foreach result generate coalesce(q_A.'ACTIVITYDATE_Year' + "~~~" + q_A.'ACTIVITYDATE_Week', q_B.'ACTIVITYDATE_Year' + "~~~" + q_B.'ACTIVITYDATE_Week') as 'ACTIVITYDATE_Year~~~ACTIVITYDATE_Week', count(q_A) as 'Activities_CurrentPeriod', case when count(q_A)>0 then 1 else 0 end as 'Weeks_Count_CurrentPeriod', count(q_B) as 'Activities_PriorPeriod', case when count(q_B)>0 then 1 else 0 end as 'Weeks_Count_PriorPeriod';
result = group result by all;
result = foreach result generate sum('Activities_CurrentPeriod') as 'Activities_Completed_Current_Period', sum('Activities_PriorPeriod') as 'Activities_Completed_Prior_Period', round(sum('Activities_CurrentPeriod') / sum('Weeks_Count_CurrentPeriod'),0) as 'Avg_Activities_Completed_per_Week_CurrentPeriod', round(sum('Activities_PriorPeriod') / sum('Weeks_Count_PriorPeriod'),0) as 'Avg_Activities_Completed_per_Week_PriorPeriod', 23.66 as 'Percentage_Change_in_Activities_Completed';

result = limit result 2000;

Activities_Count_1.query:
q = load "${App.Datasets.RBDemoActivity.FullyQualifiedName}";
q = filter q by date('ACTIVITYDATE_Year', 'ACTIVITYDATE_Month', 'ACTIVITYDATE_Day') in ["90 days ago".."current day"];
q = filter q by 'ACCOUNTRECORDTYPENAME' != "Household";
result = group q by all;
result = foreach result generate count(q) as 'A';
result = limit result 2000;

Balance_of_Customers_not_Interacted_in_9.query:
q = load "${App.Datasets.RBDemoFinancialAccount.FullyQualifiedName}";
q1 = filter q by 'ACCOUNTDAYSSINCELASTINTERACTION' > 90;
result = group q1 by 'ACCOUNTNAME' left, q by 'ACCOUNTNAME';
result = foreach result generate coalesce(q1.'ACCOUNTNAME', q.'ACCOUNTNAME') as 'Account.Name', sum(q.'DepositAndInvestmentBalanceAmount') as 'sum_Deposit_and_Investment_Balance_Amount' ;
result = order result by ('sum_Deposit_and_Investment_Balance_Amount' desc);
result = limit result 2000;

Leads_KPIs_1.query:
q = load "${App.Datasets.RBDemoLead.FullyQualifiedName}";
q = filter q by 'OPENPROCESSEDSTATUS' == "Open";
q_B = filter q by !('DAYSSINCEACTIVITYGROUP' in ["15-30 days", "< 15 days"]);
result = group q by all full, q_B by all;
result = foreach result generate count(q) as 'A', count(q_B) as 'B';
result = limit result 2000;

Opens_Leads_by_Status_1.query:
q = load "${App.Datasets.RBDemoLead.FullyQualifiedName}";
q = filter q by 'OPENPROCESSEDSTATUS' == "Open";
q = group q by 'STATUS';
q = foreach q generate 'STATUS' as 'Status', avg('LEADAGE') as 'avg_LeadAge';
q = order q by 'avg_LeadAge' desc;
q = limit q 2000;

Activities_Overdue_1.query:
