result = limit result 2000;

FA_KPIs_Last_90Days_2.query:
q = load "${App.Datasets.RBDemoFinancialAccount.FullyQualifiedName}";
q1 = filter q by 'ACCOUNTDAYSSINCELASTINTERACTION' > 90;
result = group q1 by 'ACCOUNTNAME' left, q by 'ACCOUNTNAME';
result = foreach result generate coalesce(q1.'ACCOUNTNAME', q.'ACCOUNTNAME') as 'Account.Name', sum(q.'BALANCEAMOUNT') as 'Balance_Amount', sum(q.'INVESTMENTAMOUNT') as 'Investment_Amount', sum(q.'DepositAndInvestmentBalanceAmount') as 'sum_Deposits_and_Investments_Balance';
result = group result by 'Account.Name';
result = foreach result generate 'Account.Name' as 'Account.Name', sum('sum_Deposits_and_Investments_Balance') as 'sum_Deposits_and_Investments_Balance', avg(sum('sum_Deposits_and_Investments_Balance')) over ([..] partition by all) as 'avg_Deposits_and_Investments_Balance', case when sum('sum_Deposits_and_Investments_Balance') > avg(sum('sum_Deposits_and_Investments_Balance')) over ([..] partition by all) then 1 else 0 end as 'Customers_with_Deposits_and_Investments_Balance_Above_Avg', case when sum('sum_Deposits_and_Investments_Balance') <= avg(sum('sum_Deposits_and_Investments_Balance')) over ([..] partition by all) then 1 else 0 end as 'Customers_with_Deposits_and_Investments_Balance_Below_Avg'; 
result = group result by all;
result = foreach result generate unique('Account.Name') as 'Customers', number_to_string(round(avg('avg_Deposits_and_Investments_Balance'),2), "$#,###") as 'avg_Deposits_and_Investments_Balance_String', round(avg('avg_Deposits_and_Investments_Balance'),0) as 'avg_Deposits_and_Investments_Balance', sum('Customers_with_Deposits_and_Investments_Balance_Above_Avg') as 'Customers_with_Deposits_and_Investments_Balance_Above_Avg', sum('Customers_with_Deposits_and_Investments_Balance_Below_Avg') as 'Customers_with_Deposits_and_Investments_Balance_Below_Avg';

