UpcomingRenewal_1.query:
q = load "${App.Datasets.MFG_Demo_Data.FullyQualifiedName}";
q = filter q by 'DerivedStatus' == "Upcoming Renewal";
q = group q by all;
q = foreach q generate sum('Plan') as 'Upcoming_Renewal';
q = limit q 2000;

Attainment_1.query:
q = load "${App.Datasets.MFG_Demo_Data.FullyQualifiedName}";
q = group q by 'AccountName';
q = foreach q generate 'AccountName' as 'AccountName', avg('Attainment') as 'Attainment';
q = order q by 'Attainment' desc;
q = limit q 2000;

ActualAmount_1.query:
q = load "${App.Datasets.MFG_Demo_Data.FullyQualifiedName}";
q = group q by all;
q = foreach q generate sum('Actual') as 'sum_Actual';
q = limit q 2000;
