DemandByProduct_1.query:
q = load "${App.Datasets.MFG_Demo_Data.FullyQualifiedName}";
q = group q by 'ProductName';
q = foreach q generate 'ProductName' as 'ProductName', sum('Actual') / sum('Plan') as 'DemandMet';
q = order q by 'DemandMet' desc;
q = limit q 2000;

ActualvsPlan_1.query:
q = load "${App.Datasets.MFG_Demo_Data.FullyQualifiedName}";
q = group q by ('AccountName', 'ProductName');
q = foreach q generate 'AccountName' as 'AccountName', 'ProductName' as 'ProductName', sum('Actual') - sum('Plan') as 'sum_Plan';
q = order q by ('AccountName' asc, 'ProductName' asc);
q = limit q 2000;

DetailsTable_1.query:
q = load "${App.Datasets.MFG_Demo_Data.FullyQualifiedName}";
result = group q by ('ProductFamily', 'ProductName', 'AccountName', 'ForecastStatus');
result = foreach result generate q.'ProductFamily' as 'ProductFamily', q.'ProductName' as 'ProductName', q.'AccountName' as 'AccountName', q.'ForecastStatus' as 'Status', sum(q.'Plan') as 'Planned Revenue', sum(q.'Actual') as 'Actual Revenue';
result = order result by ('ProductFamily' asc, 'ProductName' asc, 'AccountName' asc, 'Status' asc);
result = limit result 2000;

DetailsTable_2.query:
