lens_1.query:
q = load "${App.Datasets.MFG_Demo_Data.FullyQualifiedName}";
q = group q by ('SalesAgreementName', 'ForecastStatus');
q = foreach q generate 'SalesAgreementName' as 'SalesAgreementName', 'ForecastStatus' as 'ForecastStatus', sum('Actual') as 'sum_Actual';
q = order q by 'sum_Actual' desc;
q = limit q 2000;
