saql.query:
q = load "${App.Datasets.pipeline_trending_tp.FullyQualifiedName}";
q = filter q by 'Stage_IsClosed' == "false";
q = filter q by 'ForecastCategory' != "Omitted";
today = filter q by 'IsLastUpdate' == "1";
today = group today by 'ForecastCategory_ForecastCategoryName';
today = foreach today generate 'ForecastCategory_ForecastCategoryName' as 'ForecastCategory_ForecastCategoryName', sum('Amount') as 'sum_Amount', last('Opportunity_Stage_SortOrder') as 'stage_sort', 0 as 'time_sort', "Current" as 'Quarter';
one = filter q by (date('ValidFromDate_Year', 'ValidFromDate_Month', 'ValidFromDate_Day') in [.."1 quarters ago"]) && (date('ValidToDate_Year', 'ValidToDate_Month', 'ValidToDate_Day') in ["current quarter"..]);
one = group one by 'ForecastCategory_ForecastCategoryName';
one = foreach one generate 'ForecastCategory_ForecastCategoryName' as 'ForecastCategory_ForecastCategoryName', sum('Amount') as 'sum_Amount', last('Opportunity_Stage_SortOrder') as 'stage_sort', 1 as 'time_sort', "This Quarter" as 'Quarter';
two = filter q by (date('ValidFromDate_Year', 'ValidFromDate_Month', 'ValidFromDate_Day') in [.."2 quarters ago"]) && (date('ValidToDate_Year', 'ValidToDate_Month', 'ValidToDate_Day') in ["1 quarter ago"..]);
two = group two by 'ForecastCategory_ForecastCategoryName';
two = foreach two generate 'ForecastCategory_ForecastCategoryName' as 'ForecastCategory_ForecastCategoryName', sum('Amount') as 'sum_Amount', last('Opportunity_Stage_SortOrder') as 'stage_sort', 2 as 'time_sort', "1 quarter ago" as 'Quarter';
three = filter q by (date('ValidFromDate_Year', 'ValidFromDate_Month', 'ValidFromDate_Day') in [.."3 quarters ago"]) && (date('ValidToDate_Year', 'ValidToDate_Month', 'ValidToDate_Day') in ["2 quarters ago"..]);
three = group three by 'ForecastCategory_ForecastCategoryName';
three = foreach three generate 'ForecastCategory_ForecastCategoryName' as 'ForecastCategory_ForecastCategoryName', sum('Amount') as 'sum_Amount', last('Opportunity_Stage_SortOrder') as 'stage_sort', 3 as 'time_sort', "2 quarters ago" as 'Quarter';
four = filter q by (date('ValidFromDate_Year', 'ValidFromDate_Month', 'ValidFromDate_Day') in [.."4 quarters ago"]) && (date('ValidToDate_Year', 'ValidToDate_Month', 'ValidToDate_Day') in ["3 quarters ago"..]);
four = group four by 'ForecastCategory_ForecastCategoryName';
four = foreach four generate 'ForecastCategory_ForecastCategoryName' as 'ForecastCategory_ForecastCategoryName', sum('Amount') as 'sum_Amount', last('Opportunity_Stage_SortOrder') as 'stage_sort', 4 as 'time_sort', "3 quarters ago" as 'Quarter';
result = union today, one, two, three, four;
result = group result by ('Quarter', 'ForecastCategory_ForecastCategoryName');
result = foreach result generate 'ForecastCategory_ForecastCategoryName' as 'ForecastCategory_ForecastCategoryName', 'Quarter' as 'Quarter', sum('sum_Amount') as 'sum_Amount', first('stage_sort') as 'stage_sort', first('time_sort') as 'time_sort';
results = order result by ('time_sort' desc, 'stage_sort' asc);
